import SwiftUI
import Clerk

struct SignUpAuthView: View {
    @Environment(\.clerk) private var clerk
    @Environment(\.dismiss) private var dismiss

    @State private var isLoading = false
    @State private var errorMessage: String?
    @State private var showEmailSignUp = false

    var body: some View {
        ZStack {
            // Background
            Color(.systemBackground)
                .ignoresSafeArea()

            VStack(spacing: 0) {
                // Header
                VStack(spacing: 16) {
                    Text("User Type Sign Up")
                        .font(.caption)
                        .foregroundColor(.gray)
                        .frame(maxWidth: .infinity, alignment: .leading)
                        .padding(.horizontal, 24)
                        .padding(.top, 20)

                    Image(systemName: "target")
                        .font(.system(size: 60))
                        .foregroundStyle(
                            LinearGradient(
                                colors: [.blue, .purple],
                                startPoint: .topLeading,
                                endPoint: .bottomTrailing
                            )
                        )

                    Text("ATHLEAD")
                        .font(.system(size: 32, weight: .bold))
                        .foregroundColor(.blue)
                }
                .padding(.bottom, 40)

                // Error message
                if let errorMessage = errorMessage {
                    HStack {
                        Image(systemName: "exclamationmark.triangle.fill")
                        Text(errorMessage)
                            .font(.footnote)
                    }
                    .foregroundColor(.red)
                    .padding()
                    .background(Color.red.opacity(0.1))
                    .cornerRadius(10)
                    .padding(.horizontal, 24)
                    .padding(.bottom, 16)
                    .transition(.scale.combined(with: .opacity))
                }

                // Sign-up options
                VStack(spacing: 16) {
                    // Google Sign-Up
                    Button {
                        signUpWithGoogle()
                    } label: {
                        HStack {
                            Image(systemName: "g.circle.fill")
                                .font(.title3)
                            Text("Sign-Up with Google")
                                .font(.body)
                                .fontWeight(.semibold)
                        }
                        .frame(maxWidth: .infinity)
                        .padding()
                        .background(Color(red: 0.87, green: 0.32, blue: 0.24))
                        .foregroundColor(.white)
                        .cornerRadius(8)
                    }
                    .disabled(isLoading)

                    // Microsoft Sign-Up
                    Button {
                        signUpWithMicrosoft()
                    } label: {
                        HStack {
                            Image(systemName: "square.grid.2x2.fill")
                                .font(.title3)
                            Text("Sign-Up with Microsoft")
                                .font(.body)
                                .fontWeight(.semibold)
                        }
                        .frame(maxWidth: .infinity)
                        .padding()
                        .background(Color(red: 0.0, green: 0.46, blue: 0.74))
                        .foregroundColor(.white)
                        .cornerRadius(8)
                    }
                    .disabled(isLoading)

                    // Email Sign-Up
                    Button {
                        showEmailSignUp = true
                    } label: {
                        HStack {
                            Image(systemName: "envelope.fill")
                                .font(.title3)
                            Text("Sign-Up with Email")
                                .font(.body)
                                .fontWeight(.semibold)
                        }
                        .frame(maxWidth: .infinity)
                        .padding()
                        .background(Color(red: 0.18, green: 0.67, blue: 0.90))
                        .foregroundColor(.white)
                        .cornerRadius(8)
                    }
                    .disabled(isLoading)
                }
                .padding(.horizontal, 24)

                Spacer()

                // Already have an account
                HStack(spacing: 4) {
                    Text("Already have an account?")
                        .font(.subheadline)
                        .foregroundColor(.secondary)

                    Button {
                        dismiss()
                    } label: {
                        Text("Sign-In")
                            .font(.subheadline)
                            .fontWeight(.semibold)
                            .foregroundColor(.blue)
                    }
                }
                .padding(.bottom, 40)
            }

            // Loading overlay
            if isLoading {
                Color.black.opacity(0.3)
                    .ignoresSafeArea()

                ProgressView()
                    .progressViewStyle(CircularProgressViewStyle(tint: .white))
                    .scaleEffect(1.5)
            }
        }
        .sheet(isPresented: $showEmailSignUp) {
            EmailSignUpView()
        }
    }

    private func signUpWithGoogle() {
        isLoading = true
        errorMessage = nil

        Task {
            do {
                guard let strategy = clerk.client.signUp.supportedFirstFactors.first(where: {
                    $0.strategy == "oauth_google"
                }) as? SignUp.SocialStrategy else {
                    await MainActor.run {
                        errorMessage = "Google sign-up not available"
                        isLoading = false
                    }
                    return
                }

                try await clerk.client.signUp.authenticateWithRedirect(strategy)

                await MainActor.run {
                    isLoading = false
                    dismiss()
                }
            } catch {
                await MainActor.run {
                    errorMessage = error.localizedDescription
                    isLoading = false
                }
            }
        }
    }

    private func signUpWithMicrosoft() {
        isLoading = true
        errorMessage = nil

        Task {
            do {
                guard let strategy = clerk.client.signUp.supportedFirstFactors.first(where: {
                    $0.strategy == "oauth_microsoft"
                }) as? SignUp.SocialStrategy else {
                    await MainActor.run {
                        errorMessage = "Microsoft sign-up not available"
                        isLoading = false
                    }
                    return
                }

                try await clerk.client.signUp.authenticateWithRedirect(strategy)

                await MainActor.run {
                    isLoading = false
                    dismiss()
                }
            } catch {
                await MainActor.run {
                    errorMessage = error.localizedDescription
                    isLoading = false
                }
            }
        }
    }
}

