        !password.isEmpty && password.count >= 8 &&
        !firstName.isEmpty && !lastName.isEmpty
    }

    private func signUpWithEmail() {
        isLoading = true
        errorMessage = nil

        Task {
            do {
                // Create sign up with email and password
                try await clerk.signUp(
                    strategy: .standard(
                        emailAddress: email,
                        password: password,
                        firstName: firstName,
                        lastName: lastName
                    )
                )

                await MainActor.run {
                    isLoading = false
                    dismiss()
                }
            } catch {
                await MainActor.run {
                    errorMessage = error.localizedDescription
                    isLoading = false
                }
            }
        }
    }
}

