                    .background(Color.red.opacity(0.1))
                    .cornerRadius(10)
                    .padding(.horizontal, 24)
                    .padding(.bottom, 16)
                    .transition(.scale.combined(with: .opacity))
                }

                // Sign-up options
                VStack(spacing: 16) {
                    // Google Sign-Up
                    Button {
                        signUpWithGoogle()
                    } label: {
                        HStack {
                            Image(systemName: "g.circle.fill")
                                .font(.title3)
                            Text("Sign-Up with Google")
                                .font(.body)
                                .fontWeight(.semibold)
                        }
                        .frame(maxWidth: .infinity)
                        .padding()
                        .background(Color(red: 0.87, green: 0.32, blue: 0.24))
                        .foregroundColor(.white)
                        .cornerRadius(8)
                    }
                    .disabled(isLoading)

                    // Microsoft Sign-Up
                    Button {
                        signUpWithMicrosoft()
                    } label: {
                        HStack {
                            Image(systemName: "square.grid.2x2.fill")
                                .font(.title3)
                            Text("Sign-Up with Microsoft")
                                .font(.body)
                                .fontWeight(.semibold)
                        }
                        .frame(maxWidth: .infinity)
                        .padding()
                        .background(Color(red: 0.0, green: 0.46, blue: 0.74))
                        .foregroundColor(.white)
                        .cornerRadius(8)
                    }
                    .disabled(isLoading)

                    // Email Sign-Up
                    Button {
                        showEmailSignUp = true
                    } label: {
                        HStack {
                            Image(systemName: "envelope.fill")
                                .font(.title3)
                            Text("Sign-Up with Email")
                                .font(.body)
                                .fontWeight(.semibold)
                        }
                        .frame(maxWidth: .infinity)
                        .padding()
                        .background(Color(red: 0.18, green: 0.67, blue: 0.90))
                        .foregroundColor(.white)
                        .cornerRadius(8)
                    }
                    .disabled(isLoading)
                }
                .padding(.horizontal, 24)

                Spacer()

                // Already have an account
                HStack(spacing: 4) {
                    Text("Already have an account?")
                        .font(.subheadline)
                        .foregroundColor(.secondary)

                    Button {
                        dismiss()
                    } label: {
                        Text("Sign-In")
                            .font(.subheadline)
                            .fontWeight(.semibold)
                            .foregroundColor(.blue)
                    }
                }
                .padding(.bottom, 40)
            }

            // Loading overlay
            if isLoading {
                Color.black.opacity(0.3)
                    .ignoresSafeArea()

                ProgressView()
                    .progressViewStyle(CircularProgressViewStyle(tint: .white))
                    .scaleEffect(1.5)
            }
        }
        .sheet(isPresented: $showEmailSignUp) {
            EmailSignUpView()
        }
    }

    private func signUpWithGoogle() {
        showEmailSignUp = true
        // For now, trigger the email sign-up which will use Clerk's AuthView
        // TODO: Configure Clerk OAuth providers in dashboard and implement OAuth flow
    }

    private func signUpWithMicrosoft() {
        showEmailSignUp = true
        // For now, trigger the email sign-up which will use Clerk's AuthView
        // TODO: Configure Clerk OAuth providers in dashboard and implement OAuth flow
    }
}

