                                        } label: {
                                            Image(systemName: "xmark.circle.fill")
                                                .foregroundColor(.gray)
                                        }
                                    }
                                }
                                .padding()
                                .background(Color(.systemGray6))
                                .cornerRadius(8)
                            }

                            // Re-enter Password
                            VStack(alignment: .leading, spacing: 8) {
                                Text("Password")
                                    .font(.subheadline)
                                    .fontWeight(.medium)
                                    .foregroundColor(.primary)

                                HStack {
                                    Image(systemName: "lock")
                                        .foregroundColor(.gray)
                                        .frame(width: 20)

                                    if showConfirmPassword {
                                        TextField("Re-enter Password", text: $confirmPassword)
                                            .autocapitalization(.none)
                                    } else {
                                        SecureField("Re-enter Password", text: $confirmPassword)
                                            .autocapitalization(.none)
                                    }

                                    Button {
                                        showConfirmPassword.toggle()
                                    } label: {
                                        Image(systemName: showConfirmPassword ? "eye.slash" : "eye")
                                            .foregroundColor(.gray)
                                    }

                                    if !confirmPassword.isEmpty {
                                        Button {
                                            confirmPassword = ""
                                        } label: {
                                            Image(systemName: "xmark.circle.fill")
                                                .foregroundColor(.gray)
                                        }
                                    }
                                }
                                .padding()
                                .background(Color(.systemGray6))
                                .cornerRadius(8)
                            }
                        }

                        // Primary Sport Dropdown
                        VStack(alignment: .leading, spacing: 8) {
                            Text("Primary Sport")
                                .font(.subheadline)
                                .fontWeight(.medium)
                                .foregroundColor(.primary)

                            Menu {
                                ForEach(sports, id: \.self) { sport in
                                    Button(sport) {
                                        primarySport = sport
                                    }
                                }
                            } label: {
                                HStack {
                                    Text(primarySport)
                                        .foregroundColor(.primary)
                                    Spacer()
                                    Image(systemName: "chevron.down")
                                        .foregroundColor(.gray)
                                        .font(.caption)
                                }
                                .padding()
                                .background(Color(.systemGray6))
                                .cornerRadius(8)
                            }
                        }

                        // Graduation Year Dropdown
                        VStack(alignment: .leading, spacing: 8) {
                            Text("Graduation Year")
                                .font(.subheadline)
                                .fontWeight(.medium)
                                .foregroundColor(.primary)

                            Menu {
                                ForEach(graduationYears, id: \.self) { year in
                                    Button(year) {
                                        graduationYear = year
                                    }
                                }
                            } label: {
                                HStack {
                                    Text(graduationYear)
                                        .foregroundColor(.primary)
                                    Spacer()
                                    Image(systemName: "chevron.down")
                                        .foregroundColor(.gray)
                                        .font(.caption)
                                }
                                .padding()
                                .background(Color(.systemGray6))
                                .cornerRadius(8)
                            }
                        }
                    }
                    .padding(.horizontal, 24)

                    // Error message
                    if let errorMessage = errorMessage {
                        HStack {
                            Image(systemName: "exclamationmark.triangle.fill")
                            Text(errorMessage)
                                .font(.footnote)
                        }
                        .foregroundColor(.red)
                        .padding()
                        .background(Color.red.opacity(0.1))
                        .cornerRadius(10)
                        .padding(.horizontal, 24)
                        .transition(.scale.combined(with: .opacity))
                    }

                    // Submit Button
                    Button {
                        submitForm()
                    } label: {
                        HStack {
                            if isLoading {
                                ProgressView()
                                    .progressViewStyle(CircularProgressViewStyle(tint: .white))
                                    .scaleEffect(0.9)
                            } else {
                                Text("Submit")
                                    .font(.headline)
                                    .fontWeight(.semibold)
                            }
                        }
                        .frame(maxWidth: .infinity)
                        .padding()
                        .background(
                            isFormValid ? Color.blue : Color.gray.opacity(0.5)
                        )
                        .foregroundStyle(.white)
                        .cornerRadius(8)
                    }
                    .disabled(!isFormValid || isLoading)
                    .padding(.horizontal, 24)
                    .padding(.top, 8)

                    Spacer()
                }
                .padding(.bottom, 40)
            }
        }
        .navigationDestination(isPresented: $navigateToPositionSelection) {
            PrimaryPositionView(userName: firstName, sport: primarySport)
        }
        .onAppear {
            // Pre-populate fields from Clerk user if available
            if let user = clerk.user {
                if firstName.isEmpty, let clerkFirstName = user.firstName {
                    firstName = clerkFirstName
                }
                if lastName.isEmpty, let clerkLastName = user.lastName {
                    lastName = clerkLastName
                }
                if email.isEmpty, let primaryEmail = user.emailAddresses.first?.emailAddress {
                    email = primaryEmail
                }
            }
        }
    }

    private var isFormValid: Bool {
        // Basic validation
        let basicFieldsValid = !firstName.isEmpty && !lastName.isEmpty

        if usedEmailSignUp {
            // Email sign-up: validate email and password
            let emailValid = !email.isEmpty && email.contains("@")
            let passwordValid = !password.isEmpty && password.count >= 8
            let passwordsMatch = password == confirmPassword

            return basicFieldsValid && emailValid && passwordValid && passwordsMatch
        } else {
            // OAuth sign-up: only basic fields needed
            return basicFieldsValid
        }
    }

    private func submitForm() {
        errorMessage = nil
        isLoading = true

        // Validate password match for email sign-up
        if usedEmailSignUp && password != confirmPassword {
            errorMessage = "Passwords do not match"
            isLoading = false
            return
        }

        // TODO: Submit user profile data to backend
        // For now, just simulate a delay
        DispatchQueue.main.asyncAfter(deadline: .now() + 1.5) {
            isLoading = false
            // Navigate to position selection screen
            navigateToPositionSelection = true
        }
    }
}

