import SwiftUI
import Clerk

struct AthleteSignUpView: View {
    @Environment(\.clerk) private var clerk
    @Environment(\.dismiss) private var dismiss

    // Auth method determines which fields to show
    let authMethod: SignUpAuthView.AuthMethod

    // Form fields
    @State private var firstName: String = ""
    @State private var lastName: String = ""
    @State private var email: String = ""
    @State private var password: String = ""
    @State private var confirmPassword: String = ""
    @State private var primarySport: String = "Volleyball"
    @State private var graduationYear: String = "2025"

    // UI state
    @State private var isLoading = false
    @State private var errorMessage: String?
    @State private var showPassword = false
    @State private var showConfirmPassword = false
    @State private var navigateToPositionSelection = false
    @State private var isAuthenticating = false

    // Determine if user signed up with email or OAuth based on auth method
    private var usedEmailSignUp: Bool {
        return authMethod == .email
    }

    private let sports = [
        "Volleyball", "Basketball", "Football", "Soccer", "Baseball",
        "Softball", "Track & Field", "Swimming", "Tennis", "Golf",
        "Lacrosse", "Wrestling", "Cross Country", "Gymnastics", "Hockey"
    ]

    private let graduationYears = Array(2025...2032).map { String($0) }

    var body: some View {
        ZStack {
            // Background
            Color(.systemBackground)
                .ignoresSafeArea()

            ScrollView {
                VStack(spacing: 24) {
                    // Header
                    VStack(spacing: 12) {
                        Image(systemName: "target")
                            .font(.system(size: 60))
                            .foregroundStyle(
                                LinearGradient(
                                    colors: [.blue, .purple],
                                    startPoint: .topLeading,
                                    endPoint: .bottomTrailing
                                )
                            )

                        Text("ATHLEAD")
                            .font(.system(size: 32, weight: .bold))
                            .foregroundColor(.blue)

                        Text("Athlete Sign Up")
                            .font(.title3)
                            .fontWeight(.semibold)
                    }
                    .padding(.top, 40)
                    .padding(.bottom, 8)

                    // Form
                    VStack(spacing: 16) {
                        // First Name
                        FormField(
                            label: "First Name",
                            icon: "person",
                            placeholder: "First Name",
                            text: $firstName
                        )

                        // Last Name
                        FormField(
                            label: "Last Name",
                            icon: "person",
                            placeholder: "Last Name",
                            text: $lastName
                        )

                        // Email (only shown for email sign-up)
                        if usedEmailSignUp {
                            FormField(
                                label: "Email",
                                icon: "envelope",
                                placeholder: "Email",
                                text: $email,
                                keyboardType: .emailAddress,
                                autocapitalization: .never
                            )

                            // Password
                            VStack(alignment: .leading, spacing: 8) {
                                Text("Password")
                                    .font(.subheadline)
                                    .fontWeight(.medium)
                                    .foregroundColor(.primary)

                                HStack {
                                    Image(systemName: "lock")
                                        .foregroundColor(.gray)
                                        .frame(width: 20)

                                    if showPassword {
                                        TextField("Password", text: $password)
                                            .autocapitalization(.none)
                                    } else {
                                        SecureField("Password", text: $password)
                                            .autocapitalization(.none)
                                    }

                                    Button {
                                        showPassword.toggle()
                                    } label: {
                                        Image(systemName: showPassword ? "eye.slash" : "eye")
                                            .foregroundColor(.gray)
                                    }

                                    if !password.isEmpty {
                                        Button {
                                            password = ""
                                        } label: {
                                            Image(systemName: "xmark.circle.fill")
                                                .foregroundColor(.gray)
                                        }
                                    }
                                }
                                .padding()
                                .background(Color(.systemGray6))
                                .cornerRadius(8)
                            }

                            // Re-enter Password
                            VStack(alignment: .leading, spacing: 8) {
                                Text("Password")
                                    .font(.subheadline)
                                    .fontWeight(.medium)
                                    .foregroundColor(.primary)

                                HStack {
                                    Image(systemName: "lock")
                                        .foregroundColor(.gray)
                                        .frame(width: 20)

                                    if showConfirmPassword {
                                        TextField("Re-enter Password", text: $confirmPassword)
                                            .autocapitalization(.none)
                                    } else {
                                        SecureField("Re-enter Password", text: $confirmPassword)
                                            .autocapitalization(.none)
                                    }

                                    Button {
                                        showConfirmPassword.toggle()
                                    } label: {
                                        Image(systemName: showConfirmPassword ? "eye.slash" : "eye")
                                            .foregroundColor(.gray)
                                    }

                                    if !confirmPassword.isEmpty {
                                        Button {
                                            confirmPassword = ""
                                        } label: {
                                            Image(systemName: "xmark.circle.fill")
                                                .foregroundColor(.gray)
                                        }
                                    }
                                }
                                .padding()
                                .background(Color(.systemGray6))
                                .cornerRadius(8)
                            }
                        }

                        // Primary Sport Dropdown
                        VStack(alignment: .leading, spacing: 8) {
                            Text("Primary Sport")
                                .font(.subheadline)
                                .fontWeight(.medium)
                                .foregroundColor(.primary)

                            Menu {
                                ForEach(sports, id: \.self) { sport in
                                    Button(sport) {
                                        primarySport = sport
                                    }
                                }
                            } label: {
                                HStack {
                                    Text(primarySport)
                                        .foregroundColor(.primary)
                                    Spacer()
                                    Image(systemName: "chevron.down")
                                        .foregroundColor(.gray)
                                        .font(.caption)
                                }
                                .padding()
                                .background(Color(.systemGray6))
                                .cornerRadius(8)
                            }
                        }

                        // Graduation Year Dropdown
                        VStack(alignment: .leading, spacing: 8) {
                            Text("Graduation Year")
                                .font(.subheadline)
                                .fontWeight(.medium)
                                .foregroundColor(.primary)

                            Menu {
                                ForEach(graduationYears, id: \.self) { year in
                                    Button(year) {
                                        graduationYear = year
                                    }
                                }
                            } label: {
                                HStack {
                                    Text(graduationYear)
                                        .foregroundColor(.primary)
                                    Spacer()
                                    Image(systemName: "chevron.down")
                                        .foregroundColor(.gray)
                                        .font(.caption)
                                }
                                .padding()
                                .background(Color(.systemGray6))
                                .cornerRadius(8)
                            }
                        }
                    }
                    .padding(.horizontal, 24)

                    // Error message
                    if let errorMessage = errorMessage {
                        HStack {
                            Image(systemName: "exclamationmark.triangle.fill")
                            Text(errorMessage)
                                .font(.footnote)
                        }
                        .foregroundColor(.red)
                        .padding()
                        .background(Color.red.opacity(0.1))
                        .cornerRadius(10)
                        .padding(.horizontal, 24)
                        .transition(.scale.combined(with: .opacity))
                    }

                    // Submit Button
                    Button {
                        submitForm()
                    } label: {
                        HStack {
                            if isLoading {
                                ProgressView()
                                    .progressViewStyle(CircularProgressViewStyle(tint: .white))
                                    .scaleEffect(0.9)
                            } else {
                                Text("Submit")
                                    .font(.headline)
                                    .fontWeight(.semibold)
                            }
                        }
                        .frame(maxWidth: .infinity)
                        .padding()
                        .background(
                            isFormValid ? Color.blue : Color.gray.opacity(0.5)
                        )
                        .foregroundStyle(.white)
                        .cornerRadius(8)
                    }
                    .disabled(!isFormValid || isLoading)
                    .padding(.horizontal, 24)
                    .padding(.top, 8)

                    Spacer()
                }
                .padding(.bottom, 40)
            }
        }
        .navigationDestination(isPresented: $navigateToPositionSelection) {
            PrimaryPositionView(userName: firstName, sport: primarySport)
        }
        .onAppear {
            // For OAuth users, trigger authentication immediately
            if authMethod != .email && !isAuthenticating {
                isAuthenticating = true
                authenticateWithOAuth()
            }

            // Pre-populate fields from Clerk user if available
            if let user = clerk.user {
                if firstName.isEmpty, let clerkFirstName = user.firstName {
                    firstName = clerkFirstName
                }
                if lastName.isEmpty, let clerkLastName = user.lastName {
                    lastName = clerkLastName
                }
                if email.isEmpty, let primaryEmail = user.emailAddresses.first?.emailAddress {
                    email = primaryEmail
                }
            }
        }
    }

    private func authenticateWithOAuth() {
        // TODO: Implement OAuth authentication with Clerk
        // For now, just show a message that OAuth is not yet configured
        errorMessage = "OAuth authentication not yet configured. Please use email sign-up."
        isAuthenticating = false
    }

    private var isFormValid: Bool {
        // Basic validation
        let basicFieldsValid = !firstName.isEmpty && !lastName.isEmpty

        if usedEmailSignUp {
            // Email sign-up: validate email and password
            let emailValid = !email.isEmpty && email.contains("@")
            let passwordValid = !password.isEmpty && password.count >= 8
            let passwordsMatch = password == confirmPassword

            return basicFieldsValid && emailValid && passwordValid && passwordsMatch
        } else {
            // OAuth sign-up: only basic fields needed
            return basicFieldsValid
        }
    }

    private func submitForm() {
        errorMessage = nil
        isLoading = true

        // Validate password match for email sign-up
        if usedEmailSignUp && password != confirmPassword {
            errorMessage = "Passwords do not match"
            isLoading = false
            return
        }

        // TODO: Submit user profile data to backend
        // For now, just simulate a delay
        DispatchQueue.main.asyncAfter(deadline: .now() + 1.5) {
            isLoading = false
            // Navigate to position selection screen
            navigateToPositionSelection = true
        }
    }
}

