import SwiftUI

struct WelcomeView: View {
    @Environment(\.designTheme) private var theme
    @Binding var showSignIn: Bool
    @State private var currentPage = 0

    var body: some View {
        VStack(spacing: .spacing10) {
                Spacer()

                // App logo
                Image("athlead_light")
                    .resizable()
                    .scaledToFit()
                    .frame(maxWidth: 280)
                    .padding(.top, 20)
                    .accessibilityLabel("AthleadAI logo")

                // Features
                TabView(selection: $currentPage) {
                    FeatureCard(
                        icon: "person.3.fill",
                        title: "Connect with Coaches",
                        description: "Build relationships with college coaches and recruiters across the country"
                    )
                    .tag(0)

                    FeatureCard(
                        icon: "sparkles",
                        title: "AI-Powered Insights",
                        description: "Get personalized recommendations and insights to improve your recruiting journey"
                    )
                    .tag(1)

                    FeatureCard(
                        icon: "video.fill",
                        title: "Showcase Your Talent",
                        description: "Share highlights, stats, and achievements to stand out to recruiters"
                    )
                    .tag(2)
                }
                .tabViewStyle(.page(indexDisplayMode: .always))
                .indexViewStyle(.page(backgroundDisplayMode: .always))
                .frame(height: 280)

                Spacer()

                // Get Started button
                VStack(spacing: .spacing4) {
                    BrandButton(
                        title: "Get Started",
                        icon: "arrow.right",
                        style: .primary,
                        size: .medium
                    ) {
                        withAnimation(.easeInOut(duration: 0.3)) {
                            showSignIn = true
                        }
                    }

                    Text("By continuing, you agree to our Terms & Privacy Policy")
                        .font(.caption)
                        .foregroundStyle(theme.color(.mutedforeground))
                        .multilineTextAlignment(.center)
                }
                .padding(.horizontal, 32)
                .padding(.bottom, 40)
            }
            .withDesignTheme()
            .brandBackground(.auth)
    }
}

