//
//  SignUpAuthView.swift
//  AthleadAI
//
//  Sign-up view using Clerk authentication
//

import SwiftUI
import Clerk

struct SignUpAuthView: View {
    @Environment(\.designTheme) private var theme
    @Environment(\.clerk) private var clerk
    @Environment(\.dismiss) private var dismiss

    let userType: UserType

    @State private var isLoading = false
    @State private var errorMessage: String?
    @State private var showEmailSignUp = false
    @State private var navigateToOnboarding = false
    @State private var selectedAuthMethod: AuthMethod?

    enum AuthMethod {
        case google
        case microsoft
        case email
    }

    var body: some View {
        VStack(spacing: 0) {
            // Header
            VStack(spacing: .spacing4) {
                Text("User Type Sign Up")
                    .font(.caption)
                    .foregroundStyle(.white.opacity(0.9))
                    .frame(maxWidth: .infinity, alignment: .leading)
                    .padding(.horizontal, 24)
                    .padding(.top, 20)

                Image("athlead_light")
                    .resizable()
                    .scaledToFit()
                    .frame(maxWidth: 240)
                    .accessibilityLabel("AthleadAI logo")
            }
            .padding(.bottom, 40)

            // Error message
            if let errorMessage = errorMessage {
                HStack {
                    Image(systemName: "exclamationmark.triangle.fill")
                    Text(errorMessage)
                        .font(.footnote)
                }
                .foregroundStyle(theme.color(.destructive))
                .glassCard(cornerRadius: .radiusMedium, padding: .spacing4)
                .padding(.horizontal, 24)
                .padding(.bottom, 16)
                .transition(.scale.combined(with: .opacity))
            }

            // Sign-up options
            VStack(spacing: .spacing4) {
                // Google Sign-Up
                BrandButton(
                    title: "Sign-Up with Google",
                    icon: "g.circle.fill",
                    style: .destructive,
                    size: .medium,
                    isLoading: isLoading
                ) {
                    selectedAuthMethod = .google
                    navigateToOnboarding = true
                }
                .disabled(isLoading)

                // Microsoft Sign-Up
                BrandButton(
                    title: "Sign-Up with Microsoft",
                    icon: "square.grid.2x2.fill",
                    style: .primary,
                    size: .medium,
                    isLoading: isLoading
                ) {
                    selectedAuthMethod = .microsoft
                    navigateToOnboarding = true
                }
                .disabled(isLoading)

                // Email Sign-Up
                BrandButton(
                    title: "Sign-Up with Email",
                    icon: "envelope.fill",
                    style: .secondary,
                    size: .medium,
                    isLoading: isLoading
                ) {
                    selectedAuthMethod = .email
                    navigateToOnboarding = true
                }
                .disabled(isLoading)
            }
            .padding(.horizontal, 24)

            Spacer()

            // Already have an account
            HStack(spacing: .spacing1) {
                Text("Already have an account?")
                    .font(.subheadline)
                    .foregroundStyle(theme.color(.mutedforeground))

                Button {
                    dismiss()
                } label: {
                    Text("Sign-In")
                        .font(.subheadline)
                        .fontWeight(.semibold)
                        .foregroundStyle(theme.color(.primary))
                }
            }
            .padding(.bottom, 40)
        }
        .withDesignTheme()
        .brandBackground(.auth)
        .navigationDestination(isPresented: $navigateToOnboarding) {
            // Route to appropriate onboarding based on user type
            if userType == .athlete {
                AthleteProfileSetupView(authMethod: selectedAuthMethod ?? .email)
            } else if userType == .parent {
                ParentProfileSetupView(authMethod: selectedAuthMethod ?? .email)
            } else if userType == .collegeCoach {
                CollegeCoachProfileSetupView(authMethod: selectedAuthMethod ?? .email)
            } else if userType == .clubCoach {
                CoachProfileSetupView(authMethod: selectedAuthMethod ?? .email)
            } else {
                // Fallback for any unexpected user types
                Text("Onboarding for \(userType.rawValue) coming soon!")
                    .font(.title2)
                    .padding()
            }
        }
    }
}

