//
//  SplashScreenView.swift
//  AthleadAI
//
//  Splash screen with app branding and fade animation
//

import SwiftUI

struct SplashScreenView: View {
    @Environment(\.designTheme) private var theme
    @State private var opacity: Double = 0
    @State private var scale: CGFloat = 0.8

    var body: some View {
        ZStack {
            // Gradient background using theme primary color
            theme.color(.primary)
                .ignoresSafeArea()

            VStack(spacing: .spacing5) {
                // App logo
                Image("athlead_light")
                    .resizable()
                    .scaledToFit()
                    .frame(maxWidth: 320)
                    .accessibilityLabel("AthleadAI logo")

                // Tagline
                Text("Your Path to Athletic Success")
                    .font(.title3)
                    .fontWeight(.medium)
                    .foregroundStyle(.white.opacity(0.9))
            }
            .scaleEffect(scale)
            .opacity(opacity)
        }
        .withDesignTheme()
        .onAppear {
            withAnimation(.easeOut(duration: 0.6)) {
                opacity = 1
                scale = 1
            }
        }
    }
}

