import SwiftUI

struct CollegeCoachProfileView: View {
    var viewModel: CoachProfileViewModel
    @State private var showingEditView = false
    @State private var expandedSections: Set<String> = ["about"]

    @Environment(\.designTheme) private var theme

    var body: some View {
        ScrollView {
            VStack(spacing: 0) {
                // Hero Section
                CoachHeroSection(
                    profileImageURL: viewModel.profileImageURL,
                    name: viewModel.fullName,
                    title: viewModel.title,
                    schoolName: viewModel.schoolName,
                    programColors: viewModel.programColors,
                    onMessage: { viewModel.initiateMessage() },
                    onConnect: { viewModel.connect() }
                )

                // Stats Bar
                StatsBarView(
                    stats: [
                        Stat(value: "\(viewModel.yearsExperience)", label: "Years Experience"),
                        Stat(value: "\(viewModel.titlesWon)", label: "Titles Won"),
                        Stat(value: viewModel.division, label: "Division")
                    ]
                )
                .padding(.horizontal, .spacing5)
                .padding(.top, -30)

                // Content Sections
                VStack(spacing: .spacing4) {
                    // About Section
                    ExpandableSection(
                        title: "About",
                        isExpanded: expandedSections.contains("about")
                    ) {
                        toggleSection("about")
                    } content: {
                        Text(viewModel.bio)
                            .font(.body)
                            .foregroundColor(.primary)
                            .padding(.spacing5)
                    }

                    // Credentials Section
                    ExpandableSection(
                        title: "Credentials & Education",
                        isExpanded: expandedSections.contains("credentials")
                    ) {
                        toggleSection("credentials")
                    } content: {
                        VStack(alignment: .leading, spacing: .spacing4) {
                            ForEach(viewModel.education) { edu in
                                CredentialRow(
                                    icon: "graduationcap.fill",
                                    title: "\(edu.degree) \(edu.field)",
                                    subtitle: edu.institution,
                                    iconColor: .blue
                                )
                            }

                            ForEach(viewModel.certifications) { cert in
                                CredentialRow(
                                    icon: "seal.fill",
                                    title: cert.name,
                                    subtitle: cert.organization,
                                    iconColor: .green
                                )
                            }
                        }
                        .padding(.spacing5)
                    }

                    // Philosophy Section
                    if !viewModel.philosophy.isEmpty {
                        ExpandableSection(
                            title: "Coaching Philosophy",
                            isExpanded: expandedSections.contains("philosophy")
                        ) {
                            toggleSection("philosophy")
                        } content: {
                            Text(viewModel.philosophy)
                                .font(.body)
                                .foregroundColor(.primary)
                                .padding(.spacing5)
                        }
                    }

                    // Career Highlights
                    ExpandableSection(
                        title: "Career Highlights",
                        isExpanded: expandedSections.contains("highlights")
                    ) {
                        toggleSection("highlights")
                    } content: {
                        VStack(alignment: .leading, spacing: .spacing3) {
                            ForEach(viewModel.highlights, id: \.self) { highlight in
                                HStack(alignment: .top, spacing: .spacing2) {
                                    Text("")
                                        .foregroundStyle(theme.color(.primary))
                                        .font(.headline)
                                    Text(highlight)
                                        .font(.body)
                                        .foregroundColor(.primary)
                                }
                            }
                        }
                        .padding(.spacing5)
                    }

                    // Associated Program Link
                    MenuItem(
                        icon: "building.2.fill",
                        title: viewModel.programName,
                        iconColor: .orange,
                        destination: CollegeCoachProgramProfileView(viewModel: viewModel.programViewModel)
                    )

                    // Contact Information
                    ExpandableSection(
                        title: "Contact Information",
                        isExpanded: expandedSections.contains("contact")
                    ) {
                        toggleSection("contact")
                    } content: {
                        VStack(spacing: .spacing3) {
                            ContactRow(icon: "envelope.fill", value: viewModel.email)
                            if !viewModel.phone.isEmpty {
                                ContactRow(icon: "phone.fill", value: viewModel.phone)
                            }
                            if !viewModel.officeLocation.isEmpty {
                                ContactRow(icon: "building.fill", value: viewModel.officeLocation)
                            }
                        }
                        .padding(.spacing5)
                    }
                }
                .padding(.horizontal, .spacing5)
                .padding(.top, .spacing5)
                .padding(.bottom, 40)
            }
        }
        .background(Color(.systemBackground))
        .navigationTitle("Coach Profile")
        .navigationBarTitleDisplayMode(.inline)
        .toolbar {
            ToolbarItem(placement: .navigationBarTrailing) {
                if viewModel.isOwnProfile {
                    Button("Edit") {
                        showingEditView = true
                    }
                }
            }
        }
        .sheet(isPresented: $showingEditView) {
            // TODO: Implement EditCoachProfileView
            Text("Edit Profile")
                .font(.title2)
                .padding()
        }
    }

    private func toggleSection(_ section: String) {
        withAnimation(.spring()) {
            if expandedSections.contains(section) {
                expandedSections.remove(section)
            } else {
                expandedSections.insert(section)
            }
        }
    }
}

// MARK: - Hero Section Component
struct CoachHeroSection: View {
    let profileImageURL: String
    let name: String
    let title: String
    let schoolName: String
    let programColors: [Color]
    let onMessage: () -> Void
    let onConnect: () -> Void

    @Environment(\.designTheme) private var theme

    var body: some View {
        ZStack(alignment: .bottom) {
            // Background gradient
            LinearGradient(
                gradient: Gradient(colors: programColors),
                startPoint: .topLeading,
                endPoint: .bottomTrailing
            )
            .frame(height: 350)

            VStack(spacing: .spacing4) {
                // Profile Photo
                AsyncImage(url: URL(string: profileImageURL)) { image in
                    image
                        .resizable()
                        .aspectRatio(contentMode: .fill)
                } placeholder: {
                    Image(systemName: "person.circle.fill")
                        .resizable()
                        .foregroundStyle(.white.opacity(0.5))
                }
                .frame(width: 120, height: 120)
                .clipShape(Circle())
                .overlay(
                    Circle()
                        .stroke(Color.white, lineWidth: 4)
                )
                .shadow(radius: 10)

                // Name and Title
                VStack(spacing: 4) {
                    Text(name)
                        .font(.title)
                        .fontWeight(.bold)
                        .foregroundStyle(.white)

                    Text(title)
                        .font(.body)
                        .fontWeight(.semibold)
                        .foregroundStyle(.white.opacity(0.9))

                    Text(schoolName)
                        .font(.subheadline)
                        .foregroundStyle(.white.opacity(0.8))
                }

                // Action Buttons
                HStack(spacing: .spacing3) {
                    Button(action: onMessage) {
                        HStack(spacing: .spacing2) {
                            Image(systemName: "envelope.fill")
                            Text("Message")
                        }
                        .font(.subheadline)
                        .fontWeight(.semibold)
                        .foregroundStyle(.white)
                        .padding(.horizontal, .spacing5)
                        .padding(.vertical, .spacing2)
                    }
                    .photoOverlayCard(material: .ultraThinMaterial, cornerRadius: .radiusLarge, padding: 0)

                    Button(action: onConnect) {
                        HStack(spacing: .spacing2) {
                            Image(systemName: "link")
                            Text("Connect")
                        }
                        .font(.subheadline)
                        .fontWeight(.semibold)
                        .foregroundStyle(.white)
                        .padding(.horizontal, .spacing5)
                        .padding(.vertical, .spacing2)
                    }
                    .photoOverlayCard(material: .ultraThinMaterial, cornerRadius: .radiusLarge, padding: 0)
                }
            }
            .padding(.bottom, 40)
        }
    }
}

// MARK: - Stats Bar Component
struct StatsBarView: View {
    let stats: [Stat]

    var body: some View {
        HStack(spacing: 0) {
            ForEach(Array(stats.enumerated()), id: \.offset) { index, stat in
                VStack(spacing: 4) {
                    Text(stat.value)
                        .font(.title2)
                        .fontWeight(.bold)
                        .foregroundColor(.primary)

                    Text(stat.label)
                        .font(.caption)
                        .foregroundColor(.secondary)
                }
                .frame(maxWidth: .infinity)

                if index < stats.count - 1 {
                    Divider()
                        .frame(height: 40)
                }
            }
        }
        .padding(.vertical, .spacing4)
        .background(Color(.systemBackground))
        .cornerRadius(.radiusMedium)
        .shadow(color: Color.black.opacity(0.1), radius: .spacing3, x: 0, y: .spacing1)
    }
}

// MARK: - Expandable Section Component
struct ExpandableSection<Content: View>: View {
    let title: String
    let isExpanded: Bool
    let action: () -> Void
    @ViewBuilder let content: () -> Content

    var body: some View {
        VStack(spacing: 0) {
            // Header
            Button(action: action) {
                HStack {
                    Image(systemName: "chevron.right")
                        .rotationEffect(.degrees(isExpanded ? 90 : 0))
                        .foregroundColor(.secondary)
                        .font(.system(size: 14, weight: .semibold))

                    Text(title)
                        .font(.system(size: 18, weight: .semibold))
                        .foregroundColor(.primary)

                    Spacer()
                }
                .padding(.vertical, 12)
            }
            .buttonStyle(PlainButtonStyle())

            // Content
            if isExpanded {
                VStack(spacing: 0) {
                    content()
                }
                .background(Color(.systemBackground))
                .cornerRadius(.radiusMedium)
                .overlay(
                    RoundedRectangle(cornerRadius: .radiusMedium)
                        .stroke(Color(.separator), lineWidth: 1)
                )
                .transition(.opacity.combined(with: .move(edge: .top)))
            }
        }
    }
}

// MARK: - Credential Row Component
struct CredentialRow: View {
    let icon: String
    let title: String
    let subtitle: String
    let iconColor: Color

    var body: some View {
        HStack(spacing: .spacing4) {
            Image(systemName: icon)
                .foregroundStyle(.white)
                .frame(width: 40, height: 40)
                .background(iconColor)
                .clipShape(Circle())

            VStack(alignment: .leading, spacing: 4) {
                Text(title)
                    .font(.body)
                    .fontWeight(.semibold)
                    .foregroundColor(.primary)

                Text(subtitle)
                    .font(.caption)
                    .foregroundColor(.secondary)
            }

            Spacer()
        }
    }
}

// MARK: - Contact Row Component
struct ContactRow: View {
    let icon: String
    let value: String

    @Environment(\.designTheme) private var theme

    var body: some View {
        HStack(spacing: .spacing3) {
            Image(systemName: icon)
                .foregroundStyle(theme.color(.primary))
                .frame(width: 30)

            Text(value)
                .font(.body)
                .foregroundColor(.primary)

            Spacer()
        }
    }
}

