//
//  PhotoOverlayCard.swift
//  A specialized card component designed for overlaying content on top of photo backgrounds
//
//  Design Principles (Apple HIG):
//  - Uses Material effects for depth and readability on any background
//  - Provides sufficient contrast for WCAG AA accessibility compliance
//  - Adapts to iOS native blur/vibrancy system
//  - Works beautifully regardless of background photo color/brightness
//
//  Usage:
//  VStack {
//      Text("Name")
//      Text("Details")
//  }
//  .photoOverlayCard()
//
//  Or with custom options:
//  .photoOverlayCard(material: .regular, cornerRadius: .radiusLarge, padding: .spacing4)
//

import SwiftUI

// MARK: - PhotoOverlayCard ViewModifier

/// A view modifier that creates a card optimized for display over photo backgrounds
/// Uses iOS Material effects for universal readability and native feel
struct PhotoOverlayCardModifier: ViewModifier {
    var material: Material = .ultraThinMaterial
    var cornerRadius: CGFloat = .radiusLarge
    var padding: CGFloat = .spacing4
    var showBorder: Bool = true
    var shadowStrength: ShadowStrength = .medium

    @Environment(\.colorScheme) private var colorScheme

    func body(content: Content) -> some View {
        content
            .padding(padding)
            .background(
                RoundedRectangle(cornerRadius: cornerRadius, style: .continuous)
                    .fill(material)
                    .overlay(
                        // Subtle border for definition
                        RoundedRectangle(cornerRadius: cornerRadius, style: .continuous)
                            .strokeBorder(
                                Color.white.opacity(showBorder ? (colorScheme == .dark ? 0.2 : 0.3) : 0),
                                lineWidth: 1
                            )
                    )
                    .shadow(
                        color: Color.black.opacity(shadowStrength.opacity),
                        radius: shadowStrength.radius,
                        x: 0,
                        y: shadowStrength.yOffset
                    )
            )
    }
}

// MARK: - Shadow Strength Enum

/// Defines shadow strength for different emphasis levels
enum ShadowStrength {
    case none
    case subtle
    case medium
    case strong

    var opacity: Double {
        switch self {
        case .none: return 0
        case .subtle: return 0.1
        case .medium: return 0.15
        case .strong: return 0.25
        }
    }

    var radius: CGFloat {
        switch self {
        case .none: return 0
        case .subtle: return 8
        case .medium: return 12
        case .strong: return 20
        }
    }

    var yOffset: CGFloat {
        switch self {
        case .none: return 0
        case .subtle: return 2
        case .medium: return 4
        case .strong: return 8
        }
    }
}

// MARK: - View Extension

extension View {
    /// Applies photo overlay card styling optimized for display over photo backgrounds
    ///
    /// This modifier uses iOS Material effects to ensure readability on any background image.
    /// The material automatically adapts to light/dark mode and provides proper contrast.
    ///
    /// - Parameters:
    ///   - material: The Material type to use (default: .ultraThinMaterial)
    ///               Options: .ultraThinMaterial, .thinMaterial, .regularMaterial, .thickMaterial, .ultraThickMaterial
    ///   - cornerRadius: Corner radius of the card (default: .radiusLarge)
    ///   - padding: Internal padding (default: .spacing4)
    ///   - showBorder: Whether to show a subtle border (default: true)
    ///   - shadowStrength: Shadow strength for depth (default: .medium)
    /// - Returns: A view with photo overlay card styling applied
    ///
    /// ## Example Usage:
    /// ```swift
    /// // Basic usage (recommended for most cases)
    /// VStack {
    ///     Text("John Doe")
    ///     Text("Basketball  Guard  2025")
    /// }
    /// .photoOverlayCard()
    ///
    /// // Custom material for stronger blur
    /// VStack {
    ///     Text("Profile Info")
    /// }
    /// .photoOverlayCard(material: .regularMaterial)
    ///
    /// // Subtle card with minimal shadow
    /// VStack {
    ///     Text("Secondary Info")
    /// }
    /// .photoOverlayCard(shadowStrength: .subtle)
    /// ```
    func photoOverlayCard(
        material: Material = .ultraThinMaterial,
        cornerRadius: CGFloat = .radiusLarge,
        padding: CGFloat = .spacing4,
        showBorder: Bool = true,
        shadowStrength: ShadowStrength = .medium
    ) -> some View {
        modifier(PhotoOverlayCardModifier(
            material: material,
            cornerRadius: cornerRadius,
            padding: padding,
            showBorder: showBorder,
            shadowStrength: shadowStrength
        ))
    }
}

// MARK: - Container Version (Alternative API)

/// A container view that wraps content in a photo overlay card
/// This is an alternative to the modifier version, useful when you prefer container syntax
struct PhotoOverlayCard<Content: View>: View {
    let content: Content
    var material: Material = .ultraThinMaterial
    var cornerRadius: CGFloat = .radiusLarge
    var padding: CGFloat = .spacing4
    var showBorder: Bool = true
    var shadowStrength: ShadowStrength = .medium

    @Environment(\.colorScheme) private var colorScheme

    init(
        material: Material = .ultraThinMaterial,
        cornerRadius: CGFloat = .radiusLarge,
        padding: CGFloat = .spacing4,
        showBorder: Bool = true,
        shadowStrength: ShadowStrength = .medium,
        @ViewBuilder content: () -> Content
    ) {
        self.material = material
        self.cornerRadius = cornerRadius
        self.padding = padding
        self.showBorder = showBorder
        self.shadowStrength = shadowStrength
        self.content = content()
    }

    var body: some View {
        content
            .padding(padding)
            .background(
                RoundedRectangle(cornerRadius: cornerRadius, style: .continuous)
                    .fill(material)
                    .overlay(
                        RoundedRectangle(cornerRadius: cornerRadius, style: .continuous)
                            .strokeBorder(
                                Color.white.opacity(showBorder ? (colorScheme == .dark ? 0.2 : 0.3) : 0),
                                lineWidth: 1
                            )
                    )
                    .shadow(
                        color: Color.black.opacity(shadowStrength.opacity),
                        radius: shadowStrength.radius,
                        x: 0,
                        y: shadowStrength.yOffset
                    )
            )
    }
}

