//
//  AthleteFeedView.swift
//  AthleadAI
//
//  Feed view customized for athlete users with squad functionality
//

import SwiftUI

struct AthleteFeedView: View {
    @Environment(\.designTheme) private var theme
    @State private var viewModel = FeedViewModel(feedType: .athlete)

    var body: some View {
        NavigationView {
            ScrollView {
                VStack(spacing: .spacing5) {
                    // Squads Section
                    VStack(alignment: .leading, spacing: .spacing3) {
                        HStack {
                            Text("Squads")
                                .font(theme.font(.title3))
                                .fontWeight(.bold)
                                .foregroundStyle(theme.color(.foreground))

                            Spacer()

                            Button(action: {}) {
                                    Image(systemName: "plus.circle.fill")
                                        .foregroundStyle(theme.color(.primary))
                                        .font(theme.font(.title3))
                                }
                                .accessibilityLabel("Add new squad")
                                .accessibilityHint("Create or join a new squad")
                            }
                            .padding(.horizontal)

                            ScrollView(.horizontal, showsIndicators: false) {
                                HStack(spacing: .spacing4) {
                                    // All Posts (default)
                                    CircleFilter(
                                        name: "All",
                                        icon: "house.fill",
                                        iconColor: theme.color(.primary),
                                        isSelected: viewModel.selectedSquad == nil
                                    ) {
                                        viewModel.selectSquad(nil)
                                    }

                                    // Squad circles with custom images
                                    CircleFilter(
                                        name: "Carroll",
                                        imageName: "carroll",
                                        iconColor: theme.color(.primary),
                                        isSelected: viewModel.selectedSquad == "Carroll Volleyball"
                                    ) {
                                        viewModel.selectSquad("Carroll Volleyball")
                                    }

                                    CircleFilter(
                                        name: "2026",
                                        imageName: "26",
                                        iconColor: theme.color(.primary),
                                        isSelected: viewModel.selectedSquad == "Class of 2026"
                                    ) {
                                        viewModel.selectSquad("Class of 2026")
                                    }

                                    CircleFilter(
                                        name: "Texas",
                                        imageName: "tx",
                                        iconColor: theme.color(.primary),
                                        isSelected: viewModel.selectedSquad == "Texas Volleyball"
                                    ) {
                                        viewModel.selectSquad("Texas Volleyball")
                                    }
                                }
                                .padding(.horizontal)
                            }
                        }
                        .padding(.vertical, .spacing3)

                        // Feed Posts
                        VStack(spacing: .spacing4) {
                            ForEach(viewModel.filteredPosts, id: \.id) { post in
                                FeedPost(
                                    authorName: post.authorName,
                                    authorHandle: post.authorHandle,
                                    timeAgo: post.timeAgo,
                                    squad: post.squad,
                                    content: post.content,
                                    likes: post.likes,
                                    comments: post.comments,
                                    mediaType: post.mediaType
                                )
                            }
                        }
                        .padding(.horizontal)
                        .padding(.bottom, .spacing5)
                    .padding(.top, .spacing5)
                }
            }
            .withDesignTheme()
            .brandBackground(.feed)
            .navigationTitle("Feed")
            .navigationBarTitleDisplayMode(.inline)
            .toolbar {
                ToolbarItem(placement: .navigationBarTrailing) {
                    Button(action: { viewModel.toggleCreatePost() }) {
                        Image(systemName: "square.and.pencil")
                            .foregroundStyle(theme.color(.foreground))
                    }
                    .accessibilityLabel("Create new post")
                    .accessibilityHint("Opens a form to create and share a new post")
                }
            }
            .sheet(isPresented: $viewModel.showCreatePost) {
                CreatePostView()
            }
            .refreshable {
                await viewModel.refresh()
            }
            .task {
                await viewModel.loadFeed()
            }
        }
    }
}

// Create Post View (placeholder)
struct CreatePostView: View {
    @Environment(\.dismiss) var dismiss
    @Environment(\.designTheme) private var theme
    @State private var postContent = ""
    @State private var selectedSquad = "All"

    var body: some View {
        NavigationView {
            VStack(spacing: .spacing5) {
                // Squad picker
                    VStack(alignment: .leading, spacing: .spacing2) {
                        Text("Post to Squad")
                            .font(.subheadline)
                            .foregroundStyle(theme.color(.mutedforeground))

                        Menu {
                            Button("All") { selectedSquad = "All" }
                            Button("Carroll Volleyball") { selectedSquad = "Carroll Volleyball" }
                            Button("Class of 2026") { selectedSquad = "Class of 2026" }
                            Button("Texas Volleyball") { selectedSquad = "Texas Volleyball" }
                        } label: {
                            HStack {
                                Text(selectedSquad)
                                    .foregroundStyle(theme.color(.foreground))
                                Spacer()
                                Image(systemName: "chevron.down")
                                    .foregroundStyle(theme.color(.mutedforeground))
                                    .accessibilityHidden(true)
                            }
                            .liquidGlassCard()
                        }
                    }

                    // Text editor
                    TextEditor(text: $postContent)
                        .frame(height: 200)
                        .scrollContentBackground(.hidden)
                        .foregroundStyle(theme.color(.foreground))
                        .padding()
                        .liquidGlassCard()

                Spacer()
            }
            .padding()
            .withDesignTheme()
            .brandBackground(.feed)
            .navigationTitle("Create Post")
            .navigationBarTitleDisplayMode(.inline)
            .toolbar {
                ToolbarItem(placement: .navigationBarLeading) {
                    Button("Cancel") {
                        dismiss()
                    }
                    .foregroundStyle(theme.color(.foreground))
                }
                ToolbarItem(placement: .navigationBarTrailing) {
                    Button("Post") {
                        // Post logic here
                        dismiss()
                    }
                    .foregroundStyle(theme.color(.primary))
                    .fontWeight(.semibold)
                }
            }
        }
    }
}

#Preview {
    AthleteFeedView()
}

//
//  CircleFilter.swift
//  AthleadAI
//
//  Unified reusable circle filter component for both icon and image-based filters
//  Supports selection state, custom colors, and full accessibility
//

import SwiftUI

/// A reusable circular filter component that displays either an icon or image
/// with selection state and accessibility support.
///
/// Use cases:
/// - Squad/Group filters with custom images
/// - Category filters with SF Symbol icons
/// - Year, position, location filters
///
/// Example usage:
/// ```swift
/// CircleFilter(
///     name: "2026",
///     icon: "graduationcap.fill",
///     iconColor: .blue,
///     isSelected: selectedYear == "2026"
/// ) {
///     selectedYear = "2026"
/// }
/// ```
struct CircleFilter: View {
    @Environment(\.designTheme) private var theme

    let name: String
    let icon: String?
    let imageName: String?
    let iconColor: Color
    let isSelected: Bool
    let action: () -> Void

    /// Creates a circle filter with an icon
    /// - Parameters:
    ///   - name: Display name shown below the circle
    ///   - icon: SF Symbol name to display inside the circle
    ///   - iconColor: Color for the icon and selection state (default: .blue)
    ///   - isSelected: Whether this filter is currently selected
    ///   - action: Closure called when the filter is tapped
    init(
        name: String,
        icon: String,
        iconColor: Color,
        isSelected: Bool = false,
        action: @escaping () -> Void
    ) {
        self.name = name
        self.icon = icon
        self.imageName = nil
        self.iconColor = iconColor
        self.isSelected = isSelected
        self.action = action
    }

    /// Creates a circle filter with a custom image
    /// - Parameters:
    ///   - name: Display name shown below the circle
    ///   - imageName: Asset catalog image name to display inside the circle
    ///   - iconColor: Color for the selection state border
    ///   - isSelected: Whether this filter is currently selected
    ///   - action: Closure called when the filter is tapped
    init(
        name: String,
        imageName: String,
        iconColor: Color,
        isSelected: Bool = false,
        action: @escaping () -> Void
    ) {
        self.name = name
        self.icon = nil
        self.imageName = imageName
        self.iconColor = iconColor
        self.isSelected = isSelected
        self.action = action
    }

    var body: some View {
        Button(action: action) {
            VStack(spacing: .spacing2) {
                // Circle with content
                ZStack {
                    // Outer ring (gradient border)
                    Circle()
                        .strokeBorder(
                            LinearGradient(
                                colors: isSelected
                                    ? [iconColor, iconColor.opacity(0.6)]
                                    : [theme.color(.border), theme.color(.border)],
                                startPoint: .topLeading,
                                endPoint: .bottomTrailing
                            ),
                            lineWidth: 3
                        )
                        .frame(width: 70, height: 70)

                    // Inner circle with gradient fill
                    Circle()
                        .fill(
                            LinearGradient(
                                colors: isSelected
                                    ? [iconColor.opacity(0.3), iconColor.opacity(0.2)]
                                    : [theme.color(.muted).opacity(0.5), theme.color(.muted).opacity(0.3)],
                                startPoint: .topLeading,
                                endPoint: .bottomTrailing
                            )
                        )
                        .frame(width: 64, height: 64)

                    // Content (image or icon)
                    if let imageName = imageName {
                        Image(imageName)
                            .resizable()
                            .scaledToFill()
                            .frame(width: 64, height: 64)
                            .clipShape(Circle())
                    } else if let icon = icon {
                        Image(systemName: icon)
                            .font(theme.font(.title1))
                            .foregroundStyle(isSelected ? iconColor : theme.color(.mutedforeground))
                    }
                }

                // Name label
                Text(name)
                    .font(theme.font(.caption1))
                    .fontWeight(isSelected ? .semibold : .regular)
                    .foregroundStyle(isSelected ? theme.color(.foreground) : theme.color(.mutedforeground))
                    .lineLimit(1)
            }
            .frame(width: 80)
        }
        .accessibilityLabel("\(name) filter")
        .accessibilityAddTraits(isSelected ? .isSelected : [])
        .accessibilityHint(isSelected ? "Currently selected" : "Double tap to select")
    }
}

