// MARK: - Environment Key for Theme
struct DesignThemeKey: EnvironmentKey {
    static let defaultValue: DesignTheme = .defaultLight
}

extension EnvironmentValues {
    var designTheme: DesignTheme {
        get { self[DesignThemeKey.self] }
        set { self[DesignThemeKey.self] = newValue }
    }
}

// MARK: - View Extension for Easy Theme Access
extension View {
    /// Injects the appropriate design theme based on current color scheme
    func withDesignTheme() -> some View {
        self.modifier(DesignThemeModifier())
    }
}

private struct DesignThemeModifier: ViewModifier {
    @Environment(\.colorScheme) private var colorScheme

    func body(content: Content) -> some View {
        content
            .environment(\.designTheme, colorScheme == .dark ? .defaultDark : .defaultLight)
    }
}

// MARK: - Semantic Font Extensions
extension Font {
    /// Section title font (title2 with semibold weight)
    static let sectionTitle: Font = .system(.title2, design: .default, weight: .semibold)

    /// Body text font (standard body text)
    static let bodyText: Font = .system(.body, design: .default)
}

// MARK: - iOS 26+ Liquid Glass Helpers

/// Helper extension for applying glass effects with app-specific defaults (iOS 26+)
@available(iOS 18.0, *)
extension View {
    /// Applies the app's standard Liquid Glass effect with rounded corners
    ///
    /// This convenience method wraps SwiftUI's .glassEffect() API with app-specific
    /// defaults and performance monitoring. Supports interactive glass, custom tinting,
    /// and various glass variants.
    ///
    /// - Parameters:
    ///   - glass: The glass variant to use (default: .regular)
    ///   - cornerRadius: The corner radius for the glass shape (default: .radiusLarge)
    ///   - isInteractive: Whether the glass should respond to touch/pointer events (default: false)
    ///   - tint: Optional tint color for the glass effect (default: nil)
    /// - Returns: A view with the Liquid Glass effect applied
    ///
    /// ## Examples:
    /// ```swift
    /// // Basic glass effect
    /// VStack {
    ///     Text("Hello")
    /// }
    /// .appGlassEffect()
    ///
    /// // Interactive glass with custom variant
    /// Button("Tap Me") { }
    ///     .appGlassEffect(.thick, isInteractive: true)
    ///
    /// // Glass with tint and custom radius
    /// VStack {
    ///     Text("Tinted Glass")
    /// }
    /// .appGlassEffect(.clear, cornerRadius: .radiusSmall, tint: .blue)
    /// ```
    func appGlassEffect(
        _ glass: Glass = .regular,
        cornerRadius: CGFloat = .radiusLarge,
        isInteractive: Bool = false,
        tint: Color? = nil
    ) -> some View {
        var configuredGlass = glass

        if isInteractive {
            configuredGlass = configuredGlass.interactive()
        }

        if let tintColor = tint {
            configuredGlass = configuredGlass.tint(tintColor)
        }

        return self
            .glassEffect(
                configuredGlass,
                in: .rect(cornerRadius: cornerRadius)
            )
            .onAppear {
                #if DEBUG
                if FeatureFlags.glassEffectMonitoring {
                    GlassEffectCounter.increment()
                }
                #endif
            }
            .onDisappear {
                #if DEBUG
                if FeatureFlags.glassEffectMonitoring {
                    GlassEffectCounter.decrement()
                }
                #endif
            }
    }
}

