import SwiftUI

struct AIMatchedSchoolsSection: View {
    @Environment(\.designTheme) private var theme
    let matches: [SchoolMatch]
    let selectedFilter: SchoolMatch.MatchStatus?
    let onFilterChange: (SchoolMatch.MatchStatus?) -> Void
    let onSchoolTap: (School) -> Void

    var body: some View {
        VStack(alignment: .leading, spacing: theme.spacing(.spacing4)) {
            // Header
            HStack {
                VStack(alignment: .leading, spacing: theme.spacing(.spacing1)) {
                    Text("AI Matched Schools")
                        .font(theme.font(.title2))
                        .fontWeight(.bold)
                        .foregroundStyle(theme.color(.foreground))

                    Text("\(matches.count) matches found")
                        .font(theme.font(.caption1))
                        .foregroundStyle(theme.color(.foreground).opacity(0.6))
                }

                Spacer()

                Button(action: {
                    // View all action
                }) {
                    HStack(spacing: theme.spacing(.spacing1)) {
                        Text("View All")
                            .font(theme.font(.subheadline))
                        Image(systemName: "chevron.right")
                            .font(.caption)
                    }
                    .foregroundStyle(theme.color(.primary))
                }
            }
            .padding(.horizontal, theme.spacing(.spacing5))

            // Filter Pills
            ScrollView(.horizontal, showsIndicators: false) {
                HStack(spacing: theme.spacing(.spacing2)) {
                    FilterPill(
                        title: "All",
                        isSelected: selectedFilter == nil,
                        action: { onFilterChange(nil) }
                    )

                    ForEach([
                        SchoolMatch.MatchStatus.strong,
                        .good,
                        .moderate,
                        .potential
                    ], id: \.self) { status in
                        FilterPill(
                            title: status.rawValue,
                            isSelected: selectedFilter == status,
                            action: { onFilterChange(status) }
                        )
                    }
                }
                .padding(.horizontal, theme.spacing(.spacing5))
            }

            // Matched Schools List
            if matches.isEmpty {
                EmptyStateView(
                    icon: "magnifyingglass",
                    title: "No matches yet",
                    description: "Complete your profile to get AI-powered school recommendations"
                )
                .padding(theme.spacing(.spacing5))
            } else {
                ScrollView(.horizontal, showsIndicators: false) {
                    HStack(spacing: theme.spacing(.spacing3)) {
                        ForEach(matches.prefix(5)) { match in
                            SchoolMatchCard(
                                match: match,
                                onTap: { onSchoolTap(match.school) }
                            )
                        }
                    }
                    .padding(.horizontal, theme.spacing(.spacing5))
                }
            }
        }
        .padding(.vertical, theme.spacing(.spacing4))
    }
}

struct FilterPill: View {
    @Environment(\.designTheme) private var theme
    let title: String
    let isSelected: Bool
    let action: () -> Void

    var body: some View {
        Button(action: action) {
            Text(title)
