                    .font(theme.font(.caption1))
                    .foregroundStyle(theme.color(.foreground).opacity(0.7))
                    .lineLimit(2)
                    .multilineTextAlignment(.leading)
                    .frame(height: 32)

                // School Stats
                HStack(spacing: theme.spacing(.spacing3)) {
                    StatItem(icon: "sportscourt.fill", value: match.school.division)
                    StatItem(icon: "trophy.fill", value: match.school.ranking ?? "NR")
                }
