struct ActivityCard: View {
    @Environment(\.designTheme) private var theme
    let activity: ActivityItem
    let onTap: () -> Void

    var body: some View {
        Button(action: onTap) {
            HStack(spacing: theme.spacing(.spacing3)) {
                // Icon
                ZStack {
                    Circle()
                        .fill(activityColor.opacity(0.15))
                        .frame(width: 44, height: 44)

                    Image(systemName: activity.iconName)
                        .font(.system(size: 18))
                        .foregroundStyle(activityColor)
                }

                // Content
                VStack(alignment: .leading, spacing: theme.spacing(.spacing1)) {
                    Text(activity.title)
                        .font(theme.font(.subheadline))
                        .fontWeight(.semibold)
                        .foregroundStyle(theme.color(.foreground))
                        .lineLimit(1)

                    Text(activity.description)
                        .font(theme.font(.caption1))
                        .foregroundStyle(theme.color(.foreground).opacity(0.7))
                        .lineLimit(2)

                    Text(timeAgo)
                        .font(theme.font(.caption2))
                        .foregroundStyle(theme.color(.foreground).opacity(0.5))
                }

                Spacer()

                // Chevron
                Image(systemName: "chevron.right")
                    .font(.caption)
                    .foregroundStyle(theme.color(.foreground).opacity(0.3))
            }
            .padding(theme.spacing(.spacing4))
            .background(
                RoundedRectangle(cornerRadius: theme.radius(.radiusMedium))
                    .fill(theme.color(.card))
            )
        }
        .buttonStyle(.plain)
    }

    private var activityColor: Color {
        switch activity.type {
        case .newMatch:
            return .blue
        case .coachView:
            return .purple
        case .schoolFavorited:
            return .pink
        case .messageReceived:
            return .green
        case .profileUpdate:
            return .orange
        case .videoUploaded:
            return .red
        case .achievementUnlocked:
            return .yellow
        }
    }

    private var timeAgo: String {
        let now = Date()
        let interval = now.timeIntervalSince(activity.timestamp)

        let minutes = Int(interval / 60)
        let hours = Int(interval / 3600)
        let days = Int(interval / 86400)

        if minutes < 60 {
            return "\(minutes)m ago"
        } else if hours < 24 {
            return "\(hours)h ago"
        } else {
            return "\(days)d ago"
        }
    }
}

struct ActivityFeedView: View {
    @Environment(\.designTheme) private var theme
    let activities: [ActivityItem]

    var body: some View {
        ScrollView {
            LazyVStack(spacing: theme.spacing(.spacing3)) {
                ForEach(groupedActivities.keys.sorted(by: >), id: \.self) { date in
                    Section {
                        ForEach(groupedActivities[date] ?? []) { activity in
                            ActivityCard(activity: activity, onTap: {})
                        }
                    } header: {
                        HStack {
                            Text(sectionTitle(for: date))
                                .font(theme.font(.caption1))
                                .fontWeight(.semibold)
                                .foregroundStyle(theme.color(.foreground).opacity(0.6))
                                .textCase(.uppercase)

                            Spacer()
                        }
                        .padding(.horizontal, theme.spacing(.spacing5))
                        .padding(.top, theme.spacing(.spacing4))
                    }
                }
            }
            .padding(.horizontal, theme.spacing(.spacing5))
        }
    }

    private var groupedActivities: [String: [ActivityItem]] {
        Dictionary(grouping: activities) { activity in
            let calendar = Calendar.current
            let components = calendar.dateComponents([.year, .month, .day], from: activity.timestamp)
            return "\(components.year ?? 0)-\(components.month ?? 0)-\(components.day ?? 0)"
        }
    }

    private func sectionTitle(for dateKey: String) -> String {
        guard let firstActivity = groupedActivities[dateKey]?.first else {
            return dateKey
        }

        let calendar = Calendar.current
        let date = firstActivity.timestamp

        if calendar.isDateInToday(date) {
            return "Today"
        } else if calendar.isDateInYesterday(date) {
            return "Yesterday"
        } else {
            let formatter = DateFormatter()
            formatter.dateFormat = "MMMM d, yyyy"
            return formatter.string(from: date)
        }
    }
}

