                    Text("Profile Strength")
                        .font(theme.font(.subheadline))
                        .fontWeight(.semibold)
                        .foregroundStyle(theme.color(.foreground))

                    Spacer()

                    Text("85%")
                        .font(theme.font(.subheadline))
                        .fontWeight(.bold)
                        .foregroundStyle(theme.color(.primary))
                }

                GeometryReader { geometry in
                    ZStack(alignment: .leading) {
                        RoundedRectangle(cornerRadius: theme.radius(.radiusSmall))
                            .fill(theme.color(.border).opacity(0.3))
                            .frame(height: 8)

                        RoundedRectangle(cornerRadius: theme.radius(.radiusSmall))
                            .fill(
                                LinearGradient(
                                    colors: [theme.color(.primary), theme.color(.accent)],
                                    startPoint: .leading,
                                    endPoint: .trailing
                                )
                            )
                            .frame(width: geometry.size.width * 0.85, height: 8)
                    }
                }
                .frame(height: 8)

                Text("Add more videos and achievements to improve")
                    .font(theme.font(.caption1))
                    .foregroundStyle(theme.color(.foreground).opacity(0.6))
            }
        }
        .padding(theme.spacing(.spacing5))
        .liquidGlassCard()
        .padding(.horizontal, theme.spacing(.spacing5))
    }
}

