import SwiftUI

struct AthleteDashboardView: View {
    @Environment(\.designTheme) private var theme
    @StateObject private var viewModel = AthleteDashboardViewModel()
    @State private var showingSchoolDetail: School?
    @State private var showingCoachDetail: Coach?

    var body: some View {
        ScrollView {
            VStack(spacing: theme.spacing(.spacing6)) {
                // Dashboard Header
                DashboardHeader()

                // Quick Stats Overview
                QuickStatsCard(viewModel: viewModel)

                // AI Matched Schools Section
                AIMatchedSchoolsSection(
                    matches: viewModel.filteredMatches,
                    selectedFilter: viewModel.selectedMatchFilter,
                    onFilterChange: { status in
                        viewModel.selectedMatchFilter = status
                    },
                    onSchoolTap: { school in
                        showingSchoolDetail = school
                    }
                )

                // Favorite Schools Section
                FavoriteSchoolsSection(
                    schools: viewModel.favoriteSchools,
                    onSchoolTap: { school in
                        showingSchoolDetail = school
                    },
                    onToggleFavorite: { school in
                        viewModel.toggleFavoriteSchool(school)
                    }
                )

                // Saved Coaches Section
                SavedCoachesSection(
                    coaches: viewModel.savedCoaches,
                    onCoachTap: { coach in
                        showingCoachDetail = coach
                    },
                    onToggleSaved: { coach in
                        viewModel.toggleSavedCoach(coach)
                    }
                )

                // Recent Activity Section
                RecentActivitySection(
                    activities: viewModel.recentActivity,
                    onActivityTap: { activity in
                        handleActivityTap(activity)
                    }
                )
            }
            .padding(.bottom, theme.spacing(.spacing8))
        }
        .brandBackground(.adaptive)
        .refreshable {
            await viewModel.refreshData()
        }
        .sheet(item: $showingSchoolDetail) { school in
            SchoolDetailView(school: school)
        }
        .sheet(item: $showingCoachDetail) { coach in
            CoachDetailView(coach: coach)
        }
    }

    private func handleActivityTap(_ activity: ActivityItem) {
        // Handle different activity types
        switch activity.type {
        case .newMatch, .schoolFavorited:
            if let schoolId = activity.relatedEntityId {
                // Navigate to school detail
                print("Navigate to school: \(schoolId)")
            }
        case .coachView, .messageReceived:
            if let coachId = activity.relatedEntityId {
                // Navigate to coach detail or messages
                print("Navigate to coach: \(coachId)")
            }
        case .videoUploaded:
            // Navigate to film/video section
            print("Navigate to videos")
        case .achievementUnlocked:
            // Show achievement detail
            print("Show achievement")
        case .profileUpdate:
            // Navigate to profile
            print("Navigate to profile")
        }
    }
}

struct DashboardHeader: View {
    @Environment(\.designTheme) private var theme

    var body: some View {
        VStack(alignment: .leading, spacing: theme.spacing(.spacing2)) {
            Text("Dashboard")
                .font(theme.font(.largeTitle))
                .fontWeight(.bold)
                .foregroundStyle(theme.color(.foreground))

            Text("Track your recruiting journey")
                .font(theme.font(.subheadline))
                .foregroundStyle(theme.color(.foreground).opacity(0.7))
        }
        .frame(maxWidth: .infinity, alignment: .leading)
        .padding(.horizontal, theme.spacing(.spacing5))
        .padding(.top, theme.spacing(.spacing4))
    }
}

struct QuickStatsCard: View {
    @Environment(\.designTheme) private var theme
    let viewModel: AthleteDashboardViewModel

    var body: some View {
        VStack(spacing: theme.spacing(.spacing4)) {
            HStack(spacing: theme.spacing(.spacing3)) {
                QuickStatItem(
                    icon: "star.fill",
