import SwiftUI

struct FavoriteSchoolsSection: View {
    @Environment(\.designTheme) private var theme
    let schools: [School]
    let onSchoolTap: (School) -> Void
    let onToggleFavorite: (School) -> Void

    var body: some View {
        VStack(alignment: .leading, spacing: theme.spacing(.spacing4)) {
            // Header
            HStack {
                VStack(alignment: .leading, spacing: theme.spacing(.spacing1)) {
                    Text("Favorite Schools")
                        .font(theme.font(.title2))
                        .fontWeight(.bold)
                        .foregroundStyle(theme.color(.foreground))

                    Text("\(schools.count) schools saved")
                        .font(theme.font(.caption1))
                        .foregroundStyle(theme.color(.foreground).opacity(0.6))
                }

                Spacer()

                Button(action: {
                    // Manage favorites action
                }) {
                    HStack(spacing: theme.spacing(.spacing1)) {
                        Text("Manage")
                            .font(theme.font(.subheadline))
                        Image(systemName: "chevron.right")
                            .font(.caption)
                    }
                    .foregroundStyle(theme.color(.primary))
                }
