            DashboardSchoolDetailView(school: school)
        }
        .sheet(item: $showingCoachDetail) { coach in
            DashboardCoachDetailView(coach: coach)
        }
    }

    private func handleActivityTap(_ activity: DashboardActivity) {
        // Handle different activity types
        switch activity.type {
        case .newMatch, .schoolFavorited:
            if let schoolId = activity.relatedEntityId {
                // Navigate to school detail
                print("Navigate to school: \(schoolId)")
            }
        case .coachView, .messageReceived:
            if let coachId = activity.relatedEntityId {
                // Navigate to coach detail or messages
                print("Navigate to coach: \(coachId)")
            }
        case .videoUploaded:
            // Navigate to film/video section
            print("Navigate to videos")
        case .achievementUnlocked:
            // Show achievement detail
            print("Show achievement")
        case .profileUpdate:
            // Navigate to profile
            print("Navigate to profile")
        }
    }
}

