import SwiftUI

struct FavoriteSchoolsSection: View {
    @Environment(\.designTheme) private var theme
    let schools: [School]
    let onSchoolTap: (School) -> Void
    let onToggleFavorite: (School) -> Void

    var body: some View {
        VStack(alignment: .leading, spacing: .spacing4) {
            // Header
            HStack {
                VStack(alignment: .leading, spacing: .spacing1) {
                    Text("Favorite Schools")
                        .font(theme.font(.title2))
                        .fontWeight(.bold)
                        .foregroundStyle(theme.color(.foreground))

                    Text("\(schools.count) schools saved")
                        .font(theme.font(.caption1))
                        .foregroundStyle(theme.color(.foreground).opacity(0.6))
                }

                Spacer()

                Button(action: {
                    // Manage favorites action
                }) {
                    HStack(spacing: .spacing1) {
                        Text("Manage")
                            .font(theme.font(.subheadline))
                        Image(systemName: "chevron.right")
                            .font(.caption)
                    }
                    .foregroundStyle(theme.color(.primary))
                }
            }
            .padding(.horizontal, .spacing5)

            // Favorite Schools Grid
            if schools.isEmpty {
                DashboardEmptyState(
                    icon: "heart",
                    title: "No favorites yet",
                    description: "Tap the heart icon on schools you're interested in"
                )
                .padding(.spacing5)
            } else {
                ScrollView(.horizontal, showsIndicators: false) {
                    HStack(spacing: .spacing3) {
                        ForEach(schools) { school in
                            FavoriteSchoolCard(
                                school: school,
                                onTap: { onSchoolTap(school) },
                                onToggleFavorite: { onToggleFavorite(school) }
                            )
                        }
                    }
                    .padding(.horizontal, .spacing5)
                }
            }
        }
        .padding(.vertical, .spacing4)
    }
}

struct FavoriteSchoolCard: View {
    @Environment(\.designTheme) private var theme
    let school: School
    let onTap: () -> Void
    let onToggleFavorite: () -> Void

    var body: some View {
        Button(action: onTap) {
            VStack(alignment: .leading, spacing: .spacing3) {
                // Header with badge and favorite button
                HStack(alignment: .top) {
                    ZStack {
                        Circle()
                            .fill(theme.color(.primary).opacity(0.1))
                            .frame(width: 56, height: 56)

                        Text(school.initial)
                            .font(theme.font(.title3))
                            .fontWeight(.bold)
                            .foregroundStyle(theme.color(.primary))
                    }

                    Spacer()

                    Button(action: onToggleFavorite) {
                        Image(systemName: "heart.fill")
                            .font(.title3)
                            .foregroundStyle(.pink)
                    }
                    .buttonStyle(.plain)
                }

                // School Info
                VStack(alignment: .leading, spacing: .spacing1) {
                    Text(school.name)
                        .font(theme.font(.headline))
                        .fontWeight(.semibold)
                        .foregroundStyle(theme.color(.foreground))
                        .lineLimit(2)
                        .multilineTextAlignment(.leading)
                        .frame(height: 40, alignment: .top)

                    Text(school.location)
                        .font(theme.font(.caption1))
                        .foregroundStyle(theme.color(.foreground).opacity(0.6))
                        .lineLimit(1)
                }

                Divider()
                    .background(theme.color(.border))

                // Quick Stats
                VStack(spacing: .spacing2) {
                    SchoolStatRow(
                        icon: "trophy.fill",
                        label: "Ranking",
                        value: school.ranking.map { "#\($0)" } ?? "Unranked"
                    )

                    SchoolStatRow(
                        icon: "sportscourt.fill",
                        label: "Division",
                        value: school.division
                    )

                    SchoolStatRow(
                        icon: "building.2.fill",
                        label: "Conference",
                        value: school.conference
                    )
                }
            }
            .padding(.spacing4)
            .frame(width: 240)
            .background(
                RoundedRectangle(cornerRadius: .radiusLarge)
                    .fill(theme.color(.card))
                    .shadowSubtle()
            )
        }
        .buttonStyle(.plain)
    }
}

