                    Text("\(coaches.count) coaches saved")
                        .font(theme.font(.caption1))
                        .foregroundStyle(theme.color(.foreground).opacity(0.6))
                }

                Spacer()

                Button(action: {
                    // Browse coaches action
                }) {
                    HStack(spacing: .spacing1) {
                        Text("Browse")
                            .font(theme.font(.subheadline))
                        Image(systemName: "chevron.right")
                            .font(.caption)
                    }
                    .foregroundStyle(theme.color(.primary))
                }
            }
            .padding(.horizontal, .spacing5)

            // Coaches List
            if coaches.isEmpty {
                DashboardEmptyState(
                    icon: "person.3.fill",
                    title: "No saved coaches",
                    description: "Save coaches you'd like to connect with"
                )
                .padding(.spacing5)
            } else {
                ScrollView(.horizontal, showsIndicators: false) {
                    HStack(spacing: .spacing3) {
                        ForEach(coaches) { coach in
                            SavedCoachCard(
                                coach: coach,
                                onTap: { onCoachTap(coach) },
                                onToggleSaved: { onToggleSaved(coach) }
                            )
                        }
                    }
                    .padding(.horizontal, .spacing5)
                }
            }
        }
        .padding(.vertical, .spacing4)
    }
}

private struct SavedCoachCard: View {
    @Environment(\.designTheme) private var theme
    let coach: Coach
    let onTap: () -> Void
    let onToggleSaved: () -> Void

    var body: some View {
        Button(action: onTap) {
            VStack(alignment: .leading, spacing: .spacing3) {
                // Header with school badge and bookmark
                HStack(alignment: .top) {
                    ZStack {
                        Circle()
                            .fill(theme.color(.accent).opacity(0.1))
                            .frame(width: 40, height: 40)

                        Text(coach.schoolInitial)
                            .font(theme.font(.caption1))
                            .fontWeight(.bold)
                            .foregroundStyle(theme.color(.accent))
                    }

                    Spacer()

                    Button(action: onToggleSaved) {
                        Image(systemName: "bookmark.fill")
                            .font(.title3)
                            .foregroundStyle(theme.color(.primary))
                    }
                    .buttonStyle(.plain)
                }

                // Coach Info
                VStack(alignment: .leading, spacing: .spacing1) {
                    Text(coach.name)
                        .font(theme.font(.headline))
                        .fontWeight(.semibold)
                        .foregroundStyle(theme.color(.foreground))
                        .lineLimit(1)

                    Text(coach.title)
                        .font(theme.font(.caption1))
                        .foregroundStyle(theme.color(.foreground).opacity(0.6))
                        .lineLimit(1)

                    Text(coach.schoolName)
                        .font(theme.font(.caption2))
                        .foregroundStyle(theme.color(.primary))
                        .lineLimit(1)
                }

                Divider()
                    .background(theme.color(.border))

                // Stats
                VStack(alignment: .leading, spacing: .spacing2) {
                    HStack(spacing: .spacing2) {
                        Image(systemName: "clock.fill")
                            .font(.caption2)
                            .foregroundStyle(theme.color(.foreground).opacity(0.5))

                        Text("\(coach.yearsExperience) years")
                            .font(theme.font(.caption1))
                            .foregroundStyle(theme.color(.foreground).opacity(0.7))
                    }

                    if let specialization = coach.specialization {
                        HStack(spacing: .spacing2) {
                            Image(systemName: "star.fill")
                                .font(.caption2)
                                .foregroundStyle(theme.color(.foreground).opacity(0.5))

                            Text(specialization)
                                .font(theme.font(.caption1))
                                .foregroundStyle(theme.color(.foreground).opacity(0.7))
                                .lineLimit(1)
                        }
                    }
                }

                // Achievements
                if let achievements = coach.achievements, !achievements.isEmpty {
                    VStack(alignment: .leading, spacing: .spacing1) {
                        Text("Achievements")
                            .font(theme.font(.caption2))
                            .fontWeight(.semibold)
                            .foregroundStyle(theme.color(.foreground).opacity(0.6))

                        Text(achievements.first ?? "")
                            .font(theme.font(.caption2))
                            .foregroundStyle(theme.color(.foreground).opacity(0.7))
                            .lineLimit(2)
                    }
                }
            }
            .padding(.spacing4)
            .frame(width: 220)
            .background(
                RoundedRectangle(cornerRadius: .radiusLarge)
                    .fill(theme.color(.card))
                    .shadowSubtle()
            )
        }
        .buttonStyle(.plain)
    }
}

