//
//  BrandButton.swift
//  Enhanced button component with icon, loading state, and haptic feedback
//
//  Usage:
//  BrandButton(title: "Sign In", icon: "arrow.right", style: .primary) {
//      // action
//  }
//

import SwiftUI

/// App-specific button wrapper with enhanced features
struct BrandButton: View {
    let title: String
    let icon: String?
    let style: ButtonStyle
    let size: ButtonSize
    let isLoading: Bool
    let action: () -> Void

    enum ButtonStyle {
        case primary
        case secondary
        case destructive
        case outline

        var dsIntent: DSButton.Intent {
            switch self {
            case .primary: return .primary
            case .secondary: return .secondary
            case .destructive: return .destructive
            case .outline: return .secondary
            }
        }
    }

    enum ButtonSize {
        case small
        case medium
        case large

        var dsSize: DSButton.Size {
            switch self {
            case .small: return .s
            case .medium: return .m
            case .large: return .l
            }
        }
    }

    @Environment(\.isEnabled) private var isEnabled
    @Environment(\.designTheme) private var theme

    init(
        title: String,
        icon: String? = nil,
        style: ButtonStyle = .primary,
        size: ButtonSize = .medium,
        isLoading: Bool = false,
        action: @escaping () -> Void
    ) {
        self.title = title
        self.icon = icon
        self.style = style
        self.size = size
        self.isLoading = isLoading
        self.action = action
    }

    var body: some View {
        if style == .outline {
            outlineButton
        } else {
            standardButton
        }
    }

    // MARK: - Standard Button (using DSButton)

    private var standardButton: some View {
        Button(action: {
            HapticManager.shared.medium()
            action()
        }) {
            HStack(spacing: .spacing3) {
                if isLoading {
                    ProgressView()
                        .tint(foregroundColor)
                } else {
                    if let icon = icon {
                        Image(systemName: icon)
                    }
                    Text(title)
                        .font(theme.font(.body))
                }
            }
            .frame(maxWidth: .infinity)
            .padding(.horizontal, horizontalPadding)
            .padding(.vertical, verticalPadding)
            .foregroundStyle(foregroundColor)
            .background(backgroundColor)
            .clipShape(RoundedRectangle(cornerRadius: .radiusLarge, style: .continuous))
            .shadow(color: shadowColor, radius: 8, y: 4)
        }
        .disabled(!isEnabled || isLoading)
        .opacity(isEnabled && !isLoading ? 1.0 : 0.5)
        .accessibilityLabel(title)
    }

    // MARK: - Outline Button

    private var outlineButton: some View {
        Button(action: {
            HapticManager.shared.medium()
            action()
        }) {
            HStack(spacing: .spacing3) {
                if isLoading {
                    ProgressView()
                        .tint(theme.color(.primary))
                } else {
                    if let icon = icon {
                        Image(systemName: icon)
                    }
                    Text(title)
                        .font(theme.font(.body))
                }
            }
            .frame(maxWidth: .infinity)
            .padding(.horizontal, horizontalPadding)
            .padding(.vertical, verticalPadding)
            .foregroundStyle(theme.color(.primary))
            .background(
                RoundedRectangle(cornerRadius: .radiusLarge, style: .continuous)
                    .stroke(theme.color(.primary), lineWidth: 2)
            )
        }
        .disabled(!isEnabled || isLoading)
        .opacity(isEnabled && !isLoading ? 1.0 : 0.5)
        .accessibilityLabel(title)
    }

    // MARK: - Helper Properties

    private var horizontalPadding: CGFloat {
        switch size {
        case .small: return 12
        case .medium: return 16
        case .large: return 20
        }
    }

    private var verticalPadding: CGFloat {
        switch size {
        case .small: return 10
        case .medium: return 14
        case .large: return 18
        }
    }

    private var foregroundColor: Color {
        switch style {
        case .primary:
            return theme.color(.primaryforeground)
        case .secondary:
            return theme.color(.secondaryforeground)
        case .destructive:
            return theme.color(.destructiveforeground)
        case .outline:
            return theme.color(.primary)
        }
    }

    private var backgroundColor: Color {
        switch style {
        case .primary:
            return theme.color(.primary)
        case .secondary:
            return theme.color(.secondary)
        case .destructive:
            return theme.color(.destructive)
        case .outline:
            return Color.clear
        }
    }

    private var shadowColor: Color {
        switch style {
        case .primary:
            return theme.color(.ring).opacity(0.12)
        case .secondary:
            return Color.black.opacity(0.05)
        case .destructive:
            return theme.color(.destructive).opacity(0.12)
        case .outline:
            return Color.clear
        }
    }
}

// MARK: - Preview

#if DEBUG
struct BrandButton_Previews: PreviewProvider {
    static var previews: some View {
        Group {
            // Light mode
            VStack(spacing: .spacing4) {
                BrandButton(title: "Primary Button", style: .primary) {}
                BrandButton(title: "With Icon", icon: "arrow.right", style: .primary) {}
                BrandButton(title: "Secondary", style: .secondary) {}
                BrandButton(title: "Destructive", style: .destructive) {}
                BrandButton(title: "Outline", style: .outline) {}
                BrandButton(title: "Loading", style: .primary, isLoading: true) {}

                HStack(spacing: .spacing3) {
                    BrandButton(title: "Small", style: .primary, size: .small) {}
                    BrandButton(title: "Medium", style: .primary, size: .medium) {}
                    BrandButton(title: "Large", style: .primary, size: .large) {}
                }
            }
            .padding()
            .withDesignTheme()
            .background(DesignTheme.defaultLight.color(.background))
            .previewDisplayName("Light Mode")

            // Dark mode
            VStack(spacing: .spacing4) {
                BrandButton(title: "Primary Button", style: .primary) {}
                BrandButton(title: "With Icon", icon: "arrow.right", style: .primary) {}
                BrandButton(title: "Secondary", style: .secondary) {}
                BrandButton(title: "Destructive", style: .destructive) {}
                BrandButton(title: "Outline", style: .outline) {}
            }
            .padding()
            .withDesignTheme()
            .background(DesignTheme.defaultDark.color(.background))
            .preferredColorScheme(.dark)
            .previewDisplayName("Dark Mode")
        }
    }
}
#endif

//
//  DesignSystem.swift
//  Generated from CSS OKLCH tokens -> sRGB (Light/Dark)
//  Font: San Francisco (system fonts)
//
//  Usage:
//  let theme = DesignTheme.defaultLight
//  Text("Hello").font(theme.font(.body)).foregroundStyle(theme.color(.foreground))
//

import SwiftUI

public enum DSColorToken: String, CaseIterable {
  case background
  case foreground
  case card
  case cardforeground
  case popover
  case popoverforeground
  case primary
  case primaryforeground
  case secondary
  case secondaryforeground
  case muted
  case mutedforeground
  case accent
  case accentforeground
  case destructive
  case destructiveforeground
  case border
  case input
  case ring
  case chart1
  case chart2
  case chart3
  case chart4
  case chart5
  case sidebar
  case sidebarforeground
  case sidebarprimary
  case sidebarprimaryforeground
  case sidebaraccent
  case sidebaraccentforeground
  case sidebarborder
  case sidebarring}

public enum DSTextStyle {
  case largeTitle, title1, title2, title3, headline, subheadline, body, callout, footnote, caption1, caption2
}

public struct DesignTheme {
  public let colors: [DSColorToken: Color]
  public let colorScheme: ColorScheme
  public init(colors: [DSColorToken: Color], colorScheme: ColorScheme) {
    self.colors = colors
    self.colorScheme = colorScheme
  }
  // Color accessor
  public func color(_ token: DSColorToken) -> Color { colors[token] ?? .clear }
  // Font accessor using SF system fonts (Dynamic Type)
  public func font(_ style: DSTextStyle) -> Font {
    switch style {
    case .largeTitle: return .system(.largeTitle, design: .default)
    case .title1:     return .system(.title, design: .default)
    case .title2:     return .system(.title2, design: .default)
    case .title3:     return .system(.title3, design: .default)
    case .headline:   return .system(.headline, design: .default)
    case .subheadline:return .system(.subheadline, design: .default)
    case .body:       return .system(.body, design: .default)
    case .callout:    return .system(.callout, design: .default)
    case .footnote:   return .system(.footnote, design: .default)
    case .caption1:   return .system(.caption, design: .default)
    case .caption2:   return .system(size: 11, weight: .regular, design: .default)
    }
  }
}

extension Color {
  init(hex: String) {
    let hex = hex.trimmingCharacters(in: CharacterSet.alphanumerics.inverted)
    var int: UInt64 = 0
    Scanner(string: hex).scanHexInt64(&int)
    let r = Double((int >> 16) & 0xFF) / 255.0
    let g = Double((int >> 8) & 0xFF) / 255.0
    let b = Double(int & 0xFF) / 255.0
    self.init(red: r, green: g, blue: b)
  }
}

// MARK: - Theme Factories

public extension DesignTheme {
  static let defaultLight = DesignTheme(colors: Self.makeColors(light: true), colorScheme: .light)
  static let defaultDark  = DesignTheme(colors: Self.makeColors(light: false), colorScheme: .dark)

  private static func makeColors(light: Bool) -> [DSColorToken: Color] {
    let map = light ? lightHex : darkHex
    var out: [DSColorToken: Color] = [:]
    for (k, v) in map {
      if let token = DSColorToken(rawValue: k) {
        out[token] = Color(hex: v)
      }
    }
    return out
  }

  // Raw hex dictionaries (generated)
  private static let lightHex: [String:String] = [
  "background": "#F8FAFC",
  "foreground": "#1E293B",
  "card": "#FFFFFF",
  "card-foreground": "#1E293B",
  "popover": "#FFFFFF",
  "popover-foreground": "#1E293B",
  "primary": "#6366F1",
  "primary-foreground": "#FFFFFF",
  "secondary": "#E5E7EB",
  "secondary-foreground": "#374151",
  "muted": "#F3F4F6",
  "muted-foreground": "#6B7280",
  "accent": "#E0E7FF",
  "accent-foreground": "#374151",
  "destructive": "#EF4444",
  "destructive-foreground": "#FFFFFF",
  "border": "#D1D5DB",
  "input": "#D1D5DB",
  "ring": "#6366F1",
  "chart-1": "#6366F1",
  "chart-2": "#4F46E5",
  "chart-3": "#4338CA",
  "chart-4": "#3730A3",
  "chart-5": "#312E81",
  "sidebar": "#F3F4F6",
  "sidebar-foreground": "#1E293B",
  "sidebar-primary": "#6366F1",
  "sidebar-primary-foreground": "#FFFFFF",
  "sidebar-accent": "#E0E7FF",
  "sidebar-accent-foreground": "#374151",
  "sidebar-border": "#D1D5DB",
  "sidebar-ring": "#6366F1"
]
  private static let darkHex: [String:String]  = [
  "background": "#0F172A",
  "foreground": "#E2E8F0",
  "card": "#1E293B",
  "card-foreground": "#E2E8F0",
  "popover": "#1E293B",
  "popover-foreground": "#E2E8F0",
  "primary": "#818CF8",
  "primary-foreground": "#0F172A",
  "secondary": "#2D3748",
  "secondary-foreground": "#D1D5DB",
  "muted": "#152032",
  "muted-foreground": "#9CA3AF",
  "accent": "#374151",
  "accent-foreground": "#D1D5DB",
  "destructive": "#EF4444",
  "destructive-foreground": "#0F172A",
  "border": "#4B5563",
  "input": "#4B5563",
  "ring": "#818CF8",
  "chart-1": "#818CF8",
  "chart-2": "#6366F1",
  "chart-3": "#4F46E5",
  "chart-4": "#4338CA",
  "chart-5": "#3730A3",
  "sidebar": "#1E293B",
  "sidebar-foreground": "#E2E8F0",
  "sidebar-primary": "#818CF8",
  "sidebar-primary-foreground": "#0F172A",
  "sidebar-accent": "#374151",
  "sidebar-accent-foreground": "#D1D5DB",
  "sidebar-border": "#4B5563",
  "sidebar-ring": "#818CF8"
]
}

// MARK: - Example components

public struct DSButton: View {
  public enum Intent { case primary, secondary, destructive }
  public enum Size { case s, m, l }
  public var title: String
  public var intent: Intent = .primary
  public var size: Size = .m
  public var action: () -> Void

  @Environment(\.colorScheme) private var scheme

  public init(_ title: String, intent: Intent = .primary, size: Size = .m, action: @escaping () -> Void) {
    self.title = title
    self.intent = intent
    self.size = size
    self.action = action
  }

  private func theme(for scheme: ColorScheme) -> DesignTheme {
    scheme == .dark ? .defaultDark : .defaultLight
  }

  public var body: some View {
    let theme = theme(for: scheme)
    Button(action: action) {
      Text(title)
        .font(theme.font(.body))
        .foregroundStyle(theme.color(.primaryforeground))
        .padding(.horizontal, padX)
        .padding(.vertical, padY)
        .frame(maxWidth: .infinity)
        .background(background(theme))
        .clipShape(RoundedRectangle(cornerRadius: 16, style: .continuous))
        .shadow(color: theme.color(.ring).opacity(0.12), radius: 8, y: 4)
    }
    .accessibilityLabel(Text(title))
  }

  private var padY: CGFloat { switch size { case .s: 10; case .m: 14; case .l: 18 } }
  private var padX: CGFloat { switch size { case .s: 12; case .m: 16; case .l: 20 } }

  private func background(_ theme: DesignTheme) -> some View {
    switch intent {
      case .primary:    return theme.color(.primary)
      case .secondary:  return theme.color(.secondary)
      case .destructive:return theme.color(.destructive)
    }
  }
}

// MARK: - SwiftUI Preview

#if DEBUG
struct DesignSystem_Previews: PreviewProvider {
  static var previews: some View {
    Group {
      VStack(spacing: 16) {
        Text("Title").font(DesignTheme.defaultLight.font(.title1)).foregroundStyle(DesignTheme.defaultLight.color(.foreground))
        DSButton("Primary"){}
        DSButton("Secondary", intent: .secondary) {}
        DSButton("Delete", intent: .destructive) {}
      }
      .padding()
      .background(DesignTheme.defaultLight.color(.background))

      VStack(spacing: 16) {
        Text("Title").font(DesignTheme.defaultDark.font(.title1)).foregroundStyle(DesignTheme.defaultDark.color(.foreground))
        DSButton("Primary"){}
        DSButton("Secondary", intent: .secondary) {}
        DSButton("Delete", intent: .destructive) {}
      }
      .padding()
      .background(DesignTheme.defaultDark.color(.background))
      .preferredColorScheme(.dark)
    }
  }
}
#endif

