     9→//  .brandBackground(.auth)
    10→//
    11→
    12→import SwiftUI
    13→
    14→/// Background style options
    15→enum BackgroundStyle {
    16→    case auth       // Gradient background for auth flows
    17→    case feed       // Dark gradient for feed views
    18→    case adaptive   // Respects system appearance (solid color)
    19→}
    20→
    21→/// Unified background component for the app
    22→struct BrandBackground: View {
    23→    let style: BackgroundStyle
    24→
    25→    @Environment(\.designTheme) private var theme
    26→
    27→    var body: some View {
    28→        Group {
    29→            switch style {
    30→            case .auth:
    31→                LinearGradient.brandGradient
    32→            case .feed:
    33→                LinearGradient.feedBackground
    34→            case .adaptive:
    35→                theme.color(.background)
    36→            }
    37→        }
    38→        .ignoresSafeArea()
    39→    }
    40→}
    41→
