//
//  BrandBackground.swift
//  Unified background component for consistent app styling
//
//  Usage:
//  VStack {
//      // content
//  }
//  .brandBackground(.auth)
//

import SwiftUI

/// Background style options
enum BackgroundStyle {
    case auth       // Gradient background for auth flows
    case feed       // Dark gradient for feed views
    case adaptive   // Respects system appearance (solid color)
}

/// Unified background component for the app
struct BrandBackground: View {
    let style: BackgroundStyle

    @Environment(\.designTheme) private var theme

    var body: some View {
        Group {
            switch style {
            case .auth:
                LinearGradient.brandGradient
            case .feed:
                LinearGradient.feedBackground
            case .adaptive:
                theme.color(.background)
            }
        }
        .ignoresSafeArea()
    }
}

// MARK: - ViewModifier Version

struct BrandBackgroundModifier: ViewModifier {
    let style: BackgroundStyle

    func body(content: Content) -> some View {
        ZStack {
            BrandBackground(style: style)
            content
        }
    }
}

extension View {
    /// Applies brand background to the view
    /// - Parameter style: Background style to use (default: .adaptive)
    /// - Returns: View with background applied
    func brandBackground(_ style: BackgroundStyle = .adaptive) -> some View {
        modifier(BrandBackgroundModifier(style: style))
    }
}

// MARK: - Preview

#if DEBUG
struct BrandBackground_Previews: PreviewProvider {
    static var previews: some View {
        Group {
            // Auth background
            VStack {
                Text("Auth Background")
                    .font(.largeTitle)
                    .fontWeight(.bold)
                    .foregroundColor(.white)
            }
            .brandBackground(.auth)
            .previewDisplayName("Auth Background")

            // Feed background
            VStack {
                Text("Feed Background")
                    .font(.largeTitle)
                    .fontWeight(.bold)
                    .foregroundColor(.white)
            }
            .brandBackground(.feed)
            .previewDisplayName("Feed Background")

            // Adaptive background (light mode)
            VStack {
                Text("Adaptive Background")
                    .font(.largeTitle)
                    .fontWeight(.bold)
            }
            .withDesignTheme()
            .brandBackground(.adaptive)
            .previewDisplayName("Adaptive - Light")

            // Adaptive background (dark mode)
            VStack {
                Text("Adaptive Background")
                    .font(.largeTitle)
                    .fontWeight(.bold)
            }
            .withDesignTheme()
            .brandBackground(.adaptive)
            .preferredColorScheme(.dark)
            .previewDisplayName("Adaptive - Dark")
        }
    }
}
#endif

//
//  DesignTokens+Extensions.swift
//  Extensions for DesignSystem providing spacing, radius, gradients, and environment access
//
//  Usage:
//  .padding(.spacing4)
//  .cornerRadius(.radiusLarge)
//  LinearGradient.athleteGradient
//  @Environment(\.designTheme) var theme
//

import SwiftUI

// MARK: - Spacing Extensions (4pt grid)
extension CGFloat {
    /// 4pt - Extra Small spacing
    static let spacing1: CGFloat = 4
    /// 8pt - Small spacing
    static let spacing2: CGFloat = 8
    /// 12pt - Medium spacing
    static let spacing3: CGFloat = 12
    /// 16pt - Large spacing (default)
    static let spacing4: CGFloat = 16
    /// 20pt - Extra Large spacing
    static let spacing5: CGFloat = 20
    /// 24pt - 2X Large spacing
    static let spacing6: CGFloat = 24
    /// 32pt - 3X Large spacing
    static let spacing8: CGFloat = 32
    /// 40pt - 4X Large spacing
    static let spacing10: CGFloat = 40
}

// MARK: - Corner Radius
extension CGFloat {
    /// 8pt - Small corner radius
    static let radiusSmall: CGFloat = 8
    /// 12pt - Medium corner radius
    static let radiusMedium: CGFloat = 12
    /// 16pt - Large corner radius (default)
    static let radiusLarge: CGFloat = 16
    /// 20pt - Extra Large corner radius
    static let radiusXLarge: CGFloat = 20
}

// MARK: - Shadow & Elevation
extension View {
    /// Applies a subtle shadow (elevation 1)
    /// Use for slightly raised elements like buttons or small cards
    func shadowSubtle() -> some View {
        self.shadow(color: Color.black.opacity(0.05), radius: 2, x: 0, y: 1)
    }

    /// Applies a medium shadow (elevation 2)
    /// Use for cards, modals, and raised content
    func shadowMedium() -> some View {
        self.shadow(color: Color.black.opacity(0.08), radius: 8, x: 0, y: 4)
    }

    /// Applies a large shadow (elevation 3)
    /// Use for floating action buttons, popovers, and prominent elements
    func shadowLarge() -> some View {
        self.shadow(color: Color.black.opacity(0.12), radius: 16, x: 0, y: 8)
    }

    /// Applies an extra large shadow (elevation 4)
    /// Use for modals, sheets, and elements that should appear above all content
    func shadowXLarge() -> some View {
        self.shadow(color: Color.black.opacity(0.16), radius: 24, x: 0, y: 12)
    }
}

// MARK: - Gradients for Sports App
extension LinearGradient {
    /// Athlete gradient (indigo/purple)
    static let athleteGradient = LinearGradient(
        colors: [Color(hex: "#6366F1"), Color(hex: "#818CF8")],
        startPoint: .topLeading,
        endPoint: .bottomTrailing
    )

    /// Coach gradient (red/pink)
    static let coachGradient = LinearGradient(
        colors: [Color(hex: "#EF4444"), Color(hex: "#F87171")],
        startPoint: .topLeading,
        endPoint: .bottomTrailing
    )

    /// Default brand gradient (uses athlete gradient)
    static let brandGradient = athleteGradient

    /// Feed background gradient (dark variant)
    static let feedBackground = LinearGradient(
        colors: [
            Color(hex: "#1E293B"),
            Color(hex: "#0F172A")
        ],
        startPoint: .top,
        endPoint: .bottom
    )
}

// MARK: - Environment Key for Theme
struct DesignThemeKey: EnvironmentKey {
    static let defaultValue: DesignTheme = .defaultLight
}

extension EnvironmentValues {
    var designTheme: DesignTheme {
        get { self[DesignThemeKey.self] }
        set { self[DesignThemeKey.self] = newValue }
    }
}

// MARK: - View Extension for Easy Theme Access
extension View {
    /// Injects the appropriate design theme based on current color scheme
    func withDesignTheme() -> some View {
        self.modifier(DesignThemeModifier())
    }
}

private struct DesignThemeModifier: ViewModifier {
    @Environment(\.colorScheme) private var colorScheme

    func body(content: Content) -> some View {
        content
            .environment(\.designTheme, colorScheme == .dark ? .defaultDark : .defaultLight)
    }
}

// MARK: - Semantic Font Extensions
extension Font {
    /// Section title font (title2 with semibold weight)
    static let sectionTitle: Font = .system(.title2, design: .default, weight: .semibold)

    /// Body text font (standard body text)
    static let bodyText: Font = .system(.body, design: .default)
}

