import SwiftUI

struct AthletePhysicalStatsView: View {
    @Environment(\.dismiss) private var dismiss

    // User data
    let userName: String
    let sport: String

    // Form fields
    @State private var height: String = "6' 4\""
    @State private var isRightHanded: Bool = true
    @State private var approachTouch: String = "10' 2\""
    @State private var blockJump: String = "9' 10\""
    @State private var verticalJump: String = "24\""

    @State private var isLoading = false
    @State private var navigateToContactInfo = false

    // Height options
    private let heightOptions = [
        "4' 0\"", "4' 1\"", "4' 2\"", "4' 3\"", "4' 4\"", "4' 5\"", "4' 6\"", "4' 7\"", "4' 8\"", "4' 9\"", "4' 10\"", "4' 11\"",
        "5' 0\"", "5' 1\"", "5' 2\"", "5' 3\"", "5' 4\"", "5' 5\"", "5' 6\"", "5' 7\"", "5' 8\"", "5' 9\"", "5' 10\"", "5' 11\"",
        "6' 0\"", "6' 1\"", "6' 2\"", "6' 3\"", "6' 4\"", "6' 5\"", "6' 6\"", "6' 7\"", "6' 8\"", "6' 9\"", "6' 10\"", "6' 11\"",
        "7' 0\"", "7' 1\"", "7' 2\"", "7' 3\"", "7' 4\""
    ]

    // Touch height options (for volleyball)
    private let touchHeightOptions = [
        "7' 0\"", "7' 6\"", "8' 0\"", "8' 6\"", "9' 0\"", "9' 2\"", "9' 4\"", "9' 6\"", "9' 8\"", "9' 10\"",
        "10' 0\"", "10' 2\"", "10' 4\"", "10' 6\"", "10' 8\"", "10' 10\"",
        "11' 0\"", "11' 2\"", "11' 4\"", "11' 6\"", "11' 8\"", "11' 10\"",
        "12' 0\""
    ]

    // Vertical jump options (inches)
    private let verticalJumpOptions = Array(12...48).map { "\($0)\"" }

    var body: some View {
        ZStack {
            // Background
            Color(.systemBackground)
                .ignoresSafeArea()

            VStack(spacing: 0) {
                // Header
                VStack(spacing: 16) {
                    Text("Athlete Sign Up 2")
                        .font(.caption)
                        .foregroundColor(.gray)
                        .frame(maxWidth: .infinity, alignment: .leading)
                        .padding(.horizontal, 24)
                        .padding(.top, 20)

                    Image(systemName: "target")
                        .font(.system(size: 60))
                        .foregroundStyle(
                            LinearGradient(
                                colors: [.blue, .purple],
                                startPoint: .topLeading,
                                endPoint: .bottomTrailing
                            )
                        )

                    Text("ATHLEAD")
                        .font(.system(size: 32, weight: .bold))
                        .foregroundColor(.blue)
                }
                .padding(.bottom, 32)

                // Form
                ScrollView {
                    VStack(spacing: 20) {
                        // Height
                        DropdownField(
                            label: "Height",
                            selectedValue: $height,
                            options: heightOptions
                        )

                        // Handedness
                        VStack(alignment: .leading, spacing: 12) {
                            HStack(spacing: 16) {
                                Button {
                                    isRightHanded = true
                                } label: {
                                    HStack(spacing: 8) {
                                        Image(systemName: isRightHanded ? "checkmark.square.fill" : "square")
                                            .foregroundColor(isRightHanded ? .blue : .gray)
                                        Text("Right Handed")
                                            .foregroundColor(.primary)
                                    }
                                }

                                Button {
                                    isRightHanded = false
                                } label: {
                                    HStack(spacing: 8) {
                                        Image(systemName: !isRightHanded ? "checkmark.square.fill" : "square")
                                            .foregroundColor(!isRightHanded ? .blue : .gray)
                                        Text("Left Handed")
                                            .foregroundColor(.primary)
                                    }
                                }
                            }
                            .padding(.horizontal, 24)
                        }

                        // Approach Touch
                        DropdownField(
                            label: "Approach Touch",
                            selectedValue: $approachTouch,
                            options: touchHeightOptions
                        )

                        // Block Jump
                        DropdownField(
                            label: "Block Jump",
                            selectedValue: $blockJump,
                            options: touchHeightOptions
                        )

                        // Vertical Jump
                        DropdownField(
                            label: "Vertical Jump",
                            selectedValue: $verticalJump,
                            options: verticalJumpOptions
                        )
                    }
                    .padding(.bottom, 40)
                }

                Spacer()

                // Progress indicator
                VStack(spacing: 12) {
                    HStack {
                        Spacer()
                        Text("50%")
                            .font(.caption)
                            .foregroundColor(.gray)
                            .padding(.trailing, 24)
                    }

                    GeometryReader { geometry in
                        ZStack(alignment: .leading) {
                            Rectangle()
                                .fill(Color.gray.opacity(0.2))
                                .frame(height: 4)

                            Rectangle()
                                .fill(Color.blue)
                                .frame(width: geometry.size.width * 0.5, height: 4)
                        }
                    }
                    .frame(height: 4)
                    .padding(.horizontal, 24)
                }
                .padding(.bottom, 40)
            }

        }
        .safeAreaInset(edge: .bottom) {
            // Navigation buttons
            HStack {
                // Back button
                Button {
                    dismiss()
                } label: {
                    Image(systemName: "arrow.left")
                        .foregroundColor(.gray)
                        .font(.system(size: 20))
                        .padding()
                }
                .padding(.leading, 16)

                Spacer()

                // Continue button
                Button {
                    navigateToContactInfo = true
                } label: {
                    Image(systemName: "arrow.right")
                        .foregroundColor(.gray)
                        .font(.system(size: 20))
                        .padding()
                }
                .padding(.trailing, 16)
            }
            .background(Color(.systemBackground))
        }
        .navigationBarBackButtonHidden(true)
        .navigationDestination(isPresented: $navigateToContactInfo) {
            AthleteContactInfoView(userName: userName, sport: sport)
        }
    }
}

// MARK: - Dropdown Field Component
struct DropdownField: View {
    let label: String
    @Binding var selectedValue: String
    let options: [String]

    var body: some View {
        VStack(alignment: .leading, spacing: 8) {
            Text(label)
                .font(.subheadline)
                .fontWeight(.medium)
                .foregroundColor(.primary)
                .padding(.horizontal, 24)

            Menu {
                ForEach(options, id: \.self) { option in
                    Button {
                        selectedValue = option
                    } label: {
                        HStack {
                            Text(option)
                            Spacer()
                            if selectedValue == option {
                                Image(systemName: "checkmark")
                            }
                        }
                    }
                }
            } label: {
                HStack {
                    Text(selectedValue)
                        .foregroundColor(.primary)
                    Spacer()
                    Image(systemName: "chevron.down")
                        .foregroundColor(.gray)
                        .font(.caption)
                }
                .padding()
                .background(Color(.systemGray6))
                .cornerRadius(8)
            }
            .padding(.horizontal, 24)
        }
    }
}

