                Image("athlead_light")
                    .resizable()
                    .scaledToFit()
                    .frame(maxWidth: 200)
                    .accessibilityLabel("AthleadAI logo")
            }
            .padding(.bottom, 32)

            // Form
            ScrollView {
                VStack(spacing: 20) {
                    // Height
                    DropdownField(
                            label: "Height",
                            selectedValue: $height,
                            options: heightOptions
                        )

                        // Handedness
                        VStack(alignment: .leading, spacing: 12) {
                            HStack(spacing: 16) {
                                Button {
                                    isRightHanded = true
                                } label: {
                                    HStack(spacing: 8) {
                                        Image(systemName: isRightHanded ? "checkmark.square.fill" : "square")
                                            .foregroundColor(isRightHanded ? .blue : .gray)
                                        Text("Right Handed")
                                            .foregroundColor(.primary)
                                    }
                                }

                                Button {
                                    isRightHanded = false
                                } label: {
                                    HStack(spacing: 8) {
                                        Image(systemName: !isRightHanded ? "checkmark.square.fill" : "square")
                                            .foregroundColor(!isRightHanded ? .blue : .gray)
                                        Text("Left Handed")
                                            .foregroundColor(.primary)
                                    }
                                }
                            }
                            .padding(.horizontal, 24)
                        }

                        // Approach Touch
                        DropdownField(
                            label: "Approach Touch",
                            selectedValue: $approachTouch,
                            options: touchHeightOptions
                        )

                        // Block Jump
                        DropdownField(
                            label: "Block Jump",
                            selectedValue: $blockJump,
                            options: touchHeightOptions
                        )

                        // Vertical Jump
