            // Progress indicator
            VStack(spacing: 12) {
                HStack {
                    Spacer()
                    Text("50%")
                        .font(.caption)
                        .foregroundColor(.gray)
                        .padding(.trailing, 24)
                }

                GeometryReader { geometry in
                    ZStack(alignment: .leading) {
                        Rectangle()
                            .fill(Color.gray.opacity(0.2))
                            .frame(height: 4)

                        Rectangle()
                            .fill(Color.blue)
                            .frame(width: geometry.size.width * 0.5, height: 4)
                    }
                }
                .frame(height: 4)
                .padding(.horizontal, 24)
            }
            .padding(.bottom, 40)
        }
        .safeAreaInset(edge: .bottom) {
            // Navigation buttons
            HStack {
                // Back button
                Button {
                    dismiss()
                } label: {
                    Image(systemName: "arrow.left")
                        .foregroundColor(.gray)
