//
//  CoachProfileSetupView.swift
//  AthleadAI
//
//  Club/High School Coach profile setup screen - collects basic info during onboarding
//

import SwiftUI
import Clerk

struct CoachProfileSetupView: View {
    @Environment(\.clerk) private var clerk
    @Environment(\.dismiss) private var dismiss

    // Auth method determines which fields to show
    let authMethod: SignUpAuthView.AuthMethod

    // Form fields
    @State private var firstName: String = ""
    @State private var lastName: String = ""
    @State private var email: String = ""
    @State private var password: String = ""
    @State private var confirmPassword: String = ""
    @State private var phoneNumber: String = ""
    @State private var athleteEmail: String = ""

    // UI state
    @State private var isLoading = false
    @State private var errorMessage: String?
    @State private var showPassword = false
    @State private var showConfirmPassword = false
    @State private var navigateToDashboard = false
    @State private var isAuthenticating = false
    @State private var showConnectionSuccess = false

    // Determine if user signed up with email or OAuth based on auth method
    private var usedEmailSignUp: Bool {
        return authMethod == .email
    }

    var body: some View {
        ZStack {
            // Background
            Color(.systemBackground)
                .ignoresSafeArea()

            ScrollView {
                VStack(spacing: 24) {
                    // Header
                    VStack(spacing: 12) {
                        Image(systemName: "target")
                            .font(.system(size: 60))
                            .foregroundStyle(
                                LinearGradient(
                                    colors: [.blue, .purple],
                                    startPoint: .topLeading,
                                    endPoint: .bottomTrailing
                                )
                            )

                        Text("ATHLEAD")
                            .font(.system(size: 32, weight: .bold))
                            .foregroundColor(.blue)

                        Text("Coach Sign Up")
                            .font(.title3)
                            .fontWeight(.semibold)
                    }
                    .padding(.top, 40)
                    .padding(.bottom, 8)

                    // Form
                    VStack(spacing: 16) {
                        // First Name
                        FormField(
                            label: "First Name",
                            icon: "person",
                            placeholder: "First Name",
                            text: $firstName
                        )

                        // Last Name
                        FormField(
                            label: "Last Name",
                            icon: "person",
                            placeholder: "Last Name",
                            text: $lastName
                        )

                        // Email (only shown for email sign-up)
                        if usedEmailSignUp {
                            FormField(
                                label: "Email",
                                icon: "envelope",
                                placeholder: "Email",
                                text: $email,
                                keyboardType: .emailAddress,
                                autocapitalization: .never
                            )

                            // Password
                            VStack(alignment: .leading, spacing: 8) {
                                Text("Password")
                                    .font(.subheadline)
                                    .fontWeight(.medium)
                                    .foregroundColor(.primary)

                                HStack {
                                    Image(systemName: "lock")
                                        .foregroundColor(.gray)
                                        .frame(width: 20)

                                    if showPassword {
                                        TextField("Password", text: $password)
                                            .autocapitalization(.none)
                                    } else {
                                        SecureField("Password", text: $password)
                                            .autocapitalization(.none)
                                    }

                                    Button {
                                        showPassword.toggle()
                                    } label: {
                                        Image(systemName: showPassword ? "eye.slash" : "eye")
                                            .foregroundColor(.gray)
                                    }

                                    if !password.isEmpty {
                                        Button {
                                            password = ""
                                        } label: {
                                            Image(systemName: "xmark.circle.fill")
                                                .foregroundColor(.gray)
                                        }
                                    }
                                }
                                .padding()
                                .background(Color(.systemGray6))
                                .cornerRadius(8)
                            }

                            // Re-enter Password
                            VStack(alignment: .leading, spacing: 8) {
                                Text("Re-enter Password")
                                    .font(.subheadline)
                                    .fontWeight(.medium)
                                    .foregroundColor(.primary)

                                HStack {
                                    Image(systemName: "lock")
                                        .foregroundColor(.gray)
                                        .frame(width: 20)

                                    if showConfirmPassword {
                                        TextField("Re-enter Password", text: $confirmPassword)
                                            .autocapitalization(.none)
                                    } else {
                                        SecureField("Re-enter Password", text: $confirmPassword)
                                            .autocapitalization(.none)
                                    }

                                    Button {
                                        showConfirmPassword.toggle()
                                    } label: {
                                        Image(systemName: showConfirmPassword ? "eye.slash" : "eye")
                                            .foregroundColor(.gray)
                                    }

                                    if !confirmPassword.isEmpty {
                                        Button {
                                            confirmPassword = ""
                                        } label: {
                                            Image(systemName: "xmark.circle.fill")
                                                .foregroundColor(.gray)
                                        }
                                    }
                                }
                                .padding()
                                .background(Color(.systemGray6))
                                .cornerRadius(8)
                            }
                        }

                        // Phone Number (optional)
                        FormField(
                            label: "Phone Number (Optional)",
                            icon: "phone",
                            placeholder: "(555) 123-4567",
                            text: $phoneNumber,
                            keyboardType: .phonePad,
                            autocapitalization: .never
                        )

                        // Divider
                        Divider()
                            .padding(.vertical, 8)

                        // Athlete Connection Section
                        VStack(alignment: .leading, spacing: 12) {
                            Text("Connect to Athlete")
                                .font(.headline)
                                .fontWeight(.semibold)

                            Text("Enter your athlete's email to connect your accounts")
                                .font(.caption)
                                .foregroundColor(.secondary)

                            FormField(
                                label: "Athlete Email",
                                icon: "person.crop.circle.badge.plus",
                                placeholder: "athlete@example.com",
                                text: $athleteEmail,
                                keyboardType: .emailAddress,
                                autocapitalization: .never
                            )

                            // Info callout
                            HStack(alignment: .top, spacing: 8) {
                                Image(systemName: "info.circle.fill")
                                    .foregroundColor(.blue)
                                    .font(.caption)

                                VStack(alignment: .leading, spacing: 4) {
                                    Text("How it works:")
                                        .font(.caption)
                                        .fontWeight(.semibold)

                                    Text("• If the athlete already has an account, they'll receive an authorization email")
                                        .font(.caption2)
                                        .foregroundColor(.secondary)

                                    Text("• If the athlete doesn't have an account, they'll receive an invitation to sign up")
                                        .font(.caption2)
                                        .foregroundColor(.secondary)
                                }
                            }
                            .padding(12)
                            .background(Color.blue.opacity(0.1))
                            .cornerRadius(8)
                        }
                    }
                    .padding(.horizontal, 24)

                    // Success message
                    if showConnectionSuccess {
                        HStack {
                            Image(systemName: "checkmark.circle.fill")
                            Text("Connection request sent!")
                                .font(.footnote)
                        }
                        .foregroundColor(.green)
                        .padding()
                        .background(Color.green.opacity(0.1))
                        .cornerRadius(10)
                        .padding(.horizontal, 24)
                        .transition(.scale.combined(with: .opacity))
                    }

                    // Error message
                    if let errorMessage = errorMessage {
                        HStack {
                            Image(systemName: "exclamationmark.triangle.fill")
                            Text(errorMessage)
                                .font(.footnote)
                        }
                        .foregroundColor(.red)
                        .padding()
                        .background(Color.red.opacity(0.1))
                        .cornerRadius(10)
                        .padding(.horizontal, 24)
                        .transition(.scale.combined(with: .opacity))
                    }

                    // Submit Button
                    Button {
                        submitForm()
                    } label: {
                        HStack {
                            if isLoading {
                                ProgressView()
                                    .progressViewStyle(CircularProgressViewStyle(tint: .white))
                                    .scaleEffect(0.9)
                            } else {
                                Text("Complete Sign Up")
                                    .font(.headline)
                                    .fontWeight(.semibold)
                            }
                        }
                        .frame(maxWidth: .infinity)
                        .padding()
                        .background(
                            isFormValid ? Color.blue : Color.gray.opacity(0.5)
                        )
                        .foregroundStyle(.white)
                        .cornerRadius(8)
                    }
                    .disabled(!isFormValid || isLoading)
                    .padding(.horizontal, 24)
                    .padding(.top, 8)

                    Spacer()
                }
                .padding(.bottom, 40)
            }
        }
        .navigationDestination(isPresented: $navigateToDashboard) {
            // TODO: Navigate to coach dashboard
            Text("Coach Dashboard Coming Soon!")
                .font(.title2)
                .padding()
        }
        .onAppear {
            // For OAuth users, trigger authentication immediately
            if authMethod != .email && !isAuthenticating {
                isAuthenticating = true
                authenticateWithOAuth()
            }

            // Pre-populate fields from Clerk user if available
            if let user = clerk.user {
                if firstName.isEmpty, let clerkFirstName = user.firstName {
                    firstName = clerkFirstName
                }
                if lastName.isEmpty, let clerkLastName = user.lastName {
                    lastName = clerkLastName
                }
                if email.isEmpty, let primaryEmail = user.emailAddresses.first?.emailAddress {
                    email = primaryEmail
                }
            }
        }
    }

    private func authenticateWithOAuth() {
        // TODO: Implement OAuth authentication with Clerk
        // For now, just show a message that OAuth is not yet configured
        errorMessage = "OAuth authentication not yet configured. Please use email sign-up."
        isAuthenticating = false
    }

    private var isFormValid: Bool {
        // Basic validation
        let basicFieldsValid = !firstName.isEmpty && !lastName.isEmpty

        // Athlete email validation
        let athleteEmailValid = !athleteEmail.isEmpty && athleteEmail.contains("@")

        if usedEmailSignUp {
            // Email sign-up: validate email and password
            let emailValid = !email.isEmpty && email.contains("@")
            let passwordValid = !password.isEmpty && password.count >= 8
            let passwordsMatch = password == confirmPassword

            return basicFieldsValid && emailValid && passwordValid && passwordsMatch && athleteEmailValid
        } else {
            // OAuth sign-up: only basic fields needed
            return basicFieldsValid && athleteEmailValid
        }
    }

    private func submitForm() {
        errorMessage = nil
        showConnectionSuccess = false
        isLoading = true

        // Validate password match for email sign-up
        if usedEmailSignUp && password != confirmPassword {
            errorMessage = "Passwords do not match"
            isLoading = false
            return
        }

        Task {
            do {
                if usedEmailSignUp {
                    // Create Clerk account programmatically (no UI)
                    try await createClerkAccount()
                } else {
                    // For OAuth, authentication should have already happened
                    // Just verify user is authenticated
                    guard clerk.user != nil else {
                        await MainActor.run {
                            errorMessage = "Authentication required"
                            isLoading = false
                        }
                        return
                    }
                }

                // Send athlete connection request
                try await sendAthleteConnectionRequest()

                await MainActor.run {
                    showConnectionSuccess = true
                    isLoading = false

                    // Navigate to dashboard after brief delay
                    DispatchQueue.main.asyncAfter(deadline: .now() + 1.5) {
                        navigateToDashboard = true
                    }
                }
            } catch {
                await MainActor.run {
                    errorMessage = error.localizedDescription
                    isLoading = false
                }
            }
        }
    }

    private func createClerkAccount() async throws {
        // Use Clerk API to create account programmatically
        // This is a placeholder - the actual Clerk iOS SDK might require different approach
        // For now, we'll skip authentication and just proceed
        // In production, you'd integrate with your backend API that handles Clerk
        try await Task.sleep(nanoseconds: 500_000_000) // 0.5 second delay to simulate API call

        // TODO: Implement actual Clerk API integration
        // This might involve calling your backend API which then creates the Clerk user
        // Example:
        // let response = await APIClient.shared.createUser(
