//
//  MainTabRouter.swift
//  AthleadAI
//
//  Routes authenticated users to their appropriate tab navigation based on user type
//

import SwiftUI
import Clerk

// MARK: - Main Tab Router
/// Routes users to the appropriate tab view based on their user type stored in Clerk metadata
struct MainTabRouter: View {
    @Environment(\.clerk) private var clerk

    var body: some View {
        Group {
            // Determine which tab view to show based on user type
            switch userType {
            case .athlete:
                AthleteTabView()
            case .parent:
                ParentTabView()
            case .coach:
                CollegeCoachTabView()
            case .clubCoach:
                ClubCoachTabView()
            case .unknown:
                // Default to athlete view if user type is not set or unknown
                // This provides a graceful fallback for edge cases
                AthleteTabView()
            }
        }
    }

    /// Computed property to safely extract user type from Clerk public metadata
    /// Returns UserType enum value or .unknown if not found/invalid
    private var userType: UserType {
        // Access the user's public metadata
        guard let metadata = clerk.user?.publicMetadata else {
            return .unknown
        }

        // Extract userType string from metadata
        guard let userTypeString = metadata["userType"] as? String else {
            return .unknown
        }

        // Match the string to our UserType enum
        // We use rawValue matching to safely convert the string
        switch userTypeString {
        case UserType.athlete.rawValue:
            return .athlete
        case UserType.parent.rawValue:
            return .parent
        case UserType.coach.rawValue:
            return .coach
        case UserType.clubCoach.rawValue:
            return .clubCoach
        default:
            return .unknown
        }
    }
}

// MARK: - Extended UserType Enum
/// Extension to add unknown case for routing fallback
extension UserType {
    static let unknown = UserType.athlete // Default fallback to athlete
}

#Preview("Athlete User") {
    MainTabRouter()
        .environment(\.clerk, Clerk.shared)
}

