//
//  DesignTokens+Extensions.swift
//  Extensions for DesignSystem providing spacing, radius, gradients, and environment access
//
//  Usage:
//  .padding(.spacing4)
//  .cornerRadius(.radiusLarge)
//  LinearGradient.athleteGradient
//  @Environment(\.designTheme) var theme
//

import SwiftUI

// MARK: - Spacing Extensions (4pt grid)
extension CGFloat {
    /// 4pt - Extra Small spacing
    static let spacing1: CGFloat = 4
    /// 8pt - Small spacing
    static let spacing2: CGFloat = 8
    /// 12pt - Medium spacing
    static let spacing3: CGFloat = 12
    /// 16pt - Large spacing (default)
    static let spacing4: CGFloat = 16
    /// 20pt - Extra Large spacing
    static let spacing5: CGFloat = 20
    /// 24pt - 2X Large spacing
    static let spacing6: CGFloat = 24
    /// 32pt - 3X Large spacing
    static let spacing8: CGFloat = 32
    /// 40pt - 4X Large spacing
    static let spacing10: CGFloat = 40
}

// MARK: - Corner Radius
extension CGFloat {
    /// 8pt - Small corner radius
    static let radiusSmall: CGFloat = 8
    /// 12pt - Medium corner radius
    static let radiusMedium: CGFloat = 12
    /// 16pt - Large corner radius (default)
    static let radiusLarge: CGFloat = 16
    /// 20pt - Extra Large corner radius
    static let radiusXLarge: CGFloat = 20
}

// MARK: - Gradients for Sports App
extension LinearGradient {
    /// Athlete gradient (indigo/purple)
    static let athleteGradient = LinearGradient(
        colors: [Color(hex: "#6366F1"), Color(hex: "#818CF8")],
        startPoint: .topLeading,
        endPoint: .bottomTrailing
    )

    /// Coach gradient (red/pink)
    static let coachGradient = LinearGradient(
        colors: [Color(hex: "#EF4444"), Color(hex: "#F87171")],
        startPoint: .topLeading,
        endPoint: .bottomTrailing
    )

    /// Default brand gradient (uses athlete gradient)
    static let brandGradient = athleteGradient

    /// Feed background gradient (dark variant)
    static let feedBackground = LinearGradient(
        colors: [
            Color(hex: "#1E293B"),
            Color(hex: "#0F172A")
        ],
        startPoint: .top,
        endPoint: .bottom
    )
}

// MARK: - Helper for hex colors (make public for extension use)
extension Color {
    init(hex: String) {
        let hex = hex.trimmingCharacters(in: CharacterSet.alphanumerics.inverted)
        var int: UInt64 = 0
        Scanner(string: hex).scanHexInt64(&int)
        let r = Double((int >> 16) & 0xFF) / 255.0
        let g = Double((int >> 8) & 0xFF) / 255.0
        let b = Double(int & 0xFF) / 255.0
        self.init(red: r, green: g, blue: b)
    }
}

// MARK: - Environment Key for Theme
struct DesignThemeKey: EnvironmentKey {
    static let defaultValue: DesignTheme = .defaultLight
}

extension EnvironmentValues {
    var designTheme: DesignTheme {
        get { self[DesignThemeKey.self] }
        set { self[DesignThemeKey.self] = newValue }
    }
}

// MARK: - View Extension for Easy Theme Access
extension View {
    /// Injects the appropriate design theme based on current color scheme
    func withDesignTheme() -> some View {
        self.modifier(DesignThemeModifier())
    }
}

private struct DesignThemeModifier: ViewModifier {
    @Environment(\.colorScheme) private var colorScheme

    func body(content: Content) -> some View {
        content
            .environment(\.designTheme, colorScheme == .dark ? .defaultDark : .defaultLight)
    }
}

