//
//  GlassCard.swift
//  A glass-morphism card component with blur effect and border
//
//  Usage:
//  GlassCard {
//      Text("Hello")
//  }
//
//  Or as modifier:
//  VStack { Text("Hello") }
//      .glassCard()
//

import SwiftUI

/// A glass-morphism card with blur effect and border
/// Uses DesignTheme colors for consistent styling
struct GlassCard<Content: View>: View {
    let content: Content
    var cornerRadius: CGFloat = .radiusLarge
    var padding: CGFloat = .spacing4

    @Environment(\.designTheme) private var theme
    @Environment(\.colorScheme) private var colorScheme

    init(
        cornerRadius: CGFloat = .radiusLarge,
        padding: CGFloat = .spacing4,
        @ViewBuilder content: () -> Content
    ) {
        self.cornerRadius = cornerRadius
        self.padding = padding
        self.content = content()
    }

    var body: some View {
        content
            .padding(padding)
            .background(
                RoundedRectangle(cornerRadius: cornerRadius, style: .continuous)
                    .fill(theme.color(.card))
                    .overlay(
                        RoundedRectangle(cornerRadius: cornerRadius, style: .continuous)
                            .stroke(theme.color(.border).opacity(0.3), lineWidth: 1)
                    )
            )
    }
}

// MARK: - ViewModifier Version

struct GlassCardModifier: ViewModifier {
    var cornerRadius: CGFloat = .radiusLarge
    var padding: CGFloat = .spacing4

    @Environment(\.designTheme) private var theme

    func body(content: Content) -> some View {
        content
            .padding(padding)
            .background(
                RoundedRectangle(cornerRadius: cornerRadius, style: .continuous)
                    .fill(theme.color(.card))
                    .overlay(
                        RoundedRectangle(cornerRadius: cornerRadius, style: .continuous)
                            .stroke(theme.color(.border).opacity(0.3), lineWidth: 1)
                    )
            )
    }
}

extension View {
    /// Applies glass card styling to the view
    /// - Parameters:
    ///   - cornerRadius: Corner radius of the card (default: .radiusLarge)
    ///   - padding: Internal padding (default: .spacing4)
    func glassCard(cornerRadius: CGFloat = .radiusLarge, padding: CGFloat = .spacing4) -> some View {
        modifier(GlassCardModifier(cornerRadius: cornerRadius, padding: padding))
    }
}

// MARK: - Preview

#if DEBUG
struct GlassCard_Previews: PreviewProvider {
    static var previews: some View {
        Group {
            // Light mode
            VStack(spacing: .spacing4) {
                // Container version
                GlassCard {
                    VStack(alignment: .leading, spacing: .spacing3) {
                        Text("Container Version")
                            .font(.headline)
                        Text("This is a glass card using the container style.")
                            .font(.subheadline)
                            .foregroundColor(.secondary)
                    }
                }

                // Modifier version
                VStack(alignment: .leading, spacing: .spacing3) {
                    Text("Modifier Version")
                        .font(.headline)
                    Text("This is a glass card using the modifier style.")
                        .font(.subheadline)
                        .foregroundColor(.secondary)
                }
                .glassCard()

                // Custom radius
                VStack(alignment: .leading, spacing: .spacing3) {
                    Text("Custom Radius")
                        .font(.headline)
                    Text("This card has a small corner radius.")
                        .font(.subheadline)
                        .foregroundColor(.secondary)
                }
                .glassCard(cornerRadius: .radiusSmall)
            }
            .padding()
            .withDesignTheme()
            .background(DesignTheme.defaultLight.color(.background))
            .previewDisplayName("Light Mode")

            // Dark mode
            VStack(spacing: .spacing4) {
                GlassCard {
                    VStack(alignment: .leading, spacing: .spacing3) {
                        Text("Glass Card")
                            .font(.headline)
                        Text("Dark mode glass card example.")
                            .font(.subheadline)
                            .foregroundColor(.secondary)
                    }
                }

                VStack(alignment: .leading, spacing: .spacing3) {
                    Text("Another Card")
                        .font(.headline)
                    Text("With modifier style.")
                        .font(.subheadline)
                        .foregroundColor(.secondary)
                }
                .glassCard()
            }
            .padding()
            .withDesignTheme()
            .background(DesignTheme.defaultDark.color(.background))
            .preferredColorScheme(.dark)
            .previewDisplayName("Dark Mode")
        }
    }
}
#endif

