            }
            .withDesignTheme()
            .brandBackground(.adaptive)
            .previewDisplayName("Adaptive - Light")

            // Adaptive background (dark mode)
            VStack {
                Text("Adaptive Background")
                    .font(.largeTitle)
                    .fontWeight(.bold)
            }
            .withDesignTheme()
            .brandBackground(.adaptive)
            .preferredColorScheme(.dark)
            .previewDisplayName("Adaptive - Dark")
