// Squad Story Circle Component (Instagram-style)
struct SquadStoryCircle: View {
    let name: String
    let icon: String
    let isSelected: Bool
    let action: () -> Void

    var body: some View {
        Button(action: action) {
            VStack(spacing: 6) {
                // Circle with icon
                ZStack {
                    // Outer ring (gradient border)
                    Circle()
                        .strokeBorder(
                            LinearGradient(
                                colors: isSelected
                                    ? [.blue, .purple]
                                    : [Color.white.opacity(0.3), Color.white.opacity(0.3)],
                                startPoint: .topLeading,
                                endPoint: .bottomTrailing
                            ),
                            lineWidth: 3
                        )
                        .frame(width: 70, height: 70)

                    // Inner circle with icon or image
                    Circle()
                        .fill(
                            LinearGradient(
                                colors: [
                                    Color.white.opacity(0.2),
                                    Color.white.opacity(0.1)
                                ],
                                startPoint: .topLeading,
                                endPoint: .bottomTrailing
                            )
                        )
                        .frame(width: 64, height: 64)

                    // Use image asset if name matches, otherwise use icon
                    if name == "Carroll" {
                        Image("carroll")
                            .resizable()
                            .scaledToFill()
                            .frame(width: 64, height: 64)
                            .clipShape(Circle())
                    } else if name == "2026" {
                        Image("26")
                            .resizable()
                            .scaledToFill()
                            .frame(width: 64, height: 64)
                            .clipShape(Circle())
                    } else if name == "Texas" {
                        Image("tx")
                            .resizable()
                            .scaledToFill()
                            .frame(width: 64, height: 64)
                            .clipShape(Circle())
                    } else {
                        Image(systemName: icon)
                            .font(.system(size: 28))
                            .foregroundColor(isSelected ? .white : .gray)
                    }
                }

                // Name label
                Text(name)
                    .font(.caption)
                    .fontWeight(isSelected ? .semibold : .regular)
