                    Image("lc")
                        .resizable()
                        .scaledToFill()
                        .frame(width: 44, height: 44)
                        .clipShape(Circle())
                } else {
                    Image(systemName: "person.crop.circle.fill")
                        .resizable()
                        .frame(width: 44, height: 44)
                        .foregroundColor(.gray)
                }

                VStack(alignment: .leading, spacing: 2) {
                    Text(authorName)
                        .font(.headline)
                        .foregroundColor(.white)

                    HStack(spacing: 4) {
                        Text(authorHandle)
                            .font(.caption)
                            .foregroundColor(.gray)
                        Text("â€¢")
                            .font(.caption)
                            .foregroundColor(.gray)
                        Text(timeAgo)
                            .font(.caption)
                            .foregroundColor(.gray)
                    }
                }

                Spacer()

                Button(action: {}) {
                    Image(systemName: "ellipsis")
                        .foregroundColor(.gray)
