// Detail Container that switches between School and Coach
struct DetailContainerView: View {
    @Binding var detailType: AthleteSearchView.DetailType
    @Binding var showDetail: Bool
    @State private var navigationStack: [AthleteSearchView.DetailType] = []

    var body: some View {
        NavigationStack(path: $navigationStack) {
            Group {
                switch detailType {
                case .school(let school):
                    SchoolDetailView(
                        initial: school.initial,
                        name: school.name,
                        location: school.location,
                        division: school.division,
                        conference: school.conference,
                        ranking: school.ranking,
                        navigationStack: $navigationStack
                    )
                case .coach(let coach):
                    CoachDetailView(
                        name: coach.name,
                        title: coach.title,
                        school: coach.school,
                        schoolInitial: coach.schoolInitial,
                        yearsExperience: coach.yearsExperience,
                        navigationStack: $navigationStack
                    )
                }
            }
            .toolbar {
                ToolbarItem(placement: .navigationBarLeading) {
                    Button("Done") {
                        showDetail = false
                    }
                    .foregroundColor(.blue)
                }
            }
            .navigationDestination(for: AthleteSearchView.DetailType.self) { destination in
                switch destination {
                case .school(let school):
                    SchoolDetailView(
                        initial: school.initial,
                        name: school.name,
                        location: school.location,
                        division: school.division,
                        conference: school.conference,
                        ranking: school.ranking,
                        navigationStack: $navigationStack
                    )
                    .toolbar {
                        ToolbarItem(placement: .navigationBarLeading) {
                            Button("Done") {
                                showDetail = false
                            }
                            .foregroundColor(.blue)
                        }
                    }
                case .coach(let coach):
                    CoachDetailView(
                        name: coach.name,
                        title: coach.title,
                        school: coach.school,
                        schoolInitial: coach.schoolInitial,
                        yearsExperience: coach.yearsExperience,
                        navigationStack: $navigationStack
                    )
                    .toolbar {
                        ToolbarItem(placement: .navigationBarLeading) {
                            Button("Done") {
                                showDetail = false
                            }
                            .foregroundColor(.blue)
                        }
                    }
                }
            }
        }
    }
}

