import SwiftUI
import Clerk

struct SignInView: View {
    @Environment(\.clerk) private var clerk
    @State private var authIsPresented = false
    @State private var userTypeSelectionIsPresented = false
    @State private var isLoading = false
    @State private var errorMessage: String?

    var body: some View {
        NavigationStack {
            ZStack {
                // Background gradient
                LinearGradient(
                    gradient: Gradient(colors: [
                        Color(.systemBackground),
                        Color.blue.opacity(0.05)
                    ]),
                    startPoint: .top,
                    endPoint: .bottom
                )
                .ignoresSafeArea()

            ScrollView {
                VStack(spacing: 32) {
                    Spacer()
                        .frame(height: 60)

                    // App branding
                    VStack(spacing: 16) {
                        Image(systemName: "sportscourt.fill")
                            .font(.system(size: 80))
                            .foregroundStyle(
                                LinearGradient(
                                    colors: [.blue, .purple],
                                    startPoint: .topLeading,
                                    endPoint: .bottomTrailing
                                )
                            )
                            .shadow(color: .blue.opacity(0.3), radius: 10, x: 0, y: 5)

                        Text("AthleadAI")
                            .font(.system(size: 44, weight: .bold, design: .rounded))

                        Text("Connect with coaches and showcase your talent")
                            .font(.body)
                            .foregroundColor(.secondary)
                            .multilineTextAlignment(.center)
                            .padding(.horizontal, 40)
                    }

                    // Error message
                    if let errorMessage = errorMessage {
                        HStack {
                            Image(systemName: "exclamationmark.triangle.fill")
                            Text(errorMessage)
                                .font(.footnote)
                        }
                        .foregroundColor(.red)
                        .padding()
                        .background(Color.red.opacity(0.1))
                        .cornerRadius(10)
                        .padding(.horizontal)
                        .transition(.scale.combined(with: .opacity))
                    }

                    // Sign in section
                    VStack(spacing: 20) {
                        if clerk.user == nil {
                            // Sign in button
                            Button {
                                authIsPresented = true
                                errorMessage = nil
                            } label: {
                                HStack {
                                    if isLoading {
                                        ProgressView()
                                            .progressViewStyle(CircularProgressViewStyle(tint: .white))
                                            .scaleEffect(0.9)
                                    } else {
                                        Image(systemName: "person.circle.fill")
                                        Text("Sign In")
                                            .font(.headline)
                                    }
                                }
                                .frame(maxWidth: .infinity)
                                .padding()
                                .background(
                                    LinearGradient(
                                        colors: [.blue, .purple],
                                        startPoint: .leading,
                                        endPoint: .trailing
                                    )
                                )
                                .foregroundStyle(.white)
                                .cornerRadius(14)
                                .shadow(color: .blue.opacity(0.3), radius: 8, x: 0, y: 4)
                            }
                            .disabled(isLoading)
                            .padding(.horizontal, 32)

                            // Sign up button
                            Button {
                                userTypeSelectionIsPresented = true
                                errorMessage = nil
                            } label: {
                                HStack {
                                    Image(systemName: "person.badge.plus")
                                    Text("Create Account")
                                        .font(.headline)
                                }
                                .frame(maxWidth: .infinity)
                                .padding()
                                .background(Color(.systemGray6))
                                .foregroundColor(.primary)
                                .cornerRadius(14)
                            }
                            .padding(.horizontal, 32)

                            // Additional info
                            VStack(spacing: 12) {
                                HStack(spacing: 16) {
                                    FeatureBadge(icon: "shield.checkered", text: "Secure")
                                    FeatureBadge(icon: "lock.fill", text: "Private")
                                    FeatureBadge(icon: "checkmark.seal.fill", text: "Verified")
                                }

                                Text("Sign in securely with Clerk authentication")
                                    .font(.caption)
                                    .foregroundColor(.secondary)
                                    .multilineTextAlignment(.center)
                            }
                            .padding(.top, 8)
                        } else {
                            // User is signed in
                            VStack(spacing: 16) {
                                Image(systemName: "checkmark.circle.fill")
                                    .font(.system(size: 60))
                                    .foregroundStyle(.green)

                                Text("Welcome, \(clerk.user?.firstName ?? "User")!")
                                    .font(.title2)
                                    .fontWeight(.semibold)

                                Text("You're all set!")
                                    .font(.subheadline)
                                    .foregroundColor(.secondary)

                                UserButton()
                                    .padding(.top, 8)
                            }
                            .padding()
                        }
                    }

                    Spacer()
                }
                .padding(.vertical)
            }
            }
            .sheet(isPresented: $authIsPresented) {
                AuthView()
                    .onDisappear {
                        // Handle auth completion
                        if clerk.user != nil {
                            isLoading = false
                        } else {
                            // User dismissed without signing in
                            errorMessage = nil
                        }
                    }
            }
            .sheet(isPresented: $userTypeSelectionIsPresented) {
                NavigationStack {
                    UserTypeSelectionView()
                }
            }
        }
    }
}

