//
//  AthleteSearchView.swift
//  AthleadAI
//
//  Search view for athletes - can search for schools, coaches, and other athletes
//

import SwiftUI

struct AthleteSearchView: View {
    @State private var schools: [School] = []
    @State private var coaches: [Coach] = []
    @State private var isLoading = false
    @State private var searchText = ""
    @State private var selectedTab: SearchTab = .schools
    @State private var showDetail = false
    @State private var detailType: DetailType = .school(SchoolData(initial: "", name: "", location: "", division: "", conference: "", ranking: ""))

    enum SearchTab: String, CaseIterable {
        case schools = "Schools"
        case coaches = "Coaches"
        case athletes = "Athletes"
    }

    enum DetailType: Hashable {
        case school(SchoolData)
        case coach(CoachData)
    }

    struct SchoolData: Hashable {
        let initial: String
        let name: String
        let location: String
        let division: String
        let conference: String
        let ranking: String
    }

    struct CoachData: Hashable {
        let name: String
        let title: String
        let school: String
        let schoolInitial: String
        let yearsExperience: String
    }

    var body: some View {
        NavigationView {
            VStack(spacing: 0) {
                // Search Bar
                HStack {
                        Image(systemName: "magnifyingglass")
                            .foregroundColor(.gray)

                    TextField("Search schools...", text: $searchText)
                        .foregroundColor(.white)
                }
                .padding()
                .background(
                        RoundedRectangle(cornerRadius: 12)
                            .fill(Color.black.opacity(0.4))
                            .overlay(
                                RoundedRectangle(cornerRadius: 12)
                                    .stroke(Color.white.opacity(0.3), lineWidth: 1)
                        )
                )
                .padding(.horizontal)
                .padding(.vertical, 12)

                // Tab Selector
                HStack(spacing: 0) {
                    ForEach(SearchTab.allCases, id: \.self) { tab in
                        Button(action: {
                            selectedTab = tab
                        }) {
                            VStack(spacing: 8) {
                                Text(tab.rawValue)
                                    .font(.subheadline)
                                    .fontWeight(.semibold)
                                    .foregroundColor(selectedTab == tab ? .white : .gray)

                                Rectangle()
                                    .fill(selectedTab == tab ? Color.blue : Color.clear)
                                    .frame(height: 2)
                            }
                            .frame(maxWidth: .infinity)
                        }
                    }
                }
                .padding(.horizontal)
                .padding(.bottom, 12)

                // Content
                if selectedTab == .schools {
                    SchoolsListView(
                        schools: searchSchools(query: searchText),
                        isLoading: isLoading,
                        showDetail: $showDetail,
                        detailType: $detailType
                    )
                } else if selectedTab == .coaches {
                    CoachesListView(
                        coaches: searchCoaches(query: searchText),
                        isLoading: isLoading,
                        showDetail: $showDetail,
                        detailType: $detailType
                    )
                } else {
                    AthletesListView()
                }
            }
            .withDesignTheme()
            .brandBackground(.feed)
            .navigationTitle("Schools & Coaches")
            .navigationBarTitleDisplayMode(.inline)
            .task {
                await loadMockData()
            }
            .toolbar {
                ToolbarItem(placement: .navigationBarTrailing) {
                    HStack(spacing: 16) {
                        Button(action: {}) {
                            Image(systemName: "chart.bar.fill")
                                .foregroundColor(.blue)
                        }
                        Button(action: {}) {
                            Image(systemName: "bookmark.fill")
                                .foregroundColor(.blue)
                        }
                    }
                }
            }
            .sheet(isPresented: $showDetail) {
                NavigationView {
                    DetailContainerView(detailType: $detailType, showDetail: $showDetail)
                }
            }
        }
    }

    // MARK: - Mock Data Loading (Replace with real API calls)
    private func loadMockData() async {
        isLoading = true
        try? await Task.sleep(nanoseconds: 500_000_000)

        schools = [
            School(
                id: "1",
                name: "Stanford University",
                location: "Stanford, CA",
                division: "Division I",
                conference: "Pac-12",
                ranking: "#6",
                initial: "SU",
                athleticWebsite: "gostanford.com",
                facility: "Maples Pavilion",
                championships: "3 National Titles",
                recentRecord: "28-6 (2023)"
            ),
            School(
                id: "2",
                name: "University of North Carolina",
                location: "Chapel Hill, NC",
                division: "Division I",
                conference: "ACC",
                ranking: "#28",
                initial: "UNC",
                athleticWebsite: "goheels.com",
                facility: "Carmichael Arena",
                championships: "2 National Titles",
                recentRecord: "24-8 (2023)"
            ),
            School(
                id: "3",
                name: "University of Texas",
                location: "Austin, TX",
                division: "Division I",
                conference: "Big 12",
                ranking: "#38",
                initial: "UT",
                athleticWebsite: "texassports.com",
                facility: "Gregory Gym",
                championships: "1 National Title",
                recentRecord: "22-10 (2023)"
            )
        ]

        coaches = [
            Coach(
                id: "1",
                name: "John Smith",
                title: "Head Coach",
                schoolId: "1",
                schoolName: "Stanford University",
                schoolInitial: "SU",
                yearsExperience: "15 years",
                email: "jsmith@stanford.edu",
                phone: "(650) 555-0100",
                specialization: "Outside Hitter Development",
                achievements: ["3x Conference Coach of the Year", "National Championship (2021)"]
            ),
            Coach(
                id: "2",
                name: "Sarah Johnson",
                title: "Assistant Coach",
                schoolId: "3",
                schoolName: "University of Texas",
                schoolInitial: "UT",
                yearsExperience: "8 years",
                email: "sjohnson@utexas.edu",
                phone: "(512) 555-0200",
                specialization: "Defensive Specialist",
                achievements: ["Former Olympic Athlete"]
            ),
            Coach(
                id: "3",
                name: "Mike Davis",
                title: "Head Coach",
                schoolId: "2",
                schoolName: "University of North Carolina",
                schoolInitial: "UNC",
                yearsExperience: "12 years",
                email: "mdavis@unc.edu",
                phone: "(919) 555-0300",
                specialization: "Middle Blocker Development",
                achievements: ["2x National Championship", "15+ Players to Professional League"]
            )
        ]

        isLoading = false
    }

    // MARK: - Search Functions
    private func searchSchools(query: String) -> [School] {
        if query.isEmpty {
            return schools
        }
        return schools.filter { school in
            school.name.localizedCaseInsensitiveContains(query) ||
            school.location.localizedCaseInsensitiveContains(query) ||
            school.conference.localizedCaseInsensitiveContains(query)
        }
    }

    private func searchCoaches(query: String) -> [Coach] {
        if query.isEmpty {
            return coaches
        }
        return coaches.filter { coach in
            coach.name.localizedCaseInsensitiveContains(query) ||
            coach.schoolName.localizedCaseInsensitiveContains(query) ||
            coach.title.localizedCaseInsensitiveContains(query)
        }
    }
}

// Detail Container that switches between School and Coach
struct DetailContainerView: View {
    @Binding var detailType: AthleteSearchView.DetailType
    @Binding var showDetail: Bool
    @State private var navigationStack: [AthleteSearchView.DetailType] = []

    var body: some View {
        NavigationStack(path: $navigationStack) {
            Group {
                switch detailType {
                case .school(let school):
                    SchoolDetailView(
                        initial: school.initial,
                        name: school.name,
                        location: school.location,
                        division: school.division,
                        conference: school.conference,
                        ranking: school.ranking,
                        navigationStack: $navigationStack
                    )
                case .coach(let coach):
                    CoachDetailView(
                        name: coach.name,
                        title: coach.title,
                        school: coach.school,
                        schoolInitial: coach.schoolInitial,
                        yearsExperience: coach.yearsExperience,
                        navigationStack: $navigationStack
                    )
                }
            }
            .toolbar {
                ToolbarItem(placement: .navigationBarLeading) {
                    Button("Done") {
                        showDetail = false
                    }
                    .foregroundColor(.blue)
                }
            }
            .navigationDestination(for: AthleteSearchView.DetailType.self) { destination in
                switch destination {
                case .school(let school):
                    SchoolDetailView(
                        initial: school.initial,
                        name: school.name,
                        location: school.location,
                        division: school.division,
                        conference: school.conference,
                        ranking: school.ranking,
                        navigationStack: $navigationStack
                    )
                    .toolbar {
                        ToolbarItem(placement: .navigationBarLeading) {
                            Button("Done") {
                                showDetail = false
                            }
                            .foregroundColor(.blue)
                        }
                    }
                case .coach(let coach):
                    CoachDetailView(
                        name: coach.name,
                        title: coach.title,
                        school: coach.school,
                        schoolInitial: coach.schoolInitial,
                        yearsExperience: coach.yearsExperience,
                        navigationStack: $navigationStack
                    )
                    .toolbar {
                        ToolbarItem(placement: .navigationBarLeading) {
                            Button("Done") {
                                showDetail = false
                            }
                            .foregroundColor(.blue)
                        }
                    }
                }
            }
        }
    }
}

// Schools List View
struct SchoolsListView: View {
    let schools: [School]
    let isLoading: Bool
    @Binding var showDetail: Bool
    @Binding var detailType: AthleteSearchView.DetailType

    var body: some View {
        ScrollView {
            VStack(spacing: 12) {
                if isLoading {
                    ProgressView()
                        .progressViewStyle(CircularProgressViewStyle(tint: .blue))
                        .padding()
                } else if schools.isEmpty {
                    Text("No schools found")
                        .foregroundColor(.gray)
                        .padding()
                } else {
                    ForEach(schools) { school in
                        SchoolCard(
                            initial: school.initial,
                            name: school.name,
                            location: school.location,
                            division: school.division,
                            conference: school.conference,
                            ranking: school.ranking ?? "N/A",
                            showDetail: $showDetail,
                            detailType: $detailType
                        )
                    }
                }
            }
            .padding()
        }
    }
}

