// Squad Story Circle Component (Instagram-style)
struct SquadStoryCircle: View {
    let name: String
    let icon: String
    let isSelected: Bool
    let action: () -> Void

    var body: some View {
        Button(action: action) {
            VStack(spacing: 6) {
                // Circle with icon
                ZStack {
                    // Outer ring (gradient border)
                    Circle()
                        .strokeBorder(
                            LinearGradient(
                                colors: isSelected
                                    ? [.blue, .purple]
                                    : [Color.white.opacity(0.3), Color.white.opacity(0.3)],
                                startPoint: .topLeading,
                                endPoint: .bottomTrailing
                            ),
                            lineWidth: 3
                        )
                        .frame(width: 70, height: 70)

                    // Inner circle with icon or image
                    Circle()
                        .fill(
                            LinearGradient(
                                colors: [
                                    Color.white.opacity(0.2),
                                    Color.white.opacity(0.1)
                                ],
                                startPoint: .topLeading,
                                endPoint: .bottomTrailing
                            )
                        )
                        .frame(width: 64, height: 64)

                    // Use image asset if name matches, otherwise use icon
                    if name == "Carroll" {
                        Image("carroll")
                            .resizable()
                            .scaledToFill()
                            .frame(width: 64, height: 64)
                            .clipShape(Circle())
                    } else if name == "2026" {
                        Image("26")
                            .resizable()
                            .scaledToFill()
                            .frame(width: 64, height: 64)
                            .clipShape(Circle())
                    } else if name == "Texas" {
                        Image("tx")
                            .resizable()
                            .scaledToFill()
                            .frame(width: 64, height: 64)
                            .clipShape(Circle())
                    } else {
                        Image(systemName: icon)
                            .font(.system(size: 28))
                            .foregroundColor(isSelected ? .white : .gray)
                    }
                }

                // Name label
                Text(name)
                    .font(.caption)
                    .fontWeight(isSelected ? .semibold : .regular)
                    .foregroundColor(isSelected ? .white : .gray)
                    .lineLimit(1)
            }
            .frame(width: 80)
        }
    }
}

// Feed Post Component
struct FeedPost: View {
    let authorName: String
    let authorHandle: String
    let timeAgo: String
    let squad: String
    let content: String
    let likes: Int
    let comments: Int
    var mediaType: MediaType? = nil

    @State private var isLiked = false

    enum MediaType {
        case image
        case video
    }

    var body: some View {
        VStack(alignment: .leading, spacing: 12) {
            // Header
            HStack(spacing: 12) {
                // Profile image
                if authorHandle == "@kinsleyyoung" {
                    Image("B302C166-4D7D-4BAD-BBBA-39D27A56987D")
                        .resizable()
                        .scaledToFill()
                        .frame(width: 44, height: 44)
                        .clipShape(Circle())
                } else if authorHandle == "@lc_clayton51" {
                    Image("lc")
                        .resizable()
                        .scaledToFill()
                        .frame(width: 44, height: 44)
                        .clipShape(Circle())
                } else {
                    Image(systemName: "person.crop.circle.fill")
                        .resizable()
                        .frame(width: 44, height: 44)
                        .foregroundColor(.gray)
                }

                VStack(alignment: .leading, spacing: 2) {
                    Text(authorName)
                        .font(.headline)
                        .foregroundColor(.white)

                    HStack(spacing: 4) {
                        Text(authorHandle)
                            .font(.caption)
                            .foregroundColor(.gray)
                        Text("â€¢")
                            .font(.caption)
                            .foregroundColor(.gray)
                        Text(timeAgo)
                            .font(.caption)
                            .foregroundColor(.gray)
                    }
                }

                Spacer()

                Button(action: {}) {
                    Image(systemName: "ellipsis")
                        .foregroundColor(.gray)
                }
            }

            // Squad tag
            HStack(spacing: 6) {
                Image(systemName: "person.3.fill")
                    .font(.caption2)
                Text(squad)
                    .font(.caption)
            }
            .foregroundColor(.blue)
            .padding(.horizontal, 10)
            .padding(.vertical, 4)
            .background(
                Capsule()
                    .fill(Color.blue.opacity(0.2))
            )

            // Content
            Text(content)
                .font(.body)
                .foregroundColor(.white)
                .lineSpacing(4)

            // Media (Image or Video)
            if let mediaType = mediaType {
                if mediaType == .image {
                    // Check if this is Kaylee's post and show kales image
                    if authorHandle == "@kales_usc" {
                        Image("kales")
                            .resizable()
                            .scaledToFill()
                            .frame(height: 200)
                            .clipped()
                            .cornerRadius(12)
                    } else if authorHandle == "@lc_clayton51" {
                        Image("lc")
                            .resizable()
                            .scaledToFill()
                            .frame(height: 200)
                            .clipped()
                            .cornerRadius(12)
                    } else if authorHandle == "@kinsleyyoung" && squad == "Carroll Volleyball" && content.contains("Team photo") {
                        Image("dragons")
                            .resizable()
                            .scaledToFill()
                            .frame(height: 200)
                            .clipped()
                            .cornerRadius(12)
                    } else {
                        RoundedRectangle(cornerRadius: 12)
                            .fill(Color.gray.opacity(0.3))
                            .frame(height: 200)
                            .overlay(
                                Image(systemName: "photo.fill")
                                    .font(.system(size: 60))
                                    .foregroundColor(.white.opacity(0.5))
                            )
                    }
                } else if mediaType == .video {
                    ZStack {
                        // Check if this is Kaylee's post and show kales image as video thumbnail
                        if authorHandle == "@kales_usc" {
                            Image("kales")
                                .resizable()
                                .scaledToFill()
                                .frame(height: 200)
                                .clipped()
                                .cornerRadius(12)
                        } else if authorHandle == "@kinsleyyoung" && content.contains("block timing") {
                            Image("block")
                                .resizable()
                                .scaledToFill()
                                .frame(height: 200)
                                .clipped()
                                .cornerRadius(12)
                        } else {
                            RoundedRectangle(cornerRadius: 12)
                                .fill(Color.gray.opacity(0.3))
                                .frame(height: 200)
                                .overlay(
                                    Image(systemName: "video.fill")
                                        .font(.system(size: 40))
                                        .foregroundColor(.white.opacity(0.3))
                                )
                        }

                        Circle()
                            .fill(Color.white.opacity(0.9))
                            .frame(width: 60, height: 60)

                        Image(systemName: "play.fill")
                            .font(.system(size: 24))
                            .foregroundColor(.black)
                    }
                }
            }

            // Action buttons
            HStack(spacing: 24) {
                Button(action: { isLiked.toggle() }) {
                    HStack(spacing: 6) {
                        Image(systemName: isLiked ? "heart.fill" : "heart")
                            .foregroundColor(isLiked ? .red : .gray)
                        Text("\(isLiked ? likes + 1 : likes)")
                            .font(.subheadline)
                            .foregroundColor(.gray)
                    }
                }

                Button(action: {}) {
                    HStack(spacing: 6) {
                        Image(systemName: "bubble.right")
                            .foregroundColor(.gray)
                        Text("\(comments)")
                            .font(.subheadline)
                            .foregroundColor(.gray)
                    }
                }

                Button(action: {}) {
                    Image(systemName: "arrow.turn.up.right")
                        .foregroundColor(.gray)
                }

                Spacer()

                Button(action: {}) {
                    Image(systemName: "bookmark")
                        .foregroundColor(.gray)
                }
            }
        }
        .glassCard(cornerRadius: .radiusLarge, padding: .spacing4)
    }
}

