//
//  AthleteFeedView.swift
//  AthleadAI
//
//  Feed view customized for athlete users with squad functionality
//

import SwiftUI

struct AthleteFeedView: View {
    @State private var viewModel = FeedViewModel(feedType: .athlete)

    var body: some View {
        NavigationView {
            ScrollView {
                VStack(spacing: 20) {
                    // Squads Section
                    VStack(alignment: .leading, spacing: 12) {
                        HStack {
                            Text("Squads")
                                .font(.title3)
                                .fontWeight(.bold)
                                .foregroundColor(.white)

                            Spacer()

                            Button(action: {}) {
                                    Image(systemName: "plus.circle.fill")
                                        .foregroundColor(.blue)
                                        .font(.title3)
                                }
                            }
                            .padding(.horizontal)

                            ScrollView(.horizontal, showsIndicators: false) {
                                HStack(spacing: 16) {
                                    // All Posts (default)
                                    CircleFilter(
                                        name: "All",
                                        icon: "house.fill",
                                        isSelected: viewModel.selectedSquad == nil
                                    ) {
                                        viewModel.selectSquad(nil)
                                    }

                                    // Squad circles with custom images
                                    CircleFilter(
                                        name: "Carroll",
                                        imageName: "carroll",
                                        isSelected: viewModel.selectedSquad == "Carroll Volleyball"
                                    ) {
                                        viewModel.selectSquad("Carroll Volleyball")
                                    }

                                    CircleFilter(
                                        name: "2026",
                                        imageName: "26",
                                        isSelected: viewModel.selectedSquad == "Class of 2026"
                                    ) {
                                        viewModel.selectSquad("Class of 2026")
                                    }

                                    CircleFilter(
                                        name: "Texas",
                                        imageName: "tx",
                                        isSelected: viewModel.selectedSquad == "Texas Volleyball"
                                    ) {
                                        viewModel.selectSquad("Texas Volleyball")
                                    }
                                }
                                .padding(.horizontal)
                            }
                        }
                        .padding(.vertical, 12)

                        // Feed Posts
                        VStack(spacing: 16) {
                            ForEach(viewModel.filteredPosts, id: \.id) { post in
                                FeedPost(
                                    authorName: post.authorName,
                                    authorHandle: post.authorHandle,
                                    timeAgo: post.timeAgo,
                                    squad: post.squad,
                                    content: post.content,
                                    likes: post.likes,
                                    comments: post.comments,
                                    mediaType: post.mediaType
                                )
                            }
                        }
                        .padding(.horizontal)
                        .padding(.bottom, 20)
                    .padding(.top, 20)
                }
            }
            .withDesignTheme()
            .brandBackground(.feed)
            .navigationTitle("Feed")
            .navigationBarTitleDisplayMode(.inline)
            .toolbar {
                ToolbarItem(placement: .navigationBarTrailing) {
                    Button(action: { viewModel.toggleCreatePost() }) {
                        Image(systemName: "square.and.pencil")
                            .foregroundColor(.white)
                    }
                }
            }
            .sheet(isPresented: $viewModel.showCreatePost) {
                CreatePostView()
            }
            .task {
                await viewModel.loadFeed()
            }
        }
    }
}

// Create Post View (placeholder)
struct CreatePostView: View {
    @Environment(\.dismiss) var dismiss
    @State private var postContent = ""
    @State private var selectedSquad = "All"

    var body: some View {
        NavigationView {
            VStack(spacing: 20) {
                // Squad picker
                    VStack(alignment: .leading, spacing: 8) {
                        Text("Post to Squad")
                            .font(.subheadline)
                            .foregroundColor(.gray)

                        Menu {
                            Button("All") { selectedSquad = "All" }
                            Button("Carroll Volleyball") { selectedSquad = "Carroll Volleyball" }
                            Button("Class of 2026") { selectedSquad = "Class of 2026" }
                            Button("Texas Volleyball") { selectedSquad = "Texas Volleyball" }
                        } label: {
                            HStack {
                                Text(selectedSquad)
                                    .foregroundColor(.white)
                                Spacer()
                                Image(systemName: "chevron.down")
                                    .foregroundColor(.gray)
                            }
                            .padding()
                            .background(
                                RoundedRectangle(cornerRadius: 12)
                                    .fill(Color.black.opacity(0.4))
                                    .overlay(
                                        RoundedRectangle(cornerRadius: 12)
                                            .stroke(Color.white.opacity(0.3), lineWidth: 1)
                                    )
                            )
                        }
                    }

                    // Text editor
                    TextEditor(text: $postContent)
                        .frame(height: 200)
                        .scrollContentBackground(.hidden)
                        .padding()
                        .background(
                            RoundedRectangle(cornerRadius: 12)
                                .fill(.ultraThinMaterial)
                        )
                        .foregroundColor(.white)

                Spacer()
            }
            .padding()
            .withDesignTheme()
            .brandBackground(.feed)
            .navigationTitle("Create Post")
            .navigationBarTitleDisplayMode(.inline)
            .toolbar {
                ToolbarItem(placement: .navigationBarLeading) {
                    Button("Cancel") {
                        dismiss()
                    }
                    .foregroundColor(.white)
                }
                ToolbarItem(placement: .navigationBarTrailing) {
                    Button("Post") {
                        // Post logic here
                        dismiss()
                    }
                    .foregroundColor(.blue)
                    .fontWeight(.semibold)
                }
            }
        }
    }
}

#Preview {
    AthleteFeedView()
}

//
//  CollegeCoachFeedView.swift
//  AthleadAI
//
//  Feed view customized for college coaches with recruiting filters
//

import SwiftUI

struct CollegeCoachFeedView: View {
    @State private var viewModel = FeedViewModel(feedType: .coach)

    var body: some View {
        NavigationView {
            ZStack {
                // Background gradient
                LinearGradient(
                    gradient: Gradient(colors: [
                        Color(red: 0.25, green: 0.35, blue: 0.55),
                        Color(red: 0.15, green: 0.25, blue: 0.45)
                    ]),
                    startPoint: .topLeading,
                    endPoint: .bottomTrailing
                )
                .ignoresSafeArea()

                ScrollView {
                    VStack(spacing: 20) {
                        // Recruit Filters Section
                        VStack(alignment: .leading, spacing: 12) {
                            HStack {
                                Text("Filters")
                                    .font(.title3)
                                    .fontWeight(.bold)
                                    .foregroundColor(.white)

                                Spacer()

                                Button(action: { viewModel.selectFilter(nil) }) {
                                    Text("Clear")
                                        .font(.subheadline)
                                        .foregroundColor(.blue)
                                }
                            }
                            .padding(.horizontal)

                            ScrollView(.horizontal, showsIndicators: false) {
                                HStack(spacing: 16) {
                                    // All Posts (default)
                                    CircleFilter(
                                        name: "All",
                                        icon: "house.fill",
                                        isSelected: viewModel.selectedFilter == nil
                                    ) {
                                        viewModel.selectFilter(nil)
                                    }

                                    // Watchlist
                                    CircleFilter(
                                        name: "Watchlist",
                                        icon: "star.fill",
                                        iconColor: .yellow,
                                        isSelected: viewModel.selectedFilter == .watchlist
                                    ) {
                                        viewModel.selectFilter(.watchlist)
                                    }

                                    // Grad Year filters
                                    CircleFilter(
                                        name: "2025",
                                        icon: "graduationcap.fill",
                                        iconColor: .green,
                                        isSelected: viewModel.selectedFilter == .gradYear2025
                                    ) {
                                        viewModel.selectFilter(.gradYear2025)
                                    }

                                    CircleFilter(
                                        name: "2026",
                                        icon: "graduationcap.fill",
                                        iconColor: .blue,
                                        isSelected: viewModel.selectedFilter == .gradYear2026
                                    ) {
                                        viewModel.selectFilter(.gradYear2026)
                                    }

                                    CircleFilter(
                                        name: "2027",
                                        icon: "graduationcap.fill",
                                        iconColor: .purple,
                                        isSelected: viewModel.selectedFilter == .gradYear2027
                                    ) {
                                        viewModel.selectFilter(.gradYear2027)
                                    }

                                    // Position filter
                                    CircleFilter(
                                        name: "Outside",
                                        icon: "figure.volleyball",
                                        iconColor: .orange,
                                        isSelected: viewModel.selectedFilter == .positionOutside
                                    ) {
                                        viewModel.selectFilter(.positionOutside)
                                    }

                                    CircleFilter(
                                        name: "Setter",
                                        icon: "figure.volleyball",
                                        iconColor: .red,
                                        isSelected: viewModel.selectedFilter == .positionSetter
                                    ) {
                                        viewModel.selectFilter(.positionSetter)
                                    }

                                    // Location filter
                                    CircleFilter(
                                        name: "Texas",
                                        icon: "map.fill",
                                        iconColor: .teal,
                                        isSelected: viewModel.selectedFilter == .locationTexas
                                    ) {
                                        viewModel.selectFilter(.locationTexas)
                                    }
                                }
                                .padding(.horizontal)
                            }
                        }
                        .padding(.vertical, 12)

                        // Feed Posts
                        VStack(spacing: 16) {
                            ForEach(viewModel.filteredPosts, id: \.id) { post in
                                FeedPost(
                                    authorName: post.authorName,
                                    authorHandle: post.authorHandle,
                                    timeAgo: post.timeAgo,
                                    squad: post.squad,
                                    content: post.content,
                                    likes: post.likes,
                                    comments: post.comments,
                                    mediaType: post.mediaType
                                )
                            }
                        }
                        .padding(.horizontal)
                        .padding(.bottom, 20)
                    }
                    .padding(.top, 20)
                }
            }
            .navigationTitle("Feed")
            .navigationBarTitleDisplayMode(.inline)
            .toolbar {
                ToolbarItem(placement: .navigationBarTrailing) {
                    Button(action: { viewModel.toggleCreatePost() }) {
                        Image(systemName: "square.and.pencil")
                            .foregroundColor(.white)
                    }
                }
            }
            .sheet(isPresented: $viewModel.showCreatePost) {
                CreatePostView()
            }
            .task {
                await viewModel.loadFeed()
            }
        }
    }
}

// MARK: - Recruit Filter Enum
enum RecruitFilter {
    case watchlist
    case gradYear2025
    case gradYear2026
    case gradYear2027
    case positionOutside
    case positionSetter
    case locationTexas
}

