struct CoachesListView: View {
    let coaches: [Coach]
    let isLoading: Bool
    @Binding var showDetail: Bool
    @Binding var detailType: SearchViewModel.DetailType

    var body: some View {
        ScrollView {
            VStack(spacing: 12) {
                if isLoading {
                    ProgressView()
                        .progressViewStyle(CircularProgressViewStyle(tint: .blue))
