                        .stroke(Color.blue, lineWidth: 2)
                )

            VStack(alignment: .leading, spacing: 6) {
                Text(user.fullName)
                    .font(.headline)
                    .foregroundColor(.white)

                Text("\(user.sport) â€¢ \(user.position)")
                    .font(.subheadline)
                    .foregroundColor(.blue)

                Text(user.classYear)
                    .font(.caption)
                    .foregroundColor(.gray)
            }

            Spacer()

            Image(systemName: "chevron.right")
                .foregroundColor(.gray)
        }
        .glassCard(cornerRadius: .radiusLarge, padding: .spacing4)
    }
}

