//
//  CollegeCoachSearchView.swift
//  AthleadAI
//
//  Search view for college coaches - focused on athlete recruiting search
//

import SwiftUI

struct CollegeCoachSearchView: View {
    @State private var viewModel = SearchViewModel(searchType: .coach)

    let positions = ["Outside Hitter", "Setter", "Middle Blocker", "Libero", "Opposite", "Defensive Specialist"]
    let gradYears = ["2025", "2026", "2027", "2028"]
    let locations = ["Texas", "California", "Florida", "Illinois", "New York", "All States"]

    var body: some View {
        NavigationView {
            ZStack {
                // Background gradient
                LinearGradient(
                    gradient: Gradient(colors: [
                        Color(red: 0.1, green: 0.1, blue: 0.2),
                        Color(red: 0.05, green: 0.05, blue: 0.15)
                    ]),
                    startPoint: .topLeading,
                    endPoint: .bottomTrailing
                )
                .ignoresSafeArea()

                VStack(spacing: 0) {
                    // Search Bar
                    HStack {
                        Image(systemName: "magnifyingglass")
                            .foregroundColor(.gray)

                        TextField("Search athletes...", text: $viewModel.searchText)
                            .foregroundColor(.white)

                        if !viewModel.searchText.isEmpty {
                            Button(action: { viewModel.clearSearch() }) {
                                Image(systemName: "xmark.circle.fill")
                                    .foregroundColor(.gray)
                            }
                        }
                    }
                    .padding()
                    .background(
                        RoundedRectangle(cornerRadius: 12)
                            .fill(Color.black.opacity(0.4))
                            .overlay(
                                RoundedRectangle(cornerRadius: 12)
                                    .stroke(Color.white.opacity(0.3), lineWidth: 1)
                            )
                    )
                    .padding(.horizontal)
                    .padding(.vertical, 12)

                    // Filter Chips
                    ScrollView(.horizontal, showsIndicators: false) {
                        HStack(spacing: 12) {
                            // Filters Button
                            Button(action: { viewModel.toggleFilters() }) {
                                HStack(spacing: 6) {
                                    Image(systemName: "line.3.horizontal.decrease.circle")
                                    Text("Filters")
                                }
                                .padding(.horizontal, 16)
                                .padding(.vertical, 8)
                                .background(
                                    Capsule()
                                        .fill(viewModel.showFilters ? Color.blue : Color.black.opacity(0.4))
                                )
                                .foregroundColor(.white)
                            }

                            // Position Filter
                            if let position = viewModel.selectedPosition {
                                SearchFilterChip(title: position, icon: "figure.volleyball") {
                                    viewModel.selectPosition(nil)
                                }
                            }

                            // Grad Year Filter
                            if let year = viewModel.selectedGradYear {
                                SearchFilterChip(title: "Class of \(year)", icon: "graduationcap.fill") {
                                    viewModel.selectGradYear(nil)
                                }
                            }

                            // Location Filter
                            if let location = viewModel.selectedLocation {
                                SearchFilterChip(title: location, icon: "map.fill") {
                                    viewModel.selectLocation(nil)
                                }
                            }
                        }
                        .padding(.horizontal)
                    }
                    .padding(.bottom, 12)

                    // Filter Options (expandable)
                    if viewModel.showFilters {
                        VStack(alignment: .leading, spacing: 16) {
                            // Position Filter
                            VStack(alignment: .leading, spacing: 8) {
                                Text("Position")
                                    .font(.subheadline)
                                    .fontWeight(.semibold)
                                    .foregroundColor(.white.opacity(0.7))

                                ScrollView(.horizontal, showsIndicators: false) {
                                    HStack(spacing: 8) {
                                        ForEach(positions, id: \.self) { position in
                                            Button(action: {
                                                viewModel.selectPosition(position)
                                            }) {
                                                Text(position)
                                                    .font(.caption)
                                                    .padding(.horizontal, 12)
                                                    .padding(.vertical, 6)
                                                    .background(
                                                        Capsule()
                                                            .fill(viewModel.selectedPosition == position ? Color.blue : Color.white.opacity(0.2))
                                                    )
                                                    .foregroundColor(.white)
                                            }
                                        }
                                    }
                                }
                            }

                            // Grad Year Filter
                            VStack(alignment: .leading, spacing: 8) {
                                Text("Graduation Year")
                                    .font(.subheadline)
                                    .fontWeight(.semibold)
                                    .foregroundColor(.white.opacity(0.7))

                                HStack(spacing: 8) {
                                    ForEach(gradYears, id: \.self) { year in
                                        Button(action: {
                                            viewModel.selectGradYear(year)
                                        }) {
                                            Text(year)
                                                .font(.caption)
                                                .padding(.horizontal, 12)
                                                .padding(.vertical, 6)
                                                .background(
                                                    Capsule()
                                                        .fill(viewModel.selectedGradYear == year ? Color.blue : Color.white.opacity(0.2))
                                                )
                                                .foregroundColor(.white)
                                        }
                                    }
                                }
                            }

                            // Location Filter
                            VStack(alignment: .leading, spacing: 8) {
                                Text("Location")
                                    .font(.subheadline)
                                    .fontWeight(.semibold)
                                    .foregroundColor(.white.opacity(0.7))

                                ScrollView(.horizontal, showsIndicators: false) {
                                    HStack(spacing: 8) {
                                        ForEach(locations, id: \.self) { location in
                                            Button(action: {
                                                viewModel.selectLocation(location)
                                            }) {
                                                Text(location)
                                                    .font(.caption)
                                                    .padding(.horizontal, 12)
                                                    .padding(.vertical, 6)
                                                    .background(
                                                        Capsule()
                                                            .fill(viewModel.selectedLocation == location ? Color.blue : Color.white.opacity(0.2))
                                                    )
                                                    .foregroundColor(.white)
                                            }
                                        }
                                    }
                                }
                            }
                        }
                        .padding()
                        .background(
                            RoundedRectangle(cornerRadius: 16)
                                .fill(Color.black.opacity(0.4))
                                .overlay(
                                    RoundedRectangle(cornerRadius: 16)
                                        .stroke(Color.white.opacity(0.3), lineWidth: 1)
                                )
                        )
                        .padding(.horizontal)
                        .padding(.bottom, 12)
                    }

                    // Results List
                    ScrollView {
                        VStack(spacing: 12) {
                            ForEach(viewModel.filteredAthletes) { athlete in
                                AthleteResultCard(
                                    name: athlete.name,
                                    position: athlete.position,
                                    gradYear: athlete.gradYear,
                                    location: athlete.location,
                                    height: athlete.height,
                                    gpa: athlete.gpa,
                                    isWatchlisted: athlete.isWatchlisted,
                                    onToggleWatchlist: {
                                        viewModel.toggleWatchlist(for: athlete)
                                    }
                                )
                            }
                        }
                        .padding(.horizontal)
                        .padding(.bottom, 20)
                    }
                }
            }
            .navigationTitle("Athlete Search")
            .navigationBarTitleDisplayMode(.inline)
            .task {
                await viewModel.loadData()
            }
        }
    }
}

// MARK: - Search Filter Chip Component
struct SearchFilterChip: View {
    let title: String
    let icon: String
    let onRemove: () -> Void

    var body: some View {
        HStack(spacing: 6) {
            Image(systemName: icon)
                .font(.caption)
            Text(title)
                .font(.caption)
            Button(action: onRemove) {
                Image(systemName: "xmark.circle.fill")
                    .font(.caption)
            }
        }
        .padding(.horizontal, 12)
        .padding(.vertical, 8)
        .background(
            Capsule()
                .fill(Color.blue)
        )
        .foregroundColor(.white)
    }
}

// MARK: - Athlete Result Card Component
struct AthleteResultCard: View {
    let name: String
    let position: String
    let gradYear: String
    let location: String
    let height: String
    let gpa: String
    let isWatchlisted: Bool
    let onToggleWatchlist: () -> Void

    var body: some View {
        Button(action: {
            // Navigate to athlete profile
        }) {
            VStack(spacing: 0) {
                HStack(spacing: 12) {
                    // Profile Image
                    if name == "Kinsley Young" {
                        Image("B302C166-4D7D-4BAD-BBBA-39D27A56987D")
                            .resizable()
                            .scaledToFill()
                            .frame(width: 60, height: 60)
                            .clipShape(Circle())
                    } else {
                        Circle()
                            .fill(Color.gray.opacity(0.3))
                            .frame(width: 60, height: 60)
                            .overlay(
                                Image(systemName: "person.fill")
                                    .foregroundColor(.gray)
                            )
                    }

                    // Info
                    VStack(alignment: .leading, spacing: 6) {
                        HStack {
                            Text(name)
                                .font(.headline)
                                .foregroundColor(.white)

                            Spacer()

                            // Watchlist Button
                            Button(action: onToggleWatchlist) {
                                Image(systemName: isWatchlisted ? "star.fill" : "star")
                                    .foregroundColor(isWatchlisted ? .yellow : .gray)
                                    .font(.title3)
                            }
                            .buttonStyle(PlainButtonStyle())
                        }

                        HStack(spacing: 12) {
                            Label(position, systemImage: "figure.volleyball")
                                .font(.caption)
                                .foregroundColor(.blue)

                            Label("'\(gradYear.suffix(2))", systemImage: "graduationcap.fill")
                                .font(.caption)
                                .foregroundColor(.green)
                        }

                        HStack(spacing: 12) {
                            Text(location)
                                .font(.caption)
                                .foregroundColor(.gray)

                            Text("â€¢")
                                .foregroundColor(.gray)

                            Text(height)
                                .font(.caption)
                                .foregroundColor(.gray)

                            Text("â€¢")
                                .foregroundColor(.gray)

                            Text("GPA: \(gpa)")
                                .font(.caption)
                                .foregroundColor(.gray)
                        }
                    }

                    Image(systemName: "chevron.right")
                        .foregroundColor(.gray)
                        .font(.caption)
                }
                .padding(16)
            }
            .background(
                RoundedRectangle(cornerRadius: 16)
                    .fill(Color.black.opacity(0.4))
                    .overlay(
                        RoundedRectangle(cornerRadius: 16)
                            .stroke(Color.white.opacity(0.3), lineWidth: 1)
                    )
            )
        }
        .buttonStyle(PlainButtonStyle())
    }
}

#Preview {
    CollegeCoachSearchView()
}

//
//  SignInView.swift
//  AthleadAI
//
//  Sign-in view using Clerk's built-in AuthView
//

import SwiftUI
import Clerk

struct SignInView: View {
    @Environment(\.clerk) private var clerk
    @State private var authIsPresented = false
    @State private var userTypeSelectionIsPresented = false
    @State private var isLoading = false
    @State private var errorMessage: String?

    var body: some View {
        NavigationStack {
            ScrollView {
                VStack(spacing: 32) {
                    Spacer()
                        .frame(height: 60)

                    // App branding
                    VStack(spacing: 16) {
                        Image(systemName: "sportscourt.fill")
                            .font(.system(size: 80))
                            .foregroundStyle(
                                LinearGradient(
                                    colors: [.blue, .purple],
                                    startPoint: .topLeading,
                                    endPoint: .bottomTrailing
                                )
                            )
                            .shadow(color: .blue.opacity(0.3), radius: 10, x: 0, y: 5)

                        Text("AthleadAI")
                            .font(.system(size: 44, weight: .bold, design: .rounded))

                        Text("Connect with coaches and showcase your talent")
                            .font(.body)
                            .foregroundColor(.secondary)
                            .multilineTextAlignment(.center)
                            .padding(.horizontal, 40)
                    }

                    // Error message
                    if let errorMessage = errorMessage {
                        HStack {
                            Image(systemName: "exclamationmark.triangle.fill")
                            Text(errorMessage)
                                .font(.footnote)
                        }
                        .foregroundColor(.red)
                        .padding()
                        .background(Color.red.opacity(0.1))
                        .cornerRadius(10)
                        .padding(.horizontal)
                        .transition(.scale.combined(with: .opacity))
                    }

                    // Sign in section
                    VStack(spacing: 20) {
                        if clerk.user == nil {
                            // Sign in button
                            BrandButton(
                                title: "Sign In",
                                icon: "person.circle.fill",
                                style: .primary,
                                isLoading: isLoading
                            ) {
                                authIsPresented = true
                                errorMessage = nil
                            }
                            .disabled(isLoading)
                            .padding(.horizontal, 32)

                            // Sign up button
                            BrandButton(
                                title: "Create Account",
                                icon: "person.badge.plus",
                                style: .secondary
                            ) {
                                userTypeSelectionIsPresented = true
                                errorMessage = nil
                            }
                            .padding(.horizontal, 32)

                            // Additional info
                            VStack(spacing: 12) {
                                HStack(spacing: 16) {
                                    FeatureBadge(icon: "shield.checkered", text: "Secure")
                                    FeatureBadge(icon: "lock.fill", text: "Private")
                                    FeatureBadge(icon: "checkmark.seal.fill", text: "Verified")
                                }

                                Text("Sign in securely with Clerk authentication")
                                    .font(.caption)
                                    .foregroundColor(.secondary)
                                    .multilineTextAlignment(.center)
                            }
                            .padding(.top, 8)
                        } else {
                            // User is signed in
                            VStack(spacing: 16) {
                                Image(systemName: "checkmark.circle.fill")
                                    .font(.system(size: 60))
                                    .foregroundStyle(.green)

                                Text("Welcome, \(clerk.user?.firstName ?? "User")!")
                                    .font(.title2)
                                    .fontWeight(.semibold)

                                Text("You're all set!")
                                    .font(.subheadline)
                                    .foregroundColor(.secondary)

                                UserButton()
                                    .padding(.top, 8)
                            }
                            .padding()
                        }
                    }

                    Spacer()
                }
                .padding(.vertical)
            }
            .withDesignTheme()
            .brandBackground(.adaptive)
            .sheet(isPresented: $authIsPresented) {
                AuthView()
                    .onDisappear {
                        // Handle auth completion
                        if clerk.user != nil {
                            isLoading = false
                        } else {
                            // User dismissed without signing in
                            errorMessage = nil
                        }
                    }
            }
            .sheet(isPresented: $userTypeSelectionIsPresented) {
                NavigationStack {
                    UserTypeSelectionView()
                }
            }
        }
    }
}

// MARK: - Feature Badge Component
struct FeatureBadge: View {
    let icon: String
    let text: String

    var body: some View {
        HStack(spacing: 4) {
            Image(systemName: icon)
                .font(.caption)
            Text(text)
                .font(.caption)
                .fontWeight(.medium)
        }
        .foregroundColor(.secondary)
        .padding(.horizontal, 10)
        .padding(.vertical, 6)
        .background(Color.secondary.opacity(0.1))
        .cornerRadius(8)
    }
}

