                    ScrollView(.horizontal, showsIndicators: false) {
                        HStack(spacing: 12) {
                            // Filters Button
                            Button(action: { viewModel.toggleFilters() }) {
                                HStack(spacing: 6) {
                                    Image(systemName: "line.3.horizontal.decrease.circle")
                                    Text("Filters")
                                }
                                .padding(.horizontal, 16)
                                .padding(.vertical, 8)
                                .background(
                                    Capsule()
                                        .fill(viewModel.showFilters ? Color.blue : Color.black.opacity(0.4))
                                )
                                .foregroundColor(.white)
                            }

                            // Position Filter
                            if let position = viewModel.selectedPosition {
                                SearchFilterChip(title: position, icon: "figure.volleyball") {
                                    viewModel.selectPosition(nil)
                                }
                            }

                            // Grad Year Filter
                            if let year = viewModel.selectedGradYear {
                                SearchFilterChip(title: "Class of \(year)", icon: "graduationcap.fill") {
                                    viewModel.selectGradYear(nil)
                                }
                            }

                            // Location Filter
                            if let location = viewModel.selectedLocation {
                                SearchFilterChip(title: location, icon: "map.fill") {
                                    viewModel.selectLocation(nil)
                                }
                            }
                        }
                        .padding(.horizontal)
                    }
                    .padding(.bottom, 12)

                    // Filter Options (expandable)
                    if viewModel.showFilters {
                        VStack(alignment: .leading, spacing: 16) {
                            // Position Filter
                            VStack(alignment: .leading, spacing: 8) {
                                Text("Position")
                                    .font(.subheadline)
                                    .fontWeight(.semibold)
                                    .foregroundColor(.white.opacity(0.7))

                                ScrollView(.horizontal, showsIndicators: false) {
                                    HStack(spacing: 8) {
                                        ForEach(positions, id: \.self) { position in
                                            Button(action: {
                                                viewModel.selectPosition(position)
                                            }) {
                                                Text(position)
                                                    .font(.caption)
                                                    .padding(.horizontal, 12)
                                                    .padding(.vertical, 6)
                                                    .background(
                                                        Capsule()
                                                            .fill(viewModel.selectedPosition == position ? Color.blue : Color.white.opacity(0.2))
                                                    )
                                                    .foregroundColor(.white)
                                            }
                                        }
                                    }
                                }
                            }

                            // Grad Year Filter
                            VStack(alignment: .leading, spacing: 8) {
                                Text("Graduation Year")
                                    .font(.subheadline)
                                    .fontWeight(.semibold)
                                    .foregroundColor(.white.opacity(0.7))

                                HStack(spacing: 8) {
                                    ForEach(gradYears, id: \.self) { year in
                                        Button(action: {
                                            viewModel.selectGradYear(year)
                                        }) {
                                            Text(year)
                                                .font(.caption)
                                                .padding(.horizontal, 12)
                                                .padding(.vertical, 6)
                                                .background(
                                                    Capsule()
                                                        .fill(viewModel.selectedGradYear == year ? Color.blue : Color.white.opacity(0.2))
                                                )
                                                .foregroundColor(.white)
                                        }
                                    }
                                }
                            }

                            // Location Filter
                            VStack(alignment: .leading, spacing: 8) {
                                Text("Location")
                                    .font(.subheadline)
                                    .fontWeight(.semibold)
                                    .foregroundColor(.white.opacity(0.7))

                                ScrollView(.horizontal, showsIndicators: false) {
                                    HStack(spacing: 8) {
                                        ForEach(locations, id: \.self) { location in
                                            Button(action: {
                                                viewModel.selectLocation(location)
                                            }) {
                                                Text(location)
                                                    .font(.caption)
                                                    .padding(.horizontal, 12)
                                                    .padding(.vertical, 6)
                                                    .background(
                                                        Capsule()
                                                            .fill(viewModel.selectedLocation == location ? Color.blue : Color.white.opacity(0.2))
                                                    )
                                                    .foregroundColor(.white)
                                            }
                                        }
                                    }
                                }
                            }
                        }
                        .padding()
                        .background(
                            RoundedRectangle(cornerRadius: 16)
                                .fill(Color.black.opacity(0.4))
                                .overlay(
                                    RoundedRectangle(cornerRadius: 16)
                                        .stroke(Color.white.opacity(0.3), lineWidth: 1)
                                )
                        )
                        .padding(.horizontal)
                        .padding(.bottom, 12)
                    }

                    // Results List
                    ScrollView {
                        VStack(spacing: 12) {
                            ForEach(viewModel.filteredAthletes) { athlete in
                                AthleteResultCard(
                                    name: athlete.name,
                                    position: athlete.position,
                                    gradYear: athlete.gradYear,
                                    location: athlete.location,
                                    height: athlete.height,
                                    gpa: athlete.gpa,
                                    isWatchlisted: athlete.isWatchlisted,
                                    onToggleWatchlist: {
                                        viewModel.toggleWatchlist(for: athlete)
                                    }
                                )
                            }
                        }
                        .padding(.horizontal)
                        .padding(.bottom, 20)
                    }
                }
            }
            .navigationTitle("Athlete Search")
            .navigationBarTitleDisplayMode(.inline)
            .task {
                await viewModel.loadData()
            }
        }
    }
}

