//
//  SignInView.swift
//  AthleadAI
//
//  Sign-in view using Clerk's built-in AuthView
//

import SwiftUI
import Clerk

struct SignInView: View {
    @Environment(\.clerk) private var clerk
    @State private var authIsPresented = false
    @State private var userTypeSelectionIsPresented = false
    @State private var isLoading = false
    @State private var errorMessage: String?

    var body: some View {
        NavigationStack {
            ScrollView {
                VStack(spacing: 32) {
                    Spacer()
                        .frame(height: 60)

                    // App branding
                    VStack(spacing: 16) {
                        Image(systemName: "sportscourt.fill")
                            .font(.system(size: 80))
                            .foregroundStyle(
                                LinearGradient(
                                    colors: [.blue, .purple],
                                    startPoint: .topLeading,
                                    endPoint: .bottomTrailing
                                )
                            )
                            .shadow(color: .blue.opacity(0.3), radius: 10, x: 0, y: 5)
                            .accessibilityLabel("AthleadAI logo")

                        Text("AthleadAI")
                            .font(.system(size: 44, weight: .bold, design: .rounded))

                        Text("Connect with coaches and showcase your talent")
                            .font(.body)
                            .foregroundColor(.secondary)
                            .multilineTextAlignment(.center)
                            .padding(.horizontal, 40)
                    }

                    // Error message
                    if let errorMessage = errorMessage {
                        HStack {
                            Image(systemName: "exclamationmark.triangle.fill")
                            Text(errorMessage)
                                .font(.footnote)
                        }
                        .foregroundColor(.red)
                        .padding()
                        .background(Color.red.opacity(0.1))
                        .cornerRadius(10)
                        .padding(.horizontal)
                        .transition(.scale.combined(with: .opacity))
                    }

                    // Sign in section
                    VStack(spacing: 20) {
                        if clerk.user == nil {
                            // Sign in button
                            BrandButton(
                                title: "Sign In",
                                icon: "person.circle.fill",
                                style: .primary,
                                isLoading: isLoading
                            ) {
                                authIsPresented = true
                                errorMessage = nil
                            }
                            .disabled(isLoading)
                            .padding(.horizontal, 32)

                            // Sign up button
                            BrandButton(
                                title: "Create Account",
                                icon: "person.badge.plus",
                                style: .secondary
                            ) {
                                userTypeSelectionIsPresented = true
                                errorMessage = nil
                            }
                            .padding(.horizontal, 32)

                            // Additional info
                            VStack(spacing: 12) {
                                HStack(spacing: 16) {
                                    FeatureBadge(icon: "shield.checkered", text: "Secure")
                                    FeatureBadge(icon: "lock.fill", text: "Private")
                                    FeatureBadge(icon: "checkmark.seal.fill", text: "Verified")
                                }

                                Text("Sign in securely with Clerk authentication")
                                    .font(.caption)
                                    .foregroundColor(.secondary)
                                    .multilineTextAlignment(.center)
                            }
                            .padding(.top, 8)
                        } else {
                            // User is signed in
                            VStack(spacing: 16) {
                                Image(systemName: "checkmark.circle.fill")
                                    .font(.system(size: 60))
                                    .foregroundStyle(.green)

                                Text("Welcome, \(clerk.user?.firstName ?? "User")!")
                                    .font(.title2)
                                    .fontWeight(.semibold)

                                Text("You're all set!")
                                    .font(.subheadline)
                                    .foregroundColor(.secondary)

                                UserButton()
                                    .padding(.top, 8)
                            }
                            .padding()
                        }
                    }

                    Spacer()
                }
                .padding(.vertical)
            }
            .withDesignTheme()
            .brandBackground(.adaptive)
            .sheet(isPresented: $authIsPresented) {
                AuthView()
                    .onDisappear {
                        // Handle auth completion
                        if clerk.user != nil {
                            isLoading = false
                        } else {
                            // User dismissed without signing in
                            errorMessage = nil
                        }
                    }
            }
            .sheet(isPresented: $userTypeSelectionIsPresented) {
                NavigationStack {
                    UserTypeSelectionView()
                }
            }
        }
    }
}

// MARK: - Feature Badge Component
struct FeatureBadge: View {
    let icon: String
    let text: String

    var body: some View {
        HStack(spacing: 4) {
            Image(systemName: icon)
                .font(.caption)
            Text(text)
                .font(.caption)
                .fontWeight(.medium)
        }
        .foregroundColor(.secondary)
        .padding(.horizontal, 10)
        .padding(.vertical, 6)
        .background(Color.secondary.opacity(0.1))
        .cornerRadius(8)
    }
}

#Preview {
    SignInView()
        .environment(\.clerk, Clerk.shared)
}

//
//  UserTypeSelectionView.swift
//  AthleadAI
//
//  User type selection screen during sign-up
//

import SwiftUI

enum UserType: String, CaseIterable {
    case athlete = "Athlete"
    case parent = "Parent/Guardian"
    case coach = "College Coach"
    case clubCoach = "Club / High School Coach"
}

struct UserTypeSelectionView: View {
    @Environment(\.dismiss) private var dismiss
    @State private var selectedUserType: UserType?
    @State private var navigateToAuth = false

    var body: some View {
        ZStack {
            // Background
            Color(.systemBackground)
                .ignoresSafeArea()

            VStack(spacing: 0) {
                // Header
                VStack(spacing: 16) {
                    Text("User Type Sign Up")
                        .font(.caption)
                        .foregroundColor(.gray)
                        .frame(maxWidth: .infinity, alignment: .leading)
                        .padding(.horizontal, 24)
                        .padding(.top, 20)

                    Image(systemName: "target")
                        .font(.system(size: 60))
                        .foregroundStyle(
                            LinearGradient(
                                colors: [.blue, .purple],
                                startPoint: .topLeading,
                                endPoint: .bottomTrailing
                            )
                        )
                        .accessibilityLabel("Athlead logo")

                    Text("ATHLEAD")
                        .font(.system(size: 32, weight: .bold))
                        .foregroundColor(.blue)
                }
                .padding(.bottom, 40)

                // User type options
                VStack(spacing: 16) {
                    ForEach(UserType.allCases, id: \.self) { userType in
                        Button {
                            selectedUserType = userType
                            navigateToAuth = true
                        } label: {
                            Text(userType.rawValue)
                                .font(.body)
                                .foregroundColor(.primary)
                                .frame(maxWidth: .infinity)
                                .padding()
                                .background(Color(.systemGray6))
                                .cornerRadius(8)
                        }
                    }
                }
                .padding(.horizontal, 24)

                Spacer()

                // Already have an account
                HStack(spacing: 4) {
                    Text("Already have an account?")
                        .font(.subheadline)
                        .foregroundColor(.secondary)

                    Button {
                        dismiss()
                    } label: {
                        Text("Sign In")
                            .font(.subheadline)
                            .fontWeight(.semibold)
                            .foregroundColor(.blue)
                    }
                }
                .padding(.bottom, 40)
            }
        }
        .navigationDestination(isPresented: $navigateToAuth) {
            if let userType = selectedUserType {
                SignUpAuthView(userType: userType)
            }
        }
    }
}

#Preview {
    NavigationStack {
        UserTypeSelectionView()
    }
}

//
//  AthleteSearchView.swift
//  AthleadAI
//
//  Search view for athletes - can search for schools, coaches, and other athletes
//

import SwiftUI

struct AthleteSearchView: View {
    @State private var viewModel = SearchViewModel(searchType: .athlete)

    var body: some View {
        NavigationView {
            VStack(spacing: 0) {
                // Search Bar
                HStack {
                        Image(systemName: "magnifyingglass")
                            .foregroundColor(.gray)

                    TextField("Search schools...", text: $viewModel.searchText)
                        .foregroundColor(.white)
                }
                .padding()
                .background(
                        RoundedRectangle(cornerRadius: 12)
                            .fill(Color.black.opacity(0.4))
                            .overlay(
                                RoundedRectangle(cornerRadius: 12)
                                    .stroke(Color.white.opacity(0.3), lineWidth: 1)
                        )
                )
                .padding(.horizontal)
                .padding(.vertical, 12)

                // Tab Selector
                HStack(spacing: 0) {
                    ForEach(SearchViewModel.SearchTab.allCases, id: \.self) { tab in
                        Button(action: {
                            viewModel.selectTab(tab)
                        }) {
                            VStack(spacing: 8) {
                                Text(tab.rawValue)
                                    .font(.subheadline)
                                    .fontWeight(.semibold)
                                    .foregroundColor(viewModel.selectedTab == tab ? .white : .gray)

                                Rectangle()
                                    .fill(viewModel.selectedTab == tab ? Color.blue : Color.clear)
                                    .frame(height: 2)
                            }
                            .frame(maxWidth: .infinity)
                        }
                    }
                }
                .padding(.horizontal)
                .padding(.bottom, 12)

                // Content
                if viewModel.selectedTab == .schools {
                    SchoolsListView(
                        schools: viewModel.filteredSchools,
                        isLoading: viewModel.isLoading,
                        showDetail: $viewModel.showDetail,
                        detailType: $viewModel.detailType
                    )
                } else if viewModel.selectedTab == .coaches {
                    CoachesListView(
                        coaches: viewModel.filteredCoaches,
                        isLoading: viewModel.isLoading,
                        showDetail: $viewModel.showDetail,
                        detailType: $viewModel.detailType
                    )
                } else {
                    AthletesListView()
                }
            }
            .withDesignTheme()
            .brandBackground(.feed)
            .navigationTitle("Schools & Coaches")
            .navigationBarTitleDisplayMode(.inline)
            .task {
                await viewModel.loadData()
            }
            .toolbar {
                ToolbarItem(placement: .navigationBarTrailing) {
                    HStack(spacing: 16) {
                        Button(action: {}) {
                            Image(systemName: "chart.bar.fill")
                                .foregroundColor(.blue)
                        }
                        .accessibilityLabel("View statistics")
                        .accessibilityHint("Show recruiting statistics and insights")

                        Button(action: {}) {
                            Image(systemName: "bookmark.fill")
                                .foregroundColor(.blue)
                        }
                        .accessibilityLabel("View bookmarks")
                        .accessibilityHint("Show saved schools and coaches")
                    }
                }
            }
            .sheet(isPresented: $viewModel.showDetail) {
                NavigationView {
                    DetailContainerView(detailType: $viewModel.detailType, showDetail: $viewModel.showDetail)
                }
            }
        }
    }
}

// Detail Container that switches between School and Coach
struct DetailContainerView: View {
    @Binding var detailType: SearchViewModel.DetailType
    @Binding var showDetail: Bool
    @State private var navigationStack: [SearchViewModel.DetailType] = []

    var body: some View {
        NavigationStack(path: $navigationStack) {
            Group {
                switch detailType {
                case .school(let school):
                    SchoolDetailView(
                        initial: school.initial,
                        name: school.name,
                        location: school.location,
                        division: school.division,
                        conference: school.conference,
                        ranking: school.ranking,
                        navigationStack: $navigationStack
                    )
                case .coach(let coach):
                    CoachDetailView(
                        name: coach.name,
                        title: coach.title,
                        school: coach.school,
                        schoolInitial: coach.schoolInitial,
                        yearsExperience: coach.yearsExperience,
                        navigationStack: $navigationStack
                    )
                }
            }
            .toolbar {
                ToolbarItem(placement: .navigationBarLeading) {
                    Button("Done") {
                        showDetail = false
                    }
                    .foregroundColor(.blue)
                }
            }
            .navigationDestination(for: SearchViewModel.DetailType.self) { destination in
                switch destination {
                case .school(let school):
                    SchoolDetailView(
                        initial: school.initial,
                        name: school.name,
                        location: school.location,
                        division: school.division,
                        conference: school.conference,
                        ranking: school.ranking,
                        navigationStack: $navigationStack
                    )
                    .toolbar {
                        ToolbarItem(placement: .navigationBarLeading) {
                            Button("Done") {
                                showDetail = false
                            }
                            .foregroundColor(.blue)
                        }
                    }
                case .coach(let coach):
                    CoachDetailView(
                        name: coach.name,
                        title: coach.title,
                        school: coach.school,
                        schoolInitial: coach.schoolInitial,
                        yearsExperience: coach.yearsExperience,
                        navigationStack: $navigationStack
                    )
                    .toolbar {
                        ToolbarItem(placement: .navigationBarLeading) {
                            Button("Done") {
                                showDetail = false
                            }
                            .foregroundColor(.blue)
                        }
                    }
                }
            }
        }
    }
}

// Schools List View
struct SchoolsListView: View {
    let schools: [School]
    let isLoading: Bool
    @Binding var showDetail: Bool
    @Binding var detailType: SearchViewModel.DetailType

    var body: some View {
        ScrollView {
            VStack(spacing: 12) {
                if isLoading {
                    ProgressView()
                        .progressViewStyle(CircularProgressViewStyle(tint: .blue))
                        .padding()
                } else if schools.isEmpty {
                    Text("No schools found")
                        .foregroundColor(.gray)
                        .padding()
                } else {
                    ForEach(schools) { school in
                        SchoolCard(
                            initial: school.initial,
                            name: school.name,
                            location: school.location,
                            division: school.division,
                            conference: school.conference,
                            ranking: school.ranking ?? "N/A",
                            showDetail: $showDetail,
                            detailType: $detailType
                        )
                    }
                }
            }
            .padding()
        }
    }
}

// School Card Component
struct SchoolCard: View {
    let initial: String
    let name: String
    let location: String
    let division: String
    let conference: String
    let ranking: String
    @Binding var showDetail: Bool
    @Binding var detailType: SearchViewModel.DetailType

    var body: some View {
        Button(action: {
            detailType = .school(SearchViewModel.SchoolData(
                initial: initial,
                name: name,
                location: location,
                division: division,
                conference: conference,
                ranking: ranking
            ))
            showDetail = true
        }) {
            HStack(spacing: 16) {
                // School Initial
                Text(initial)
                    .font(.headline)
                    .fontWeight(.bold)
                    .foregroundColor(.blue)
                    .frame(width: 60, height: 60)
                    .background(
                        RoundedRectangle(cornerRadius: 12)
                            .fill(Color.blue.opacity(0.2))
                    )

                VStack(alignment: .leading, spacing: 6) {
                    HStack {
                        Text(name)
                            .font(.headline)
                            .foregroundColor(.white)

                        Spacer()

                        Text(ranking)
                            .font(.subheadline)
                            .fontWeight(.semibold)
                            .foregroundColor(.blue)
                            .padding(.horizontal, 8)
                            .padding(.vertical, 4)
                            .background(
                                Capsule()
                                    .fill(Color.blue.opacity(0.2))
                            )
                    }

                    Text(location)
                        .font(.subheadline)
                        .foregroundColor(.gray)

                    HStack(spacing: 8) {
                        Text(division)
                            .font(.caption)
                            .foregroundColor(.gray)

                        Text("â€¢")
                            .foregroundColor(.gray)

                        Text(conference)
                            .font(.caption)
                            .foregroundColor(.gray)
                    }
                }
            }
            .glassCard(cornerRadius: .radiusLarge, padding: .spacing4)
        }
        .buttonStyle(PlainButtonStyle())
        .accessibilityElement(children: .combine)
    }
}

// Coaches List View
struct CoachesListView: View {
    let coaches: [Coach]
    let isLoading: Bool
    @Binding var showDetail: Bool
