import SwiftUI

// MARK: - Athlete Tab Enum
enum AthleteTab {
    case profile
    case feed
    case ai
    case messages
    case search
}

// MARK: - Athlete Tab View
struct AthleteTabView: View {
    @State private var selectedTab: AthleteTab = .feed

    var body: some View {
        TabView(selection: $selectedTab) {
            ProfileView()
                .tabItem {
                    Label("Profile", systemImage: "person.circle.fill")
                }
                .tag(AthleteTab.profile)

            AthleteFeedView()
                .tabItem {
                    Label("Feed", systemImage: "house.fill")
                }
                .tag(AthleteTab.feed)

            AIView()
                .tabItem {
                    Label("AI", systemImage: "sparkles")
                }
                .tag(AthleteTab.ai)

            MessagesView()
                .tabItem {
                    Label("Messages", systemImage: "message.fill")
                }
                .tag(AthleteTab.messages)

            AthleteSearchView()
                .tabItem {
                    Label("Search", systemImage: "magnifyingglass")
                }
                .tag(AthleteTab.search)
        }
    }
}

