//
//  Loading state components with accessibility support
//

import SwiftUI

// MARK: - Full-Screen Loading View

/// Full-screen loading indicator with optional message
struct LoadingView: View {
    let message: String
    var size: LoadingSize = .medium

    enum LoadingSize {
        case small, medium, large

        var scale: CGFloat {
            switch self {
            case .small: return 1.0
            case .medium: return 1.5
            case .large: return 2.0
            }
        }
    }

    @Environment(\.designTheme) private var theme
    @Environment(\.userType) private var userType

    init(_ message: String = "Loading...", size: LoadingSize = .medium) {
        self.message = message
        self.size = size
    }

    var body: some View {
        VStack(spacing: .spacing5) {
            ProgressView()
                .scaleEffect(size.scale)
                .tint(userType.accentColor)
                .accessibilityHidden(true)

            Text(message)
                .font(theme.font(.body))
                .foregroundStyle(theme.color(.mutedforeground))
                .multilineTextAlignment(.center)
        }
        .frame(maxWidth: .infinity, maxHeight: .infinity)
        .accessibilityElement(combining: .children)
