//
//  FeedViewModel.swift
//  AthleadAI
//
//  ViewModel for managing feed state and business logic
//  Supports both athlete (squad-based) and coach (filter-based) feeds
//

import SwiftUI
import Observation

@Observable
class FeedViewModel {
    // MARK: - Published State (read-only from outside)
    private(set) var posts: [FeedPostData] = []
    private(set) var isLoading = false
    private(set) var error: Error?

    // MARK: - User-controlled State (can be modified by view)
    var selectedSquad: String? = nil
    var selectedFilter: RecruitFilter? = nil
    var showCreatePost = false

    // MARK: - Feed Type
    enum FeedType {
        case athlete
        case coach
    }

    let feedType: FeedType

    // MARK: - Recruit Filter (for coach feeds)
    enum RecruitFilter {
        case watchlist
        case gradYear2025
        case gradYear2026
        case gradYear2027
        case positionOutside
        case positionSetter
        case locationTexas
    }

    // MARK: - Computed Properties
    var filteredPosts: [FeedPostData] {
        if feedType == .athlete {
            // Filter by squad for athlete feeds
            if let squad = selectedSquad {
                return posts.filter { $0.squad == squad }
            }
            return posts
        } else {
            // Filter by recruit filter for coach feeds
            if let filter = selectedFilter {
                return posts.filter { post in
                    // Apply filter logic based on filter type
                    // For now, return all posts (implement actual filtering logic as needed)
                    return true
                }
            }
            return posts
        }
    }

    var hasError: Bool {
        error != nil
    }

    // MARK: - Initialization
    init(feedType: FeedType) {
        self.feedType = feedType
    }

    // MARK: - Actions
    @MainActor
    func loadFeed() async {
        setLoading(true)
        clearError()

        do {
            // Simulate network delay
            try await Task.sleep(nanoseconds: 500_000_000)

            // Load mock posts
            posts = mockPosts()
            setLoading(false)
        } catch {
            setError(error)
            setLoading(false)
        }
    }

    func selectSquad(_ squad: String?) {
        selectedSquad = squad
    }

    func selectFilter(_ filter: RecruitFilter?) {
        selectedFilter = filter
    }

    func toggleCreatePost() {
        showCreatePost.toggle()
    }

    func addPost(_ post: FeedPostData) {
        posts.insert(post, at: 0)
    }

    func toggleLike(for post: FeedPostData) {
        if let index = posts.firstIndex(where: { $0.id == post.id }) {
            posts[index].isLiked.toggle()
            posts[index].likes += posts[index].isLiked ? 1 : -1
        }
    }

    func deletePost(_ post: FeedPostData) {
        posts.removeAll { $0.id == post.id }
    }

    func createPost(content: String, squad: String?) async {
        setLoading(true)
        clearError()

        do {
            // Simulate network delay
            try await Task.sleep(nanoseconds: 1_000_000_000)

            let newPost = FeedPostData(
                id: UUID().uuidString,
                authorName: "Current User",
                authorHandle: "@currentuser",
                authorImageName: nil,
                timeAgo: "Just now",
                squad: squad ?? "All",
                content: content,
                mediaType: nil,
                mediaImageName: nil,
                likes: 0,
                comments: 0,
                isLiked: false
            )

            addPost(newPost)
            showCreatePost = false
            setLoading(false)
        } catch {
            setError(error)
            setLoading(false)
        }
    }

    // MARK: - Private Helpers
    private func setLoading(_ loading: Bool) {
        isLoading = loading
    }

    private func setError(_ error: Error?) {
        self.error = error
    }

    private func clearError() {
        error = nil
    }

    // MARK: - Mock Data
    private func mockPosts() -> [FeedPostData] {
        return [
            FeedPostData(
                id: "1",
                authorName: "Kinsley Young",
                authorHandle: "@kinsleyyoung",
                authorImageName: nil,
                timeAgo: "2h ago",
                squad: "Carroll Volleyball",
                content: "Great practice today! Working on my block timing. Ready for Friday's game! ğŸ",
                mediaType: .video,
                mediaImageName: nil,
                likes: 124,
                comments: 18,
                isLiked: false
            ),
            FeedPostData(
                id: "2",
                authorName: "Carey Clayton",
                authorHandle: "@lc_clayton51",
                authorImageName: nil,
                timeAgo: "5h ago",
                squad: "Class of 2026",
                content: "Committed to University of Memphis! So excited for the next chapter! ğŸ…",
                mediaType: .image,
                mediaImageName: nil,
                likes: 342,
                comments: 67,
                isLiked: false
            ),
            FeedPostData(
                id: "3",
                authorName: "Coach Jackson",
                authorHandle: "@coach_j",
                authorImageName: nil,
                timeAgo: "1d ago",
                squad: "Carroll Volleyball",
                content: "Proud of our team's performance this season. Keep pushing!",
                mediaType: nil,
                mediaImageName: nil,
                likes: 89,
                comments: 12,
                isLiked: false
            ),
            FeedPostData(
                id: "4",
                authorName: "Kaylee Young",
                authorHandle: "@kales_usc",
                authorImageName: nil,
                timeAgo: "2d ago",
                squad: "USC Beach Volleyball",
                content: "Highlight reel from last weekend's tournament. Thanks to everyone who came out to support! ğŸ’ª",
                mediaType: .video,
                mediaImageName: nil,
                likes: 215,
                comments: 34,
                isLiked: false
            ),
            FeedPostData(
                id: "5",
                authorName: "Kinsley Young",
                authorHandle: "@kinsleyyoung",
                authorImageName: nil,
                timeAgo: "3d ago",
                squad: "Carroll Volleyball",
                content: "Team photo day! Love this squad â¤ï¸",
                mediaType: .image,
                mediaImageName: nil,
                likes: 189,
                comments: 42,
                isLiked: false
            )
        ]
    }
}

// MARK: - Feed Post Data Model
struct FeedPostData: Identifiable {
    let id: String
    let authorName: String
    let authorHandle: String
    let authorImageName: String?
    let timeAgo: String
    let squad: String
    let content: String
    let mediaType: FeedPost.MediaType?
    let mediaImageName: String?
    var likes: Int
    let comments: Int
    var isLiked: Bool
}

