//
//  CircleFilter.swift
//  AthleadAI
//
//  Unified reusable circle filter component for both icon and image-based filters
//  Supports selection state, custom colors, and full accessibility
//

import SwiftUI

/// A reusable circular filter component that displays either an icon or image
/// with selection state and accessibility support.
///
/// Use cases:
/// - Squad/Group filters with custom images
/// - Category filters with SF Symbol icons
/// - Year, position, location filters
///
/// Example usage:
/// ```swift
/// CircleFilter(
///     name: "2026",
///     icon: "graduationcap.fill",
///     iconColor: .blue,
///     isSelected: selectedYear == "2026"
/// ) {
///     selectedYear = "2026"
/// }
/// ```
struct CircleFilter: View {
    let name: String
    let icon: String?
    let imageName: String?
    let iconColor: Color
    let isSelected: Bool
    let action: () -> Void

    /// Creates a circle filter with an icon
    /// - Parameters:
    ///   - name: Display name shown below the circle
    ///   - icon: SF Symbol name to display inside the circle
    ///   - iconColor: Color for the icon and selection state (default: .blue)
    ///   - isSelected: Whether this filter is currently selected
    ///   - action: Closure called when the filter is tapped
    init(
        name: String,
        icon: String,
        iconColor: Color = .blue,
        isSelected: Bool = false,
        action: @escaping () -> Void
    ) {
        self.name = name
        self.icon = icon
        self.imageName = nil
        self.iconColor = iconColor
        self.isSelected = isSelected
        self.action = action
    }

    /// Creates a circle filter with a custom image
    /// - Parameters:
    ///   - name: Display name shown below the circle
    ///   - imageName: Asset catalog image name to display inside the circle
    ///   - isSelected: Whether this filter is currently selected
    ///   - action: Closure called when the filter is tapped
    init(
        name: String,
        imageName: String,
        isSelected: Bool = false,
        action: @escaping () -> Void
    ) {
        self.name = name
        self.icon = nil
        self.imageName = imageName
        self.iconColor = .blue // Default color for border when using images
        self.isSelected = isSelected
        self.action = action
    }

    var body: some View {
        Button(action: action) {
            VStack(spacing: .spacing2) {
                // Circle with content
                ZStack {
                    // Outer ring (gradient border)
                    Circle()
                        .strokeBorder(
                            LinearGradient(
                                colors: isSelected
                                    ? [iconColor, iconColor.opacity(0.6)]
                                    : [Color.white.opacity(0.3), Color.white.opacity(0.3)],
                                startPoint: .topLeading,
                                endPoint: .bottomTrailing
                            ),
                            lineWidth: 3
                        )
                        .frame(width: 70, height: 70)

                    // Inner circle with gradient fill
                    Circle()
                        .fill(
                            LinearGradient(
                                colors: isSelected
                                    ? [iconColor.opacity(0.3), iconColor.opacity(0.2)]
                                    : [Color.white.opacity(0.2), Color.white.opacity(0.1)],
                                startPoint: .topLeading,
                                endPoint: .bottomTrailing
                            )
                        )
                        .frame(width: 64, height: 64)

                    // Content (image or icon)
                    if let imageName = imageName {
                        Image(imageName)
                            .resizable()
                            .scaledToFill()
                            .frame(width: 64, height: 64)
                            .clipShape(Circle())
                    } else if let icon = icon {
                        Image(systemName: icon)
                            .font(.system(size: 28))
                            .foregroundColor(isSelected ? iconColor : .gray)
                    }
                }

                // Name label
                Text(name)
                    .font(.caption)
                    .fontWeight(isSelected ? .semibold : .regular)
                    .foregroundColor(isSelected ? .white : .gray)
                    .lineLimit(1)
            }
            .frame(width: 80)
        }
        .accessibilityLabel("\(name) filter")
        .accessibilityAddTraits(isSelected ? .isSelected : [])
        .accessibilityHint(isSelected ? "Currently selected" : "Double tap to select")
    }
}

