//
//  RootView.swift
//  AthleadAI
//
//  Root view that handles authentication routing and app flow
//

import SwiftUI
import Clerk

struct RootView: View {
    @Environment(\.clerk) private var clerk
    @State private var showSplash = true
    @State private var showWelcome = false
    @State private var contentOpacity: Double = 0
    @State private var isClerkLoaded = false

    var body: some View {
        ZStack {
            if showSplash {
                // Splash screen shown initially
                SplashScreenView()
                    .transition(.opacity)
            } else {
                // Main content after splash
                Group {
                    if !isClerkLoaded {
                        // Clerk is still loading
                        LoadingView()
                    } else if clerk.user != nil {
                        // User is authenticated - route to appropriate tab view based on user type
                        MainTabRouter()
                            .transition(.opacity)
                    } else {
                        // User is not authenticated - show welcome/sign in
                        if showWelcome {
                            SignInView()
                                .transition(.asymmetric(
                                    insertion: .move(edge: .trailing),
                                    removal: .move(edge: .leading)
                                ))
                        } else {
                            WelcomeView(showSignIn: $showWelcome)
                                .transition(.opacity)
                        }
                    }
                }
                .opacity(contentOpacity)
            }
        }
        .animation(.easeInOut(duration: 0.5), value: showSplash)
        .animation(.easeInOut(duration: 0.3), value: showWelcome)
        .animation(.easeInOut(duration: 0.3), value: clerk.user)
        .task {
            // Show splash screen for 1.5 seconds
            try? await Task.sleep(nanoseconds: 1_500_000_000)

            withAnimation {
                showSplash = false
            }

            // Wait for Clerk to be loaded (it's loaded in AthleadAIApp)
            // Give it a moment to complete
            try? await Task.sleep(nanoseconds: 500_000_000)
            isClerkLoaded = true

            // Fade in content
            withAnimation(.easeIn(duration: 0.3)) {
                contentOpacity = 1
            }
        }
    }
}

// MARK: - Loading View Component
struct LoadingView: View {
    @State private var isAnimating = false

    var body: some View {
        ZStack {
            Color(.systemBackground)
                .ignoresSafeArea()

            VStack(spacing: 20) {
                Image(systemName: "sportscourt.fill")
                    .font(.system(size: 60))
                    .foregroundStyle(
                        LinearGradient(
                            colors: [.blue, .purple],
                            startPoint: .topLeading,
                            endPoint: .bottomTrailing
                        )
                    )
                    .rotationEffect(.degrees(isAnimating ? 360 : 0))
                    .animation(
                        .linear(duration: 2)
                            .repeatForever(autoreverses: false),
                        value: isAnimating
                    )

                ProgressView()
                    .scaleEffect(1.2)
                    .tint(.blue)

                Text("Loading...")
                    .font(.subheadline)
                    .foregroundColor(.secondary)
            }
        }
        .onAppear {
            isAnimating = true
        }
    }
}

#Preview("Root - Authenticated") {
    RootView()
        .environment(\.clerk, Clerk.shared)
}

#Preview("Loading") {
    LoadingView()
}

//
//  LoadingView.swift
//  AthleadAI
//
//  Loading state components with accessibility support
//

import SwiftUI

// MARK: - Full-Screen Loading View

/// Full-screen loading indicator with optional message
struct LoadingView: View {
    let message: String
    var size: LoadingSize = .medium

    enum LoadingSize {
        case small, medium, large

        var scale: CGFloat {
            switch self {
            case .small: return 1.0
            case .medium: return 1.5
            case .large: return 2.0
            }
        }
    }

    @Environment(\.designTheme) private var theme
    @Environment(\.userType) private var userType

    init(_ message: String = "Loading...", size: LoadingSize = .medium) {
        self.message = message
        self.size = size
    }

    var body: some View {
        VStack(spacing: .spacing5) {
            ProgressView()
                .scaleEffect(size.scale)
                .tint(userType.accentColor)
                .accessibilityHidden(true)

            Text(message)
                .font(theme.font(.body))
                .foregroundStyle(theme.color(.mutedforeground))
                .multilineTextAlignment(.center)
        }
        .frame(maxWidth: .infinity, maxHeight: .infinity)
        .accessibilityElement(combining: .children)
        .accessibilityLabel("\(message)")
    }
}

// MARK: - Inline Loading View

/// Inline loading indicator for smaller contexts
struct InlineLoadingView: View {
    let message: String

    @Environment(\.designTheme) private var theme
    @Environment(\.userType) private var userType

    init(_ message: String = "Loading...") {
        self.message = message
    }

    var body: some View {
        HStack(spacing: .spacing3) {
            ProgressView()
                .tint(userType.accentColor)
                .accessibilityHidden(true)

            Text(message)
                .font(theme.font(.callout))
                .foregroundStyle(theme.color(.mutedforeground))
        }
        .padding(.spacing3)
        .accessibilityElement(combining: .children)
        .accessibilityLabel("\(message)")
    }
}

// MARK: - Skeleton View

/// Skeleton placeholder for content while loading
struct SkeletonView: View {
    var height: CGFloat = 20
    var cornerRadius: CGFloat = .radiusSmall

    @State private var isAnimating = false
    @Environment(\.designTheme) private var theme
    @Environment(\.accessibilityReduceMotion) private var reduceMotion

    var body: some View {
        RoundedRectangle(cornerRadius: cornerRadius, style: .continuous)
            .fill(theme.color(.muted))
            .frame(height: height)
            .overlay(
                GeometryReader { geometry in
                    RoundedRectangle(cornerRadius: cornerRadius, style: .continuous)
                        .fill(
                            LinearGradient(
                                colors: [
                                    Color.clear,
                                    theme.color(.background).opacity(0.3),
                                    Color.clear
                                ],
                                startPoint: .leading,
                                endPoint: .trailing
                            )
                        )
                        .offset(x: isAnimating ? geometry.size.width : -geometry.size.width)
                        .animation(
                            reduceMotion ? nil : Animation.linear(duration: 1.5)
                                .repeatForever(autoreverses: false),
                            value: isAnimating
                        )
                }
                .clipped()
            )
            .onAppear {
                isAnimating = true
            }
            .accessibilityLabel("Loading content")
    }
}

// MARK: - Skeleton Card

/// Pre-built skeleton card for common use cases
struct SkeletonCard: View {
    @Environment(\.designTheme) private var theme

    var body: some View {
        VStack(alignment: .leading, spacing: .spacing3) {
            // Header
            HStack(spacing: .spacing3) {
                Circle()
                    .fill(theme.color(.muted))
                    .frame(width: 40, height: 40)

                VStack(alignment: .leading, spacing: .spacing2) {
                    SkeletonView(height: 16, cornerRadius: .radiusSmall)
                        .frame(width: 120)

                    SkeletonView(height: 12, cornerRadius: .radiusSmall)
                        .frame(width: 80)
                }
            }

            // Content
            VStack(spacing: .spacing2) {
                SkeletonView(height: 14)
                SkeletonView(height: 14)
                SkeletonView(height: 14, cornerRadius: .radiusSmall)
                    .frame(width: 200)
            }

            // Image placeholder
            SkeletonView(height: 200, cornerRadius: .radiusMedium)
        }
        .padding(.spacing4)
        .glassCard()
        .accessibilityLabel("Loading post")
    }
}

// MARK: - Skeleton List

/// Skeleton view for list items
struct SkeletonList: View {
    var itemCount: Int = 3

    var body: some View {
        VStack(spacing: .spacing4) {
            ForEach(0..<itemCount, id: \.self) { _ in
                SkeletonCard()
            }
        }
    }
}

// MARK: - Preview

#if DEBUG
struct LoadingView_Previews: PreviewProvider {
    static var previews: some View {
        Group {
            // Full-screen loading
            LoadingView("Loading posts...")
                .withDesignTheme()
                .background(DesignTheme.defaultLight.color(.background))
                .previewDisplayName("Full-Screen Loading")

            // Inline loading
            VStack {
                InlineLoadingView("Searching...")
                    .withDesignTheme()
            }
            .frame(maxWidth: .infinity, maxHeight: .infinity)
            .background(DesignTheme.defaultLight.color(.background))
            .previewDisplayName("Inline Loading")

            // Skeleton views
            ScrollView {
                VStack(spacing: .spacing4) {
                    SkeletonCard()
                    SkeletonCard()
                    SkeletonCard()
                }
                .padding()
            }
            .withDesignTheme()
            .background(DesignTheme.defaultLight.color(.background))
            .previewDisplayName("Skeleton Cards")

            // Dark mode
            ScrollView {
                VStack(spacing: .spacing4) {
                    LoadingView("Loading...")
                        .frame(height: 200)

                    InlineLoadingView("Processing...")

                    SkeletonCard()
                }
                .padding()
            }
            .withDesignTheme()
            .background(DesignTheme.defaultDark.color(.background))
            .preferredColorScheme(.dark)
            .previewDisplayName("Dark Mode")
        }
    }
}
#endif

//
//  ErrorView.swift
//  AthleadAI
//
//  Error state components with retry functionality
//

import SwiftUI

// MARK: - Full-Screen Error View

/// Full-screen error display with optional retry action
struct ErrorView: View {
    let title: String
    let message: String
    let retryAction: (() -> Void)?

    @Environment(\.designTheme) private var theme

    init(
        title: String = "Something Went Wrong",
        message: String,
        retryAction: (() -> Void)? = nil
    ) {
        self.title = title
        self.message = message
        self.retryAction = retryAction
    }

    var body: some View {
        VStack(spacing: .spacing5) {
            // Error icon
            Image(systemName: "exclamationmark.triangle.fill")
                .font(.system(size: 60))
                .foregroundStyle(theme.color(.destructive))
                .accessibilityHidden(true)

            // Title
            Text(title)
                .font(theme.font(.title2))
                .fontWeight(.semibold)
                .foregroundStyle(theme.color(.foreground))
                .multilineTextAlignment(.center)

            // Message
            Text(message)
                .font(theme.font(.body))
                .foregroundStyle(theme.color(.mutedforeground))
                .multilineTextAlignment(.center)
                .padding(.horizontal, .spacing8)

            // Retry button
            if let retryAction = retryAction {
                BrandButton(
                    title: "Try Again",
                    icon: "arrow.clockwise",
                    style: .primary
                ) {
                    retryAction()
                }
                .padding(.horizontal, .spacing8)
                .padding(.top, .spacing4)
            }
        }
        .frame(maxWidth: .infinity, maxHeight: .infinity)
        .padding(.spacing6)
        .accessibilityElement(combining: .children)
        .accessibilityLabel("\(title). \(message)")
    }
}

// MARK: - Inline Error View

/// Inline error banner for smaller contexts
struct ErrorBanner: View {
    let message: String
    let retryAction: (() -> Void)?

    @Environment(\.designTheme) private var theme

    init(
        message: String,
        retryAction: (() -> Void)? = nil
    ) {
        self.message = message
        self.retryAction = retryAction
    }

    var body: some View {
        HStack(spacing: .spacing3) {
            Image(systemName: "exclamationmark.circle.fill")
                .foregroundStyle(theme.color(.destructive))
                .accessibilityHidden(true)

            Text(message)
                .font(theme.font(.callout))
                .foregroundStyle(theme.color(.destructive))
                .lineLimit(2)

            Spacer()

            if let retryAction = retryAction {
                Button {
                    retryAction()
                } label: {
                    Text("Retry")
                        .font(theme.font(.callout))
                        .fontWeight(.medium)
                        .foregroundStyle(theme.color(.destructive))
                }
                .accessibilityLabel("Retry action")
            }
        }
        .padding(.spacing3)
        .background(
            RoundedRectangle(cornerRadius: .radiusMedium, style: .continuous)
                .fill(theme.color(.destructive).opacity(0.1))
        )
        .overlay(
            RoundedRectangle(cornerRadius: .radiusMedium, style: .continuous)
                .stroke(theme.color(.destructive).opacity(0.3), lineWidth: 1)
        )
        .accessibilityElement(combining: .children)
    }
}

// MARK: - Network Error View

/// Specialized error view for network issues
struct NetworkErrorView: View {
    let retryAction: (() -> Void)?

    @Environment(\.designTheme) private var theme

    init(retryAction: (() -> Void)? = nil) {
        self.retryAction = retryAction
    }

    var body: some View {
        ErrorView(
            title: "No Internet Connection",
            message: "Please check your connection and try again.",
            retryAction: retryAction
        )
    }
}

// MARK: - Not Found Error View

/// Specialized error view for 404/not found scenarios
struct NotFoundErrorView: View {
    let itemName: String
    let action: (() -> Void)?

    @Environment(\.designTheme) private var theme

    init(
        itemName: String = "content",
        action: (() -> Void)? = nil
    ) {
        self.itemName = itemName
        self.action = action
    }

    var body: some View {
        VStack(spacing: .spacing5) {
            Image(systemName: "magnifyingglass")
                .font(.system(size: 60))
                .foregroundStyle(theme.color(.mutedforeground))
                .accessibilityHidden(true)

            Text("Not Found")
                .font(theme.font(.title2))
                .fontWeight(.semibold)
                .foregroundStyle(theme.color(.foreground))

            Text("The \(itemName) you're looking for doesn't exist or has been removed.")
                .font(theme.font(.body))
                .foregroundStyle(theme.color(.mutedforeground))
                .multilineTextAlignment(.center)
                .padding(.horizontal, .spacing8)

            if let action = action {
                BrandButton(
                    title: "Go Back",
                    icon: "arrow.left",
                    style: .secondary
                ) {
                    action()
                }
                .padding(.horizontal, .spacing8)
                .padding(.top, .spacing4)
            }
        }
        .frame(maxWidth: .infinity, maxHeight: .infinity)
        .padding(.spacing6)
    }
}

// MARK: - Generic API Error View

/// Error view for API/server errors with helpful messaging
struct APIErrorView: View {
    let error: Error?
    let retryAction: (() -> Void)?

    @Environment(\.designTheme) private var theme

    init(
        error: Error?,
        retryAction: (() -> Void)? = nil
    ) {
        self.error = error
        self.retryAction = retryAction
    }

    private var errorMessage: String {
        if let error = error {
            return error.localizedDescription
        }
        return "An unexpected error occurred. Please try again."
    }

    var body: some View {
        ErrorView(
            title: "Unable to Load",
            message: errorMessage,
            retryAction: retryAction
        )
    }
}

// MARK: - Preview

#if DEBUG
struct ErrorView_Previews: PreviewProvider {
    static var previews: some View {
        Group {
            // Full-screen error
            ErrorView(
                title: "Failed to Load Posts",
                message: "We couldn't load your posts. Please check your connection and try again.",
                retryAction: { print("Retry tapped") }
            )
            .withDesignTheme()
            .background(DesignTheme.defaultLight.color(.background))
            .previewDisplayName("Full-Screen Error")

            // Error banner
            VStack {
                ErrorBanner(
                    message: "Failed to send message",
                    retryAction: { print("Retry") }
                )
                .padding()

                Spacer()
            }
            .withDesignTheme()
            .background(DesignTheme.defaultLight.color(.background))
            .previewDisplayName("Error Banner")

            // Network error
            NetworkErrorView(retryAction: { print("Retry") })
                .withDesignTheme()
                .background(DesignTheme.defaultLight.color(.background))
                .previewDisplayName("Network Error")

            // Not found error
            NotFoundErrorView(
                itemName: "profile",
                action: { print("Go back") }
            )
            .withDesignTheme()
            .background(DesignTheme.defaultLight.color(.background))
            .previewDisplayName("Not Found")

            // Dark mode
