//  AthleadAI
//
//  Error state components with retry functionality
//

import SwiftUI

// MARK: - Full-Screen Error View

/// Full-screen error display with optional retry action
struct ErrorView: View {
    let title: String
    let message: String
    let retryAction: (() -> Void)?

    @Environment(\.designTheme) private var theme

    init(
        title: String = "Something Went Wrong",
        message: String,
        retryAction: (() -> Void)? = nil
    ) {
        self.title = title
        self.message = message
        self.retryAction = retryAction
    }

    var body: some View {
        VStack(spacing: .spacing5) {
            // Error icon
            Image(systemName: "exclamationmark.triangle.fill")
                .font(.system(size: 60))
                .foregroundStyle(theme.color(.destructive))
                .accessibilityHidden(true)

            // Title
            Text(title)
                .font(theme.font(.title2))
                .fontWeight(.semibold)
                .foregroundStyle(theme.color(.foreground))
                .multilineTextAlignment(.center)

            // Message
            Text(message)
                .font(theme.font(.body))
                .foregroundStyle(theme.color(.mutedforeground))
                .multilineTextAlignment(.center)
                .padding(.horizontal, .spacing8)

            // Retry button
            if let retryAction = retryAction {
                BrandButton(
                    title: "Try Again",
                    icon: "arrow.clockwise",
                    style: .primary
                ) {
                    retryAction()
                }
                .padding(.horizontal, .spacing8)
                .padding(.top, .spacing4)
            }
        }
        .frame(maxWidth: .infinity, maxHeight: .infinity)
        .padding(.spacing6)
        .accessibilityElement(combining: .children)
        .accessibilityLabel("\(title). \(message)")
    }
}

// MARK: - Inline Error View

/// Inline error banner for smaller contexts
struct ErrorBanner: View {
    let message: String
    let retryAction: (() -> Void)?

    @Environment(\.designTheme) private var theme

    init(
        message: String,
        retryAction: (() -> Void)? = nil
    ) {
        self.message = message
        self.retryAction = retryAction
    }

    var body: some View {
        HStack(spacing: .spacing3) {
            Image(systemName: "exclamationmark.circle.fill")
                .foregroundStyle(theme.color(.destructive))
                .accessibilityHidden(true)

            Text(message)
                .font(theme.font(.callout))
                .foregroundStyle(theme.color(.destructive))
                .lineLimit(2)

            Spacer()

            if let retryAction = retryAction {
                Button {
                    retryAction()
                } label: {
                    Text("Retry")
                        .font(theme.font(.callout))
                        .fontWeight(.medium)
                        .foregroundStyle(theme.color(.destructive))
                }
                .accessibilityLabel("Retry action")
            }
        }
        .padding(.spacing3)
        .background(
            RoundedRectangle(cornerRadius: .radiusMedium, style: .continuous)
                .fill(theme.color(.destructive).opacity(0.1))
        )
        .overlay(
            RoundedRectangle(cornerRadius: .radiusMedium, style: .continuous)
                .stroke(theme.color(.destructive).opacity(0.3), lineWidth: 1)
        )
        .accessibilityElement(combining: .children)
    }
}

// MARK: - Network Error View

/// Specialized error view for network issues
struct NetworkErrorView: View {
    let retryAction: (() -> Void)?

    @Environment(\.designTheme) private var theme

    init(retryAction: (() -> Void)? = nil) {
        self.retryAction = retryAction
    }

    var body: some View {
        ErrorView(
